   579‚Üí
   580‚Üíclass TLGeometry {
   581‚Üí    // Point in polygon test
   582‚Üí    static pointInPolygon(point, polygon) {
   583‚Üí        let inside = false;
   584‚Üí        for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
   585‚Üí            const xi = polygon[i].x, yi = polygon[i].y;
   586‚Üí            const xj = polygon[j].x, yj = polygon[j].y;
   587‚Üí            
   588‚Üí            if (((yi > point.y) !== (yj > point.y)) && 
   589‚Üí                (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi)) {
   590‚Üí                inside = !inside;
   591‚Üí            }
   592‚Üí        }
   593‚Üí        return inside;
   594‚Üí    }
   595‚Üí
   596‚Üí    // Point in rotated rectangle
   597‚Üí    static pointInRotatedRect(point, rect) {
   598‚Üí        const { x, y, width, height, rotation = 0 } = rect;
   599‚Üí        
   600‚Üí        if (rotation === 0) {
   601‚Üí            return point.x >= x && point.x <= x + width && 
   602‚Üí                   point.y >= y && point.y <= y + height;
   603‚Üí        }
   604‚Üí
   605‚Üí        // Transform point to rectangle's local coordinates
   606‚Üí        const center = { x: x + width / 2, y: y + height / 2 };
   607‚Üí        const rotatedPoint = rotatePoint(point, center, -rotation);
   608‚Üí        
   609‚Üí        return rotatedPoint.x >= x && rotatedPoint.x <= x + width &&
   610‚Üí               rotatedPoint.y >= y && rotatedPoint.y <= y + height;
   611‚Üí    }
   612‚Üí
   613‚Üí    // Get rotated rectangle corners
   614‚Üí    static getRotatedRectCorners(rect) {
   615‚Üí        const { x, y, width, height, rotation = 0 } = rect;
   616‚Üí        const centerX = x + width / 2;
   617‚Üí        const centerY = y + height / 2;
   618‚Üí        
   619‚Üí        const corners = [
   620‚Üí            { x: x, y: y },
   621‚Üí            { x: x + width, y: y },
   622‚Üí            { x: x + width, y: y + height },
   623‚Üí            { x: x, y: y + height }
   624‚Üí        ];
   625‚Üí        
   626‚Üí        if (rotation === 0) {
   627‚Üí            return corners;
   628‚Üí        }
   629‚Üí        
   630‚Üí        return corners.map(corner => 
   631‚Üí            rotatePoint(corner, { x: centerX, y: centerY }, rotation)
   632‚Üí        );
   633‚Üí    }
   634‚Üí
   635‚Üí    // Distance from point to line segment
   636‚Üí    static pointToLineDistance(point, lineStart, lineEnd) {
   637‚Üí        const A = point.x - lineStart.x;
   638‚Üí        const B = point.y - lineStart.y;
   639‚Üí        const C = lineEnd.x - lineStart.x;
   640‚Üí        const D = lineEnd.y - lineStart.y;
   641‚Üí
   642‚Üí        const dot = A * C + B * D;
   643‚Üí        const lenSq = C * C + D * D;
   644‚Üí        
   645‚Üí        if (lenSq === 0) {
   646‚Üí            return Math.sqrt(A * A + B * B);
   647‚Üí        }
   648‚Üí        
   649‚Üí        let param = dot / lenSq;
   650‚Üí        param = clamp(param, 0, 1);
   651‚Üí        
   652‚Üí        const nearestX = lineStart.x + param * C;
   653‚Üí        const nearestY = lineStart.y + param * D;
   654‚Üí        
   655‚Üí        const dx = point.x - nearestX;
   656‚Üí        const dy = point.y - nearestY;
   657‚Üí        
   658‚Üí        return Math.sqrt(dx * dx + dy * dy);
   659‚Üí    }
   660‚Üí
   661‚Üí    // Bounding box intersection
   662‚Üí    static boundsIntersect(bounds1, bounds2) {
   663‚Üí        return !(bounds2.x > bounds1.x + bounds1.w ||
   664‚Üí                bounds2.x + bounds2.w < bounds1.x ||
   665‚Üí                bounds2.y > bounds1.y + bounds1.h ||
   666‚Üí                bounds2.y + bounds2.h < bounds1.y);
   667‚Üí    }
   668‚Üí
   669‚Üí    // Get bounds of multiple shapes
   670‚Üí    static getCommonBounds(shapes) {
   671‚Üí        if (shapes.length === 0) return { x: 0, y: 0, w: 0, h: 0 };
   672‚Üí        
   673‚Üí        let minX = Infinity, minY = Infinity;
   674‚Üí        let maxX = -Infinity, maxY = -Infinity;
   675‚Üí        
   676‚Üí        shapes.forEach(shape => {
   677‚Üí            const bounds = shape.getBounds();
   678‚Üí            minX = Math.min(minX, bounds.x);
   679‚Üí            minY = Math.min(minY, bounds.y);
   680‚Üí            maxX = Math.max(maxX, bounds.x + bounds.w);
   681‚Üí            maxY = Math.max(maxY, bounds.y + bounds.h);
   682‚Üí        });
   683‚Üí        
   684‚Üí        return {
   685‚Üí            x: minX,
   686‚Üí            y: minY,
   687‚Üí            w: maxX - minX,
   688‚Üí            h: maxY - minY
   689‚Üí        };
   690‚Üí    }
   691‚Üí}
   692‚Üí
   693‚Üí// =====================================================
   694‚Üí// EXPORTS
   695‚Üí// =====================================================
   696‚Üí
   697‚Üí// Make everything globally available
   698‚Üíif (typeof window !== 'undefined') {
   699‚Üí    // Browser environment
   700‚Üí    window.TLRecordType = TLRecordType;
   701‚Üí    window.TLShapeType = TLShapeType;
   702‚Üí    window.TLEventType = TLEventType;
   703‚Üí    window.TLToolType = TLToolType;
   704‚Üí    window.TLRecord = TLRecord;
   705‚Üí    // TLShape, TLPage, TLCamera, TLInstance are defined in core-store.js
   706‚Üí    window.TLEventInfo = TLEventInfo;
   707‚Üí    window.TLEventResult = TLEventResult;
   708‚Üí    window.TLGeometry = TLGeometry;
   709‚Üí    
   710‚Üí    // Utility functions - generateId now provided by utils.js
   711‚Üí    window.getCurrentTimestamp = getCurrentTimestamp;
   712‚Üí    window.clamp = clamp;
   713‚Üí    window.lerp = lerp;
   714‚Üí    // window.distance = distance; // Now provided by utils.js
   715‚Üí    window.rotatePoint = rotatePoint;
   716‚Üí} else if (typeof module !== 'undefined' && module.exports) {
   717‚Üí    // Node.js environment
   718‚Üí    module.exports = {
   719‚Üí        TLRecordType,
   720‚Üí        TLShapeType,
   721‚Üí        TLEventType,
   722‚Üí        TLToolType,
   723‚Üí        TLRecord,
   724‚Üí        // TLShape, TLPage, TLCamera, TLInstance are defined in core-store.js
   725‚Üí        TLEventInfo,
   726‚Üí        TLEventResult,
   727‚Üí        TLGeometry,
