/**
 * TLDraw Core Type Definitions and Interfaces
 * Foundation layer for all canvas functionality
 */

// =====================================================
// CORE RECORD TYPES
// =====================================================

const TLRecordType = {
    SHAPE: 'shape',
    PAGE: 'page', 
    CAMERA: 'camera',
    INSTANCE: 'instance',
    USER: 'user',
    DOCUMENT: 'document'
};

// =====================================================
// SHAPE TYPE DEFINITIONS
// =====================================================

const TLShapeType = {
    DRAW: 'draw',
    TEXT: 'text',
    GEO: 'geo',
    RECTANGLE: 'rectangle',
    ELLIPSE: 'ellipse',
    ARROW: 'arrow',
    DOCUMENT: 'document',
    YOUTUBE: 'youtube',
    SPOTIFY: 'spotify',
    APPLE: 'apple',
    TEXTFILE: 'textfile',
    IMAGE: 'image',
    LINE: 'line',
    TRIANGLE: 'triangle',
    DIAMOND: 'diamond',
    STAR: 'star',
    HEXAGON: 'hexagon',
    EMBED: 'embed'
};

// =====================================================
// EVENT TYPE DEFINITIONS
// =====================================================

const TLEventType = {
    POINTER_DOWN: 'pointer_down',
    POINTER_MOVE: 'pointer_move', 
    POINTER_UP: 'pointer_up',
    DOUBLE_CLICK: 'double_click',
    RIGHT_CLICK: 'right_click',
    KEY_DOWN: 'key_down',
    KEY_UP: 'key_up',
    WHEEL: 'wheel',
    ENTER: 'enter',
    EXIT: 'exit',
    TICK: 'tick',
    CANCEL: 'cancel',
    COMPLETE: 'complete',
    INTERRUPT: 'interrupt'
};

// =====================================================
// TOOL TYPE DEFINITIONS  
// =====================================================

const TLToolType = {
    SELECT: 'select',
    DRAW: 'draw',
    HAND: 'hand',
    ERASER: 'eraser',
    TEXT: 'text',
    RECTANGLE: 'rectangle',
    ELLIPSE: 'ellipse',
    ARROW: 'arrow',
    LINE: 'line',
    TRIANGLE: 'triangle',
    DIAMOND: 'diamond',
    STAR: 'star',
    HEXAGON: 'hexagon'
};

// =====================================================
// UTILITY FUNCTIONS
// =====================================================

function getCurrentTimestamp() {
    return Date.now();
}

// Distance function moved to utils.js for consolidation
// function distance(p1, p2) {
//     const dx = p2.x - p1.x;
//     const dy = p2.y - p1.y;
//     return Math.sqrt(dx * dx + dy * dy);
// }

function rotatePoint(point, center, angle) {
    const cos = Math.cos(angle);
    const sin = Math.sin(angle);
    const dx = point.x - center.x;
    const dy = point.y - center.y;

    return {
        x: dx * cos - dy * sin + center.x,
        y: dx * sin + dy * cos + center.y
    };
}

// =====================================================
// BASE RECORD CLASS
// =====================================================

class TLRecord {
    constructor(data = {}) {
        this.id = data.id || (window.generateId ? window.generateId() : `record:${Date.now().toString(36)}${Math.random().toString(36).substr(2)}`);
        this.type = data.type || TLRecordType.SHAPE;
        this.typeName = data.typeName || 'record';
        this.meta = data.meta || {};
        this.props = data.props || {};
        this.createdAt = data.createdAt || getCurrentTimestamp();
        this.updatedAt = data.updatedAt || getCurrentTimestamp();
    }

    // Update record with new data
    update(changes) {
        Object.assign(this, changes);
        this.updatedAt = getCurrentTimestamp();
        return this;
    }

    // Create a deep copy of this record
    clone() {
        return new this.constructor(JSON.parse(JSON.stringify(this)));
    }

    // Serialize record to JSON
    toJSON() {
        return {
            id: this.id,
            type: this.type,
            typeName: this.typeName,
            meta: this.meta,
            props: this.props,
            createdAt: this.createdAt,
            updatedAt: this.updatedAt
        };
    }

    // Validate record structure
    validate() {
        if (!this.id) throw new Error('Record must have an id');
        if (!this.type) throw new Error('Record must have a type');
        return true;
    }
}

