// =====================================================
// EVENT INFO CLASSES
// =====================================================

class TLEventInfo {
    constructor(type, data = {}) {
        this.type = type;
        this.timestamp = getCurrentTimestamp();

        // Pointer information
        this.currentPagePoint = data.currentPagePoint || { x: 0, y: 0 };
        this.originPagePoint = data.originPagePoint || { x: 0, y: 0 };
        this.previousPagePoint = data.previousPagePoint || { x: 0, y: 0 };
        this.delta = data.delta || { x: 0, y: 0 };

        // Input state
        this.shiftKey = data.shiftKey || false;
        this.ctrlKey = data.ctrlKey || false;
        this.altKey = data.altKey || false;
        this.metaKey = data.metaKey || false;

        // Pointer details
        this.pointerId = data.pointerId || 0;
        this.button = data.button || 0;
        this.pressure = data.pressure || 0.5;
        this.isPen = data.isPen || false;
        this.touches = data.touches || [];

        // Target information
        this.target = data.target || null;
        this.shape = data.shape || null;

        // Keyboard information
        this.key = data.key || '';
        this.code = data.code || '';

        // Custom data
        this.name = data.name || '';
        this.data = data.data || {};
    }

    // Check if modifier keys are pressed
    hasModifier() {
        return this.shiftKey || this.ctrlKey || this.altKey || this.metaKey;
    }

    // Get distance from origin
    getDistanceFromOrigin() {
        return window.distance(this.currentPagePoint, this.originPagePoint);
    }

    // Clone event info with modifications
    clone(modifications = {}) {
        return new TLEventInfo(this.type, { ...this, ...modifications });
    }
}

// =====================================================
// EVENT RESULT CLASSES
// =====================================================

class TLEventResult {
    static CONTINUE = new TLEventResult('continue');
    static HANDLED = new TLEventResult('handled');

    constructor(type, data = {}) {
        this.type = type; // 'continue', 'handled', 'transition'
        this.nextState = data.nextState || null;
        this.data = data.data || {};
        this.preventDefault = data.preventDefault || false;
        this.stopPropagation = data.stopPropagation || false;
    }

    static transition(nextState, data = {}) {
        return new TLEventResult('transition', { nextState, data });
    }

    static handle(data = {}) {
        return new TLEventResult('handled', data);
    }

    static interrupt(nextState, data = {}) {
        return new TLEventResult('interrupt', { nextState, data });
    }
}

// =====================================================
// GEOMETRY UTILITIES
// =====================================================

class TLGeometry {
    // Point in polygon test
    static pointInPolygon(point, polygon) {
        let inside = false;
        for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
            const xi = polygon[i].x, yi = polygon[i].y;
            const xj = polygon[j].x, yj = polygon[j].y;

            if (((yi > point.y) !== (yj > point.y)) && 
                (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi)) {
                inside = !inside;
            }
        }
        return inside;
    }

    // Point in rotated rectangle
    static pointInRotatedRect(point, rect) {
        const { x, y, width, height, rotation = 0 } = rect;

        if (rotation === 0) {
            return point.x >= x && point.x <= x + width && 
                   point.y >= y && point.y <= y + height;
        }

        // Transform point to rectangle's local coordinates
        const center = { x: x + width / 2, y: y + height / 2 };
        const rotatedPoint = rotatePoint(point, center, -rotation);

        return rotatedPoint.x >= x && rotatedPoint.x <= x + width &&
               rotatedPoint.y >= y && rotatedPoint.y <= y + height;
    }

    // Get rotated rectangle corners
    static getRotatedRectCorners(rect) {
        const { x, y, width, height, rotation = 0 } = rect;
        const centerX = x + width / 2;
        const centerY = y + height / 2;

        const corners = [
            { x: x, y: y },
            { x: x + width, y: y },
            { x: x + width, y: y + height },
            { x: x, y: y + height }
        ];

        if (rotation === 0) {
            return corners;
        }

        return corners.map(corner => 
            rotatePoint(corner, { x: centerX, y: centerY }, rotation)
        );
    }

    // Distance from point to line segment
    static pointToLineDistance(point, lineStart, lineEnd) {
        const A = point.x - lineStart.x;
        const B = point.y - lineStart.y;
        const C = lineEnd.x - lineStart.x;
        const D = lineEnd.y - lineStart.y;

        const dot = A * C + B * D;
        const lenSq = C * C + D * D;

        if (lenSq === 0) {
            return Math.sqrt(A * A + B * B);
        }

        let param = dot / lenSq;
        param = clamp(param, 0, 1);

        const nearestX = lineStart.x + param * C;
        const nearestY = lineStart.y + param * D;

        const dx = point.x - nearestX;
        const dy = point.y - nearestY;

        return Math.sqrt(dx * dx + dy * dy);
    }

    // Bounding box intersection
    static boundsIntersect(bounds1, bounds2) {
        return !(bounds2.x > bounds1.x + bounds1.w ||
                bounds2.x + bounds2.w < bounds1.x ||
                bounds2.y > bounds1.y + bounds1.h ||
                bounds2.y + bounds2.h < bounds1.y);
    }

    // Get bounds of multiple shapes
    static getCommonBounds(shapes) {
        if (shapes.length === 0) return { x: 0, y: 0, w: 0, h: 0 };

        let minX = Infinity, minY = Infinity;
        let maxX = -Infinity, maxY = -Infinity;

        shapes.forEach(shape => {
            const bounds = shape.getBounds();
            minX = Math.min(minX, bounds.x);
            minY = Math.min(minY, bounds.y);
            maxX = Math.max(maxX, bounds.x + bounds.w);
            maxY = Math.max(maxY, bounds.y + bounds.h);
        });

        return {
            x: minX,
            y: minY,
            w: maxX - minX,
            h: maxY - minY
        };
    }
}

// =====================================================
// EXPORTS
// =====================================================

// Make everything globally available
if (typeof window !== 'undefined') {
    // Browser environment
    window.TLRecordType = TLRecordType;
    window.TLShapeType = TLShapeType;
    window.TLEventType = TLEventType;
    window.TLToolType = TLToolType;
    window.TLRecord = TLRecord;
    // TLShape, TLPage, TLCamera, TLInstance are defined in core-store.js
    window.TLEventInfo = TLEventInfo;
    window.TLEventResult = TLEventResult;
    window.TLGeometry = TLGeometry;

    // Utility functions - generateId now provided by utils.js
    window.getCurrentTimestamp = getCurrentTimestamp;
    window.clamp = clamp;
    window.lerp = lerp;
    // window.distance = distance; // Now provided by utils.js
    window.rotatePoint = rotatePoint;
} else if (typeof module !== 'undefined' && module.exports) {
    // Node.js environment
    module.exports = {
        TLRecordType,
        TLShapeType,
        TLEventType,
        TLToolType,
        TLRecord,
        // TLShape, TLPage, TLCamera, TLInstance are defined in core-store.js
        TLEventInfo,
        TLEventResult,
        TLGeometry,
        getCurrentTimestamp,
        clamp,
        lerp,
        distance,
        rotatePoint
    };
}

