/**
 * Core Store System - Based on @tldraw/store architecture
 * Provides typed records, schema migrations, and reactive updates
 */

// Record types - strongly typed like tldraw's tlschema
const TLRecordTypes = {
    SHAPE: 'shape',
    INSTANCE: 'instance', 
    PAGE: 'page',
    CAMERA: 'camera',
    ASSET: 'asset'
};

// Shape types are defined in types-definitions.js

// Base record class
class BaseRecord {
    constructor(type, id, props = {}) {
        this.id = id || this.generateId(type);
        this.typeName = type;
        this.meta = {};
        this.index = 'a1';
        Object.assign(this, props);
    }

    generateId(type) {
        // Use centralized generateId from utils.js
        return window.generateId ? window.generateId(type) : `${type}:${Date.now().toString(36)}${Math.random().toString(36).substr(2)}`;
    }

    clone() {
        return new this.constructor(this.typeName, this.id, { ...this });
    }

    update(changes) {
        Object.assign(this, changes);
        return this;
    }
}

// Shape record - equivalent to TLShape
class TLShape extends BaseRecord {
    constructor(type, id, props = {}) {
        super(TLRecordTypes.SHAPE, id, {
            type,
            x: 0,
            y: 0,
            rotation: 0,
            isLocked: false,
            parentId: 'page:main',
            index: 'a1',
            props: {},
            meta: {},
            ...props
        });
    }

    getBounds() {
        const { x, y, props } = this;
        const w = props.w || 100;
        const h = props.h || 100;
        return { x, y, w, h };
    }

    getCenter() {
        const bounds = this.getBounds();
        return {
            x: bounds.x + bounds.w / 2,
            y: bounds.y + bounds.h / 2
        };
    }

    translate(delta) {
        this.x += delta.x;
        this.y += delta.y;
        return this;
    }

    rotate(angle) {
        this.rotation += angle;
        return this;
    }

    scale(factor, origin = null) {
        if (origin) {
            const center = this.getCenter();
            const dx = center.x - origin.x;
            const dy = center.y - origin.y;
            this.x = origin.x + dx * factor;
            this.y = origin.y + dy * factor;
        }

        if (this.props.w) this.props.w *= factor;
        if (this.props.h) this.props.h *= factor;
        return this;
    }
}

// Instance record - tracks selection, current page, etc.
class TLInstance extends BaseRecord {
    constructor(id, props = {}) {
        super(TLRecordTypes.INSTANCE, id, {
            currentPageId: 'page:main',
            selectedShapeIds: [],
            brush: null,
            currentTool: 'select',
            hoveredShapeId: null,
            hintingShapeIds: [],
            followingUserId: null,
            opacityForNextShape: 1,
            stylesForNextShape: {},
            isToolLocked: false,
            exportBackground: true,
            ...props
        });
    }

    setSelectedShapes(ids) {
        this.selectedShapeIds = Array.isArray(ids) ? ids : [ids];
        return this;
    }

    setCurrentTool(tool) {
        this.currentTool = tool;
        return this;
    }
}

// Page record
class TLPage extends BaseRecord {
    constructor(id, props = {}) {
        super(TLRecordTypes.PAGE, id, {
            name: 'Page 1',
            index: 'a1',
            shapeIds: [],
            ...props
        });
    }

    addShape(shapeId) {
        // Accept either shape object or shape ID
        const id = typeof shapeId === 'string' ? shapeId : shapeId.id;
        if (!this.shapeIds.includes(id)) {
            this.shapeIds.push(id);
        }
        return this;
    }

    removeShape(shapeId) {
        this.shapeIds = this.shapeIds.filter(id => id !== shapeId);
        return this;
    }
}

// Camera record
class TLCamera extends BaseRecord {
    constructor(id, props = {}) {
        super(TLRecordTypes.CAMERA, id, {
            x: 0,
            y: 0,
            z: 1,
            meta: {},
            ...props
        });
    }

    screenToPage(screenPoint, viewport = null) {
        if (!screenPoint || typeof screenPoint.x === 'undefined' || typeof screenPoint.y === 'undefined') {
            console.warn('TLCamera.screenToPage: Invalid screenPoint', screenPoint);
            return { x: 0, y: 0 };
        }

        return {
            x: screenPoint.x / this.z - this.x,
            y: screenPoint.y / this.z - this.y
        };
    }

    pageToScreen(pagePoint, viewport = null) {
        return {
            x: (pagePoint.x + this.x) * this.z,
            y: (pagePoint.y + this.y) * this.z
        };
    }

    pan(delta) {
        this.x += delta.x / this.z;
        this.y += delta.y / this.z;
        return this;
    }

    zoom(factor, center = null) {
        if (center) {
            const beforePage = this.screenToPage(center);
            this.z *= factor;
            const afterPage = this.screenToPage(center);
            this.x += afterPage.x - beforePage.x;
            this.y += afterPage.y - beforePage.y;
        } else {
            this.z *= factor;
        }
        return this;
    }
}

// Store class - reactive data store like @tldraw/store
class TLStore extends EventTarget {
    constructor() {
        super();
        this.records = new Map();
        this.history = [];
        this.historyIndex = -1;
        this.maxHistorySize = 100;
        this.isInTransaction = false;
        this.transactionChanges = [];

        // Initialize default records
        this.initializeDefaults();
    }

    initializeDefaults() {
        // Create default instance
        const instance = new TLInstance('instance:main');
        this.put(instance);

        // Create default page
        const page = new TLPage('page:main');
        this.put(page);

        // Create default camera
        const camera = new TLCamera('camera:main');
        this.put(camera);

        // Save initial state
        this.mark('initial');
    }

    put(record) {
        const oldRecord = this.records.get(record.id);
        this.records.set(record.id, record);

        const change = {
            type: oldRecord ? 'update' : 'create',
            id: record.id,
            record: record.clone(),
            oldRecord: oldRecord?.clone()
        };

        if (this.isInTransaction) {
            this.transactionChanges.push(change);
        } else {
            this.recordChange(change);
        }

        this.dispatchEvent(new CustomEvent('change', { 
            detail: { changes: [change] } 
        }));

        return record;
    }

    get(id) {
        return this.records.get(id);
    }

    remove(id) {
        const record = this.records.get(id);
        if (record) {
            this.records.delete(id);

            const change = {
                type: 'remove',
                id,
                record: null,
                oldRecord: record.clone()
            };

            if (this.isInTransaction) {
                this.transactionChanges.push(change);
            } else {
                this.recordChange(change);
            }

            this.dispatchEvent(new CustomEvent('change', { 
                detail: { changes: [change] } 
            }));
        }
        return record;
    }

    get(id) {
        return this.records.get(id);
    }

    query(typeName) {
        return Array.from(this.records.values()).filter(r => r.typeName === typeName);
    }

    // Transaction support - like Editor.run()
    transaction(fn) {
        if (this.isInTransaction) {
            return fn(); // Nested transactions just execute
        }

        this.isInTransaction = true;
        this.transactionChanges = [];

        try {
            const result = fn();

            if (this.transactionChanges.length > 0) {
                this.recordChange({
                    type: 'transaction',
                    changes: [...this.transactionChanges]
                });
            }

            return result;
        } finally {
            this.isInTransaction = false;
            this.transactionChanges = [];
        }
    }

    // History management - proper undo/redo
    recordChange(change) {
        // Remove any future history
        this.history = this.history.slice(0, this.historyIndex + 1);

        // Add new change
        this.history.push(change);
        this.historyIndex++;

        // Limit history size
        if (this.history.length > this.maxHistorySize) {
            this.history.shift();
            this.historyIndex--;
        }
    }

    mark(name) {
        this.recordChange({
            type: 'mark',
            name,
            timestamp: Date.now()
        });
    }

    undo() {
        if (this.historyIndex >= 0) {
            const change = this.history[this.historyIndex];
            this.applyChangeReverse(change);
            this.historyIndex--;
            this.dispatchEvent(new CustomEvent('history-change', { 
                detail: { type: 'undo', change } 
            }));
            return true;
        }
        return false;
    }

    redo() {
        if (this.historyIndex < this.history.length - 1) {
            this.historyIndex++;
            const change = this.history[this.historyIndex];
            this.applyChange(change);
            this.dispatchEvent(new CustomEvent('history-change', { 
                detail: { type: 'redo', change } 
            }));
            return true;
        }
        return false;
    }

    applyChange(change) {
        switch (change.type) {
            case 'create':
                this.records.set(change.id, change.record.clone());
                break;
            case 'update':
                this.records.set(change.id, change.record.clone());
                break;
            case 'remove':
                this.records.delete(change.id);
                break;
            case 'transaction':
                change.changes.forEach(c => this.applyChange(c));
                break;
        }
    }

    applyChangeReverse(change) {
        switch (change.type) {
            case 'create':
                this.records.delete(change.id);
                break;
            case 'update':
                if (change.oldRecord) {
                    this.records.set(change.id, change.oldRecord.clone());
                } else {
                    this.records.delete(change.id);
                }
                break;
            case 'remove':
                this.records.set(change.id, change.oldRecord.clone());
                break;
            case 'transaction':
                // Reverse order for transactions
                [...change.changes].reverse().forEach(c => this.applyChangeReverse(c));
                break;
        }
    }

    // Convenience methods
    getCurrentInstance() {
        return this.get('instance:main');
    }

    getCurrentPage() {
        const instance = this.getCurrentInstance();
        return instance ? this.get(instance.currentPageId) : null;
    }

    getCurrentCamera() {
        return this.get('camera:main');
    }

    getCurrentPageShapes() {
        const page = this.getCurrentPage();
        if (!page) {
            console.log('Store: getCurrentPageShapes - no current page');
            return [];
        }

        const shapes = page.shapeIds
            .map(id => this.get(id))
            .filter(Boolean);

        console.log('Store: getCurrentPageShapes returning', shapes.length, 'shapes:', shapes.map(s => `${s.type}:${s.id}`));

        return shapes;
    }

    getSelectedShapes() {
        const instance = this.getCurrentInstance();
        if (!instance) return [];

        return instance.selectedShapeIds
            .map(id => this.get(id))
            .filter(Boolean);
    }

    // Snapshot for persistence
    getSnapshot() {
        const records = {};
        for (const [id, record] of this.records) {
            records[id] = { ...record };
        }
        return { records };
    }

    loadSnapshot(snapshot) {
        this.records.clear();
        for (const [id, recordData] of Object.entries(snapshot.records)) {
            let record;
            switch (recordData.typeName) {
                case TLRecordTypes.SHAPE:
                    record = new TLShape(recordData.type, id, recordData);
                    break;
                case TLRecordTypes.INSTANCE:
                    record = new TLInstance(id, recordData);
                    break;
                case TLRecordTypes.PAGE:
                    record = new TLPage(id, recordData);
                    break;
                case TLRecordTypes.CAMERA:
                    record = new TLCamera(id, recordData);
                    break;
                default:
                    record = new BaseRecord(recordData.typeName, id, recordData);
            }
            this.records.set(id, record);
        }

        this.dispatchEvent(new CustomEvent('snapshot-loaded'));
    }
}

// Export all classes
if (typeof window !== 'undefined') {
    window.TLRecord = TLRecord;
    window.TLShapeType = TLShapeType;
    window.BaseRecord = BaseRecord;
    window.TLShape = TLShape;
    window.TLInstance = TLInstance;
    window.TLPage = TLPage;
    window.TLCamera = TLCamera;
    window.TLStore = TLStore;
}

