   327‚Üí    static pointInPolygon(point, polygon) {
   328‚Üí        let inside = false;
   329‚Üí        for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
   330‚Üí            const xi = polygon[i].x, yi = polygon[i].y;
   331‚Üí            const xj = polygon[j].x, yj = polygon[j].y;
   332‚Üí            
   333‚Üí            if (((yi > point.y) !== (yj > point.y)) && 
   334‚Üí                (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi)) {
   335‚Üí                inside = !inside;
   336‚Üí            }
