   341‚Üí    static pointInRotatedRect(point, rect) {
   342‚Üí        const { x, y, width, height, rotation = 0 } = rect;
   343‚Üí        
   344‚Üí        if (rotation === 0) {
   345‚Üí            return point.x >= x && point.x <= x + width && 
   346‚Üí                   point.y >= y && point.y <= y + height;
   347‚Üí        }
   348‚Üí
   349‚Üí        const cos = Math.cos(-rotation);
   350‚Üí        const sin = Math.sin(-rotation);
   351‚Üí        const dx = point.x - (x + width / 2);
   352‚Üí        const dy = point.y - (y + height / 2);
   353‚Üí        
   354‚Üí        const localX = dx * cos - dy * sin;
   355‚Üí        const localY = dx * sin + dy * cos;
   356‚Üí        
   357‚Üí        return localX >= -width / 2 && localX <= width / 2 && 
   358‚Üí               localY >= -height / 2 && localY <= height / 2;
   359‚Üí    }
   360‚Üí    
   361‚Üí    // Geometric calculations
   362‚Üí    static getRotatedRectCorners(rect) {
   363‚Üí        const { x, y, width, height, rotation = 0 } = rect;
   364‚Üí        const centerX = x + width / 2;
   365‚Üí        const centerY = y + height / 2;
   366‚Üí        
   367‚Üí        const corners = [
   368‚Üí            { x: -width / 2, y: -height / 2 },
   369‚Üí            { x: width / 2, y: -height / 2 },
   370‚Üí            { x: width / 2, y: height / 2 },
   371‚Üí            { x: -width / 2, y: height / 2 }
   372‚Üí        ];
   373‚Üí        
   374‚Üí        if (rotation === 0) {
   375‚Üí            return corners.map(corner => ({
   376‚Üí                x: corner.x + centerX,
   377‚Üí                y: corner.y + centerY
   378‚Üí            }));
   379‚Üí        }
   380‚Üí        
   381‚Üí        const cos = Math.cos(rotation);
   382‚Üí        const sin = Math.sin(rotation);
   383‚Üí        
   384‚Üí        return corners.map(corner => ({
   385‚Üí            x: corner.x * cos - corner.y * sin + centerX,
   386‚Üí            y: corner.x * sin + corner.y * cos + centerY
   387‚Üí        }));
   388‚Üí    }
   389‚Üí    
   390‚Üí    static pointToLineDistance(point, lineStart, lineEnd) {
   391‚Üí        const A = point.x - lineStart.x;
   392‚Üí        const B = point.y - lineStart.y;
   393‚Üí        const C = lineEnd.x - lineStart.x;
   394‚Üí        const D = lineEnd.y - lineStart.y;
   395‚Üí
   396‚Üí        const dot = A * C + B * D;
   397‚Üí        const lenSq = C * C + D * D;
   398‚Üí        
   399‚Üí        if (lenSq === 0) {
   400‚Üí            return Math.sqrt(A * A + B * B);
   401‚Üí        }
   402‚Üí        
   403‚Üí        let param = dot / lenSq;
   404‚Üí        param = clamp(param, 0, 1);
   405‚Üí        
   406‚Üí        const nearestX = lineStart.x + param * C;
   407‚Üí        const nearestY = lineStart.y + param * D;
   408‚Üí        
   409‚Üí        const dx = point.x - nearestX;
   410‚Üí        const dy = point.y - nearestY;
   411‚Üí        
   412‚Üí        return Math.sqrt(dx * dx + dy * dy);
   413‚Üí    }
   414‚Üí}
