   416‚Üí// Shape transformation utilities
   417‚Üífunction rotatePoint(point, center, angle) {
   418‚Üí    const cos = Math.cos(angle);
   419‚Üí    const sin = Math.sin(angle);
   420‚Üí    const dx = point.x - center.x;
   421‚Üí    const dy = point.y - center.y;
   422‚Üí    
   423‚Üí    return {
   424‚Üí        x: dx * cos - dy * sin + center.x,
   425‚Üí        y: dx * sin + dy * cos + center.y
   426‚Üí    };
   427‚Üí}
