   429‚Üífunction transformBounds(bounds, transform) {
   430‚Üí    const { x, y, w, h } = bounds;
   431‚Üí    const { scaleX = 1, scaleY = 1, rotation = 0, translateX = 0, translateY = 0 } = transform;
   432‚Üí    
   433‚Üí    // Apply scale
   434‚Üí    const scaledW = w * scaleX;
   435‚Üí    const scaledH = h * scaleY;
   436‚Üí    
   437‚Üí    // Apply rotation (around center)
   438‚Üí    if (rotation !== 0) {
   439‚Üí        const centerX = x + w / 2;
   440‚Üí        const centerY = y + h / 2;
   441‚Üí        const corners = [
   442‚Üí            { x: x, y: y },
   443‚Üí            { x: x + scaledW, y: y },
   444‚Üí            { x: x + scaledW, y: y + scaledH },
   445‚Üí            { x: x, y: y + scaledH }
   446‚Üí        ];
   447‚Üí        
   448‚Üí        const rotatedCorners = corners.map(corner => 
   449‚Üí            rotatePoint(corner, { x: centerX, y: centerY }, rotation)
   450‚Üí        );
   451‚Üí        
   452‚Üí        const minX = Math.min(...rotatedCorners.map(c => c.x));
   453‚Üí        const minY = Math.min(...rotatedCorners.map(c => c.y));
   454‚Üí        const maxX = Math.max(...rotatedCorners.map(c => c.x));
   455‚Üí        const maxY = Math.max(...rotatedCorners.map(c => c.y));
   456‚Üí        
   457‚Üí        return {
   458‚Üí            x: minX + translateX,
   459‚Üí            y: minY + translateY,
   460‚Üí            w: maxX - minX,
   461‚Üí            h: maxY - minY
   462‚Üí        };
   463‚Üí    }
   464‚Üí    
   465‚Üí    // No rotation, simple transform
   466‚Üí    return {
   467‚Üí        x: x + translateX,
   468‚Üí        y: y + translateY,
   469‚Üí        w: scaledW,
   470‚Üí        h: scaledH
   471‚Üí    };
   472‚Üí}
