   527‚Üí// STATE MANAGEMENT UTILITIES (Chunk 2A consolidation)
   528‚Üíclass EventBus {
   529‚Üí    constructor() {
   530‚Üí        this.events = new Map();
   531‚Üí        this.onceEvents = new Map();
   532‚Üí        this.debugMode = false;
   533‚Üí    }
   534‚Üí    
   535‚Üí    on(event, handler) {
   536‚Üí        if (!this.events.has(event)) {
   537‚Üí            this.events.set(event, new Set());
   538‚Üí        }
   539‚Üí        this.events.get(event).add(handler);
   540‚Üí        
   541‚Üí        if (this.debugMode) {
   542‚Üí            console.log(`EventBus: Subscribed to '${event}'`);
   543‚Üí        }
   544‚Üí        
   545‚Üí        // Return unsubscribe function
   546‚Üí        return () => this.off(event, handler);
   547‚Üí    }
   548‚Üí    
   549‚Üí    once(event, handler) {
   550‚Üí        if (!this.onceEvents.has(event)) {
   551‚Üí            this.onceEvents.set(event, new Set());
   552‚Üí        }
   553‚Üí        this.onceEvents.get(event).add(handler);
   554‚Üí        
   555‚Üí        if (this.debugMode) {
   556‚Üí            console.log(`EventBus: Subscribed once to '${event}'`);
   557‚Üí        }
   558‚Üí    }
   559‚Üí    
   560‚Üí    off(event, handler) {
   561‚Üí        if (this.events.has(event)) {
   562‚Üí            this.events.get(event).delete(handler);
   563‚Üí            if (this.events.get(event).size === 0) {
   564‚Üí                this.events.delete(event);
   565‚Üí            }
   566‚Üí        }
   567‚Üí        
   568‚Üí        if (this.onceEvents.has(event)) {
   569‚Üí            this.onceEvents.get(event).delete(handler);
   570‚Üí            if (this.onceEvents.get(event).size === 0) {
   571‚Üí                this.onceEvents.delete(event);
   572‚Üí            }
   573‚Üí        }
   574‚Üí        
   575‚Üí        if (this.debugMode) {
   576‚Üí            console.log(`EventBus: Unsubscribed from '${event}'`);
   577‚Üí        }
   578‚Üí    }
   579‚Üí    
   580‚Üí    emit(event, data = null) {
   581‚Üí        // Handle regular subscribers
   582‚Üí        if (this.events.has(event)) {
   583‚Üí            this.events.get(event).forEach(handler => {
   584‚Üí                try {
   585‚Üí                    handler(data, event);
   586‚Üí                } catch (error) {
   587‚Üí                    console.error(`EventBus: Error in handler for '${event}':`, error);
   588‚Üí                }
   589‚Üí            });
   590‚Üí        }
   591‚Üí        
   592‚Üí        // Handle once subscribers
   593‚Üí        if (this.onceEvents.has(event)) {
   594‚Üí            const onceHandlers = this.onceEvents.get(event);
   595‚Üí            onceHandlers.forEach(handler => {
   596‚Üí                try {
   597‚Üí                    handler(data, event);
   598‚Üí                } catch (error) {
   599‚Üí                    console.error(`EventBus: Error in once handler for '${event}':`, error);
   600‚Üí                }
   601‚Üí            });
   602‚Üí            this.onceEvents.delete(event); // Remove all once handlers after execution
   603‚Üí        }
   604‚Üí        
   605‚Üí        if (this.debugMode) {
   606‚Üí            console.log(`EventBus: Emitted '${event}' with data:`, data);
   607‚Üí        }
   608‚Üí    }
   609‚Üí    
   610‚Üí    clear() {
   611‚Üí        this.events.clear();
   612‚Üí        this.onceEvents.clear();
   613‚Üí    }
   614‚Üí    
   615‚Üí    debug(enabled = true) {
   616‚Üí        this.debugMode = enabled;
   617‚Üí    }
   618‚Üí    
   619‚Üí    getEventCount() {
   620‚Üí        return this.events.size + this.onceEvents.size;
   621‚Üí    }
   622‚Üí    
   623‚Üí    getSubscriberCount(event) {
   624‚Üí        const regular = this.events.has(event) ? this.events.get(event).size : 0;
   625‚Üí        const once = this.onceEvents.has(event) ? this.onceEvents.get(event).size : 0;
   626‚Üí        return regular + once;
   627‚Üí    }
   628‚Üí}
