   630‚Üí// State synchronization utilities
   631‚Üífunction createStateSync(interval = 100) {
   632‚Üí    const subscribers = new Set();
   633‚Üí    let lastState = null;
   634‚Üí    let syncTimer = null;
   635‚Üí    
   636‚Üí    function sync() {
   637‚Üí        const currentState = captureCurrentState();
   638‚Üí        if (JSON.stringify(currentState) !== JSON.stringify(lastState)) {
   639‚Üí            subscribers.forEach(callback => {
   640‚Üí                try {
   641‚Üí                    callback(currentState, lastState);
   642‚Üí                } catch (error) {
   643‚Üí                    console.error('StateSync: Error in subscriber:', error);
   644‚Üí                }
   645‚Üí            });
   646‚Üí            lastState = currentState;
   647‚Üí        }
   648‚Üí    }
   649‚Üí    
   650‚Üí    function captureCurrentState() {
   651‚Üí        return {
   652‚Üí            timestamp: Date.now(),
   653‚Üí            camera: window.tlEditor?.store?.getCurrentCamera?.() || null,
   654‚Üí            selection: window.tlEditor?.store?.getSelectedShapes?.() || [],
   655‚Üí            currentTool: window.tlEditor?.store?.getCurrentTool?.() || 'select',
   656‚Üí            shapeCount: window.tlEditor?.store?.getAllShapes?.()?.length || 0
   657‚Üí        };
   658‚Üí    }
   659‚Üí    
   660‚Üí    return {
   661‚Üí        subscribe(callback) {
   662‚Üí            subscribers.add(callback);
   663‚Üí            if (subscribers.size === 1) {
   664‚Üí                syncTimer = setInterval(sync, interval);
   665‚Üí            }
   666‚Üí            return () => {
   667‚Üí                subscribers.delete(callback);
   668‚Üí                if (subscribers.size === 0 && syncTimer) {
   669‚Üí                    clearInterval(syncTimer);
   670‚Üí                    syncTimer = null;
   671‚Üí                }
   672‚Üí            };
   673‚Üí        },
   674‚Üí        
   675‚Üí        forceSync() {
   676‚Üí            sync();
   677‚Üí        },
   678‚Üí        
   679‚Üí        stop() {
   680‚Üí            if (syncTimer) {
   681‚Üí                clearInterval(syncTimer);
   682‚Üí                syncTimer = null;
   683‚Üí            }
   684‚Üí            subscribers.clear();
   685‚Üí        }
   686‚Üí    };
   687‚Üí}
   688‚Üí
   689‚Üí// Store state utilities
   690‚Üífunction createStoreSubscription(store, event, handler) {
   691‚Üí    if (!store || typeof store.on !== 'function') {
