   693‚Üí        return () => {};
   694‚Üí    }
   695‚Üí    
   696‚Üí    store.on(event, handler);
   697‚Üí    return () => {
   698‚Üí        if (store && typeof store.off === 'function') {
   699‚Üí            store.off(event, handler);
