   726‚Üífunction createElement(tag, attributes = {}, children = []) {
   727‚Üí    const element = document.createElement(tag);
   728‚Üí    
   729‚Üí    // Set attributes
   730‚Üí    Object.entries(attributes).forEach(([key, value]) => {
   731‚Üí        if (key === 'className') {
   732‚Üí            element.className = value;
   733‚Üí        } else if (key === 'style' && typeof value === 'object') {
   734‚Üí            Object.assign(element.style, value);
   735‚Üí        } else if (key.startsWith('data-')) {
   736‚Üí            element.setAttribute(key, value);
   737‚Üí        } else {
   738‚Üí            element[key] = value;
   739‚Üí        }
   740‚Üí    });
   741‚Üí    
   742‚Üí    // Append children
   743‚Üí    children.forEach(child => {
   744‚Üí        if (typeof child === 'string') {
   745‚Üí            element.appendChild(document.createTextNode(child));
   746‚Üí        } else if (child instanceof Node) {
   747‚Üí            element.appendChild(child);
   748‚Üí        }
   749‚Üí    });
   750‚Üí    
   751‚Üí    return element;
   752‚Üí}
