   869‚Üí        };
   870‚Üí        const callNow = immediate && !timeout;
   871‚Üí        clearTimeout(timeout);
   872‚Üí        timeout = setTimeout(later, wait);
   873‚Üí        if (callNow) func.apply(this, args);
   874‚Üí    };
   875‚Üí}
