   886‚Üí}
   887‚Üí
   888‚Üí// Object utilities
   889‚Üífunction deepClone(obj) {
   890‚Üí    if (obj === null || typeof obj !== 'object') return obj;
   891‚Üí    if (obj instanceof Date) return new Date(obj.getTime());
   892‚Üí    if (obj instanceof Array) return obj.map(item => deepClone(item));
   893‚Üí    if (typeof obj === 'object') {
   894‚Üí        const clonedObj = {};
   895‚Üí        for (const key in obj) {
