/**
 * Utility functions for canvas functionality
 */

// Generate unique IDs
function generateId(prefix = 'shape') {
    return `${prefix}:${Date.now().toString(36)}${Math.random().toString(36).substr(2)}`;
}

// Math utilities
function clamp(value, min, max) {
    return Math.min(Math.max(value, min), max);
}

function lerp(a, b, t) {
    return a + (b - a) * t;
}

// Geometry utilities
const TLGeometry = {
    boundsIntersect(a, b) {
        return !(
            a.x + a.w < b.x ||
            b.x + b.w < a.x ||
            a.y + a.h < b.y ||
            b.y + b.h < a.y
        );
    },

    getCommonBounds(shapes) {
        if (!shapes || shapes.length === 0) {
            return { x: 0, y: 0, w: 0, h: 0 };
        }

        let minX = Infinity;
        let minY = Infinity;
        let maxX = -Infinity;
        let maxY = -Infinity;

        shapes.forEach(shape => {
            const bounds = shape.getBounds ? shape.getBounds() : {
                x: shape.x || 0,
                y: shape.y || 0,
                w: shape.props?.w || 100,
                h: shape.props?.h || 100
            };

            minX = Math.min(minX, bounds.x);
            minY = Math.min(minY, bounds.y);
            maxX = Math.max(maxX, bounds.x + bounds.w);
            maxY = Math.max(maxY, bounds.y + bounds.h);
        });

        return {
            x: minX,
            y: minY,
            w: maxX - minX,
            h: maxY - minY
        };
    }
};

// Event result types
const TLEventResult = {
    HANDLED: { type: 'handled' },
    CONTINUE: { type: 'continue' },
    transition: (state, data = {}) => ({ type: 'transition', state, data })
};

// ===== CONSOLIDATED UTILITY FUNCTIONS (ye-code-combiner Chunks 1A, 1B, 2A, 3A & 4A) =====

// DIAGNOSTIC & PERFORMANCE UTILITIES (Chunk 4A consolidation)
class DiagnosticUtils {
    constructor() {
        this.metrics = new Map();
        this.errorLog = [];
        this.performanceMarks = new Map();
        this.startTime = Date.now();
    }

    // Performance monitoring
    static measurePerformance(name, fn) {
        const start = performance.now();
        try {
            const result = fn();
            const duration = performance.now() - start;
            console.log(`‚ö° ${name}: ${duration.toFixed(2)}ms`);
            return result;
        } catch (error) {
            const duration = performance.now() - start;
            console.error(`‚ùå ${name} failed after ${duration.toFixed(2)}ms:`, error);
            throw error;
        }
    }

    static async measureAsyncPerformance(name, asyncFn) {
        const start = performance.now();
        try {
            const result = await asyncFn();
            const duration = performance.now() - start;
            console.log(`‚ö° ${name}: ${duration.toFixed(2)}ms`);
            return result;
        } catch (error) {
            const duration = performance.now() - start;
            console.error(`‚ùå ${name} failed after ${duration.toFixed(2)}ms:`, error);
            throw error;
        }
    }

    // Error tracking utilities
    static logError(error, context = '') {
        const errorEntry = {
            timestamp: new Date().toISOString(),
            message: error.message || error,
            stack: error.stack,
            context,
            type: error.constructor.name,
            userAgent: navigator.userAgent
        };

        console.error('üö® Error logged:', errorEntry);
        return errorEntry;
    }

    static createErrorReport(errors) {
        return {
            timestamp: new Date().toISOString(),
            totalErrors: errors.length,
            errorsByType: errors.reduce((acc, err) => {
                acc[err.type] = (acc[err.type] || 0) + 1;
                return acc;
            }, {}),
            recentErrors: errors.slice(-5),
            systemInfo: {
                userAgent: navigator.userAgent,
                url: window.location.href,
                viewport: {
                    width: window.innerWidth,
                    height: window.innerHeight
                }
            }
        };
    }

    // Testing utilities
    static async runTest(name, testFn, timeout = 5000) {
        const startTime = Date.now();

        return new Promise((resolve) => {
            const timeoutId = setTimeout(() => {
                resolve({
                    name,
                    success: false,
                    error: 'Test timeout',
                    duration: Date.now() - startTime
                });
            }, timeout);

            try {
                const result = testFn();

                if (result instanceof Promise) {
                    result
                        .then(() => {
                            clearTimeout(timeoutId);
                            resolve({
                                name,
                                success: true,
                                duration: Date.now() - startTime
                            });
                        })
                        .catch((error) => {
                            clearTimeout(timeoutId);
                            resolve({
                                name,
                                success: false,
                                error: error.message || error,
                                duration: Date.now() - startTime
                            });
                        });
                } else {
                    clearTimeout(timeoutId);
                    resolve({
                        name,
                        success: true,
                        result,
                        duration: Date.now() - startTime
                    });
                }
            } catch (error) {
                clearTimeout(timeoutId);
                resolve({
                    name,
                    success: false,
                    error: error.message || error,
                    duration: Date.now() - startTime
                });
            }
        });
    }

    static async runTestSuite(tests, options = {}) {
        const { parallel = false, timeout = 5000, verbose = true } = options;
        const results = [];
        const startTime = Date.now();

        if (parallel) {
            const promises = tests.map(test => 
                DiagnosticUtils.runTest(test.name, test.fn, timeout)
            );
            results.push(...await Promise.all(promises));
        } else {
            for (const test of tests) {
                const result = await DiagnosticUtils.runTest(test.name, test.fn, timeout);
                results.push(result);
                if (verbose) {
                    console.log(`${result.success ? '‚úÖ' : '‚ùå'} ${result.name} (${result.duration}ms)`);
                }
            }
        }

        const totalDuration = Date.now() - startTime;
        const passed = results.filter(r => r.success).length;
        const failed = results.length - passed;

        const summary = {
            total: results.length,
            passed,
            failed,
            duration: totalDuration,
            successRate: (passed / results.length * 100).toFixed(1) + '%',
            results
        };

        if (verbose) {
            console.log(`\nüìä Test Suite Complete: ${passed}/${results.length} passed (${summary.successRate}) in ${totalDuration}ms`);
        }

        return summary;
    }

    // System diagnostics
    static getSystemDiagnostics() {
        return {
            timestamp: Date.now(),
            memory: performance.memory ? {
                used: Math.round(performance.memory.usedJSHeapSize / 1024 / 1024),
                total: Math.round(performance.memory.totalJSHeapSize / 1024 / 1024),
                limit: Math.round(performance.memory.jsHeapSizeLimit / 1024 / 1024)
            } : null,
            navigation: performance.navigation ? {
                type: performance.navigation.type,
                redirectCount: performance.navigation.redirectCount
            } : null,
            timing: performance.timing ? {
                loadComplete: performance.timing.loadEventEnd - performance.timing.navigationStart,
                domReady: performance.timing.domContentLoadedEventEnd - performance.timing.navigationStart,
                firstPaint: performance.getEntriesByType('paint').find(entry => entry.name === 'first-paint')?.startTime || null
            } : null,
            url: window.location.href,
            userAgent: navigator.userAgent,
            viewport: {
                width: window.innerWidth,
                height: window.innerHeight
            },
            connection: navigator.connection ? {
                effectiveType: navigator.connection.effectiveType,
                downlink: navigator.connection.downlink
            } : null
        };
    }
}

// Performance monitoring utilities
function startPerformanceTimer(name) {
    const markName = `timer-${name}-start`;
    performance.mark(markName);
    return markName;
}

function endPerformanceTimer(startMark, name) {
    const endMark = `timer-${name}-end`;
    performance.mark(endMark);
    performance.measure(name, startMark, endMark);

    const measure = performance.getEntriesByName(name, 'measure')[0];
    return measure ? measure.duration : null;
}

// Error boundary utility
function createErrorBoundary(fn, errorHandler) {
    return function(...args) {
        try {
            const result = fn.apply(this, args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    if (errorHandler) errorHandler(error);
                    throw error;
                });
            }
            return result;
        } catch (error) {
            if (errorHandler) errorHandler(error);
            throw error;
        }
    };
}

// Memory monitoring
function getMemoryUsage() {
    if (!performance.memory) return null;

    return {
        used: Math.round(performance.memory.usedJSHeapSize / 1024 / 1024),
        total: Math.round(performance.memory.totalJSHeapSize / 1024 / 1024),
        limit: Math.round(performance.memory.jsHeapSizeLimit / 1024 / 1024),
        percentage: Math.round((performance.memory.usedJSHeapSize / performance.memory.jsHeapSizeLimit) * 100)
    };
}

// Global diagnostic instance
const globalDiagnostics = new DiagnosticUtils();

// SHAPE GEOMETRY UTILITIES (Chunk 3A consolidation)
class ShapeGeometry {
    // Point intersection tests
    static pointInPolygon(point, polygon) {
        let inside = false;
        for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
            const xi = polygon[i].x, yi = polygon[i].y;
            const xj = polygon[j].x, yj = polygon[j].y;

            if (((yi > point.y) !== (yj > point.y)) && 
                (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi)) {
                inside = !inside;
            }
        }
        return inside;
    }

    static pointInRotatedRect(point, rect) {
        const { x, y, width, height, rotation = 0 } = rect;

        if (rotation === 0) {
            return point.x >= x && point.x <= x + width && 
                   point.y >= y && point.y <= y + height;
        }

        const cos = Math.cos(-rotation);
        const sin = Math.sin(-rotation);
        const dx = point.x - (x + width / 2);
        const dy = point.y - (y + height / 2);

        const localX = dx * cos - dy * sin;
        const localY = dx * sin + dy * cos;

        return localX >= -width / 2 && localX <= width / 2 && 
               localY >= -height / 2 && localY <= height / 2;
    }

    // Geometric calculations
    static getRotatedRectCorners(rect) {
        const { x, y, width, height, rotation = 0 } = rect;
        const centerX = x + width / 2;
        const centerY = y + height / 2;

        const corners = [
            { x: -width / 2, y: -height / 2 },
            { x: width / 2, y: -height / 2 },
            { x: width / 2, y: height / 2 },
            { x: -width / 2, y: height / 2 }
        ];

        if (rotation === 0) {
            return corners.map(corner => ({
                x: corner.x + centerX,
                y: corner.y + centerY
            }));
        }

        const cos = Math.cos(rotation);
        const sin = Math.sin(rotation);

        return corners.map(corner => ({
            x: corner.x * cos - corner.y * sin + centerX,
            y: corner.x * sin + corner.y * cos + centerY
        }));
    }

    static pointToLineDistance(point, lineStart, lineEnd) {
        const A = point.x - lineStart.x;
        const B = point.y - lineStart.y;
        const C = lineEnd.x - lineStart.x;
        const D = lineEnd.y - lineStart.y;

        const dot = A * C + B * D;
        const lenSq = C * C + D * D;

        if (lenSq === 0) {
            return Math.sqrt(A * A + B * B);
        }

        let param = dot / lenSq;
        param = clamp(param, 0, 1);

        const nearestX = lineStart.x + param * C;
        const nearestY = lineStart.y + param * D;

        const dx = point.x - nearestX;
        const dy = point.y - nearestY;

        return Math.sqrt(dx * dx + dy * dy);
    }
}

// Shape transformation utilities
function rotatePoint(point, center, angle) {
    const cos = Math.cos(angle);
    const sin = Math.sin(angle);
    const dx = point.x - center.x;
    const dy = point.y - center.y;

    return {
        x: dx * cos - dy * sin + center.x,
        y: dx * sin + dy * cos + center.y
    };
}

function transformBounds(bounds, transform) {
    const { x, y, w, h } = bounds;
    const { scaleX = 1, scaleY = 1, rotation = 0, translateX = 0, translateY = 0 } = transform;

    // Apply scale
    const scaledW = w * scaleX;
    const scaledH = h * scaleY;

    // Apply rotation (around center)
    if (rotation !== 0) {
        const centerX = x + w / 2;
        const centerY = y + h / 2;
        const corners = [
            { x: x, y: y },
            { x: x + scaledW, y: y },
            { x: x + scaledW, y: y + scaledH },
            { x: x, y: y + scaledH }
        ];

        const rotatedCorners = corners.map(corner => 
            rotatePoint(corner, { x: centerX, y: centerY }, rotation)
        );

        const minX = Math.min(...rotatedCorners.map(c => c.x));
        const minY = Math.min(...rotatedCorners.map(c => c.y));
        const maxX = Math.max(...rotatedCorners.map(c => c.x));
        const maxY = Math.max(...rotatedCorners.map(c => c.y));

        return {
            x: minX + translateX,
            y: minY + translateY,
            w: maxX - minX,
            h: maxY - minY
        };
    }

    // No rotation, simple transform
    return {
        x: x + translateX,
        y: y + translateY,
        w: scaledW,
        h: scaledH
    };
}

// Shape bounds utilities
function getBoundsCenter(bounds) {
    return {
        x: bounds.x + bounds.w / 2,
        y: bounds.y + bounds.h / 2
    };
}

function expandBounds(bounds, padding) {
    return {
        x: bounds.x - padding,
        y: bounds.y - padding,
        w: bounds.w + padding * 2,
        h: bounds.h + padding * 2
    };
}

function boundsContainPoint(bounds, point) {
    return point.x >= bounds.x && 
           point.x <= bounds.x + bounds.w &&
           point.y >= bounds.y && 
           point.y <= bounds.y + bounds.h;
}

function boundsIntersect(a, b) {
    return !(a.x + a.w < b.x ||
             b.x + b.w < a.x ||
             a.y + a.h < b.y ||
             b.y + b.h < a.y);
}

// Shape creation utilities  
function createShapeBounds(x, y, w, h) {
    return {
        x: Math.min(x, x + w),
        y: Math.min(y, y + h),
        w: Math.abs(w),
        h: Math.abs(h)
    };
}

function normalizeShapeProps(props, shapeType) {
    const defaults = {
        rectangle: { w: 100, h: 100, fill: 'none', stroke: '#000000' },
        ellipse: { w: 100, h: 100, fill: 'none', stroke: '#000000' },
        text: { text: 'Text', fontSize: 16, w: 100, h: 24 },
        draw: { segments: [], strokeWidth: 2, color: '#000000' },
        arrow: { start: {x: 0, y: 0}, end: {x: 100, y: 100}, stroke: '#000000' }
    };

    return { ...defaults[shapeType] || {}, ...props };
}

// STATE MANAGEMENT UTILITIES (Chunk 2A consolidation)
class EventBus {
    constructor() {
        this.events = new Map();
        this.onceEvents = new Map();
        this.debugMode = false;
    }

    on(event, handler) {
        if (!this.events.has(event)) {
            this.events.set(event, new Set());
        }
        this.events.get(event).add(handler);

        if (this.debugMode) {
            console.log(`EventBus: Subscribed to '${event}'`);
        }

        // Return unsubscribe function
        return () => this.off(event, handler);
    }

    once(event, handler) {
        if (!this.onceEvents.has(event)) {
            this.onceEvents.set(event, new Set());
        }
        this.onceEvents.get(event).add(handler);

        if (this.debugMode) {
            console.log(`EventBus: Subscribed once to '${event}'`);
        }
    }

    off(event, handler) {
        if (this.events.has(event)) {
            this.events.get(event).delete(handler);
            if (this.events.get(event).size === 0) {
                this.events.delete(event);
            }
        }

        if (this.onceEvents.has(event)) {
            this.onceEvents.get(event).delete(handler);
            if (this.onceEvents.get(event).size === 0) {
                this.onceEvents.delete(event);
            }
        }

        if (this.debugMode) {
            console.log(`EventBus: Unsubscribed from '${event}'`);
        }
    }

    emit(event, data = null) {
        // Handle regular subscribers
        if (this.events.has(event)) {
            this.events.get(event).forEach(handler => {
                try {
                    handler(data, event);
                } catch (error) {
                    console.error(`EventBus: Error in handler for '${event}':`, error);
                }
            });
        }

        // Handle once subscribers
        if (this.onceEvents.has(event)) {
            const onceHandlers = this.onceEvents.get(event);
            onceHandlers.forEach(handler => {
                try {
                    handler(data, event);
                } catch (error) {
                    console.error(`EventBus: Error in once handler for '${event}':`, error);
                }
            });
            this.onceEvents.delete(event); // Remove all once handlers after execution
        }

        if (this.debugMode) {
            console.log(`EventBus: Emitted '${event}' with data:`, data);
        }
    }

    clear() {
        this.events.clear();
        this.onceEvents.clear();
    }

    debug(enabled = true) {
        this.debugMode = enabled;
    }

    getEventCount() {
        return this.events.size + this.onceEvents.size;
    }

    getSubscriberCount(event) {
        const regular = this.events.has(event) ? this.events.get(event).size : 0;
        const once = this.onceEvents.has(event) ? this.onceEvents.get(event).size : 0;
        return regular + once;
    }
}

// State synchronization utilities
function createStateSync(interval = 100) {
    const subscribers = new Set();
    let lastState = null;
    let syncTimer = null;

    function sync() {
        const currentState = captureCurrentState();
        if (JSON.stringify(currentState) !== JSON.stringify(lastState)) {
            subscribers.forEach(callback => {
                try {
                    callback(currentState, lastState);
                } catch (error) {
                    console.error('StateSync: Error in subscriber:', error);
                }
            });
            lastState = currentState;
        }
    }

    function captureCurrentState() {
        return {
            timestamp: Date.now(),
            camera: window.tlEditor?.store?.getCurrentCamera?.() || null,
            selection: window.tlEditor?.store?.getSelectedShapes?.() || [],
            currentTool: window.tlEditor?.store?.getCurrentTool?.() || 'select',
            shapeCount: window.tlEditor?.store?.getAllShapes?.()?.length || 0
        };
    }

    return {
        subscribe(callback) {
            subscribers.add(callback);
            if (subscribers.size === 1) {
                syncTimer = setInterval(sync, interval);
            }
            return () => {
                subscribers.delete(callback);
                if (subscribers.size === 0 && syncTimer) {
                    clearInterval(syncTimer);
                    syncTimer = null;
                }
            };
        },

        forceSync() {
            sync();
        },

        stop() {
            if (syncTimer) {
                clearInterval(syncTimer);
                syncTimer = null;
            }
            subscribers.clear();
        }
    };
}

// Store state utilities
function createStoreSubscription(store, event, handler) {
    if (!store || typeof store.on !== 'function') {
        console.warn('Invalid store for subscription');
        return () => {};
    }

    store.on(event, handler);
    return () => {
        if (store && typeof store.off === 'function') {
            store.off(event, handler);
        }
    };
}

function getStoreSnapshot(store) {
    if (!store) return null;

    try {
        return {
            timestamp: Date.now(),
            records: store.getAllRecords ? store.getAllRecords() : [],
            selection: store.getSelectedShapes ? store.getSelectedShapes() : [],
            currentTool: store.getCurrentTool ? store.getCurrentTool() : 'select',
            camera: store.getCurrentCamera ? store.getCurrentCamera() : null
        };
    } catch (error) {
        console.error('Error capturing store snapshot:', error);
        return null;
    }
}

// Global state manager instance
const globalEventBus = new EventBus();
const globalStateSync = createStateSync(500); // 500ms sync interval

// DOM UTILITIES (Chunk 1B consolidation)
function createElement(tag, attributes = {}, children = []) {
    const element = document.createElement(tag);

    // Set attributes
    Object.entries(attributes).forEach(([key, value]) => {
        if (key === 'className') {
            element.className = value;
        } else if (key === 'style' && typeof value === 'object') {
            Object.assign(element.style, value);
        } else if (key.startsWith('data-')) {
            element.setAttribute(key, value);
        } else {
            element[key] = value;
        }
    });

    // Append children
    children.forEach(child => {
        if (typeof child === 'string') {
            element.appendChild(document.createTextNode(child));
        } else if (child instanceof Node) {
            element.appendChild(child);
        }
    });

    return element;
}

function createDiv(className = '', content = '') {
    const div = document.createElement('div');
    if (className) div.className = className;
    if (content) div.textContent = content;
    return div;
}

function createContainer(id, className = 'container') {
    return createElement('div', { id, className });
}

// Event handling utilities
function addEventListenerSafe(element, event, handler, options = {}) {
    if (!element || typeof element.addEventListener !== 'function') {
        console.warn('Invalid element for event listener:', element);
        return null;
    }

    const wrappedHandler = (e) => {
        try {
            return handler(e);
        } catch (error) {
            console.error(`Error in ${event} handler:`, error);
        }
    };

    element.addEventListener(event, wrappedHandler, options);
    return wrappedHandler;
}

function removeEventListenerSafe(element, event, handler, options = {}) {
    if (!element || typeof element.removeEventListener !== 'function') {
        console.warn('Invalid element for event listener removal:', element);
        return;
    }
    element.removeEventListener(event, handler, options);
}

// DOM query utilities
function querySelector(selector, parent = document) {
    try {
        return parent.querySelector(selector);
    } catch (error) {
        console.error('Invalid selector:', selector, error);
        return null;
    }
}

function querySelectorAll(selector, parent = document) {
    try {
        return Array.from(parent.querySelectorAll(selector));
    } catch (error) {
        console.error('Invalid selector:', selector, error);
        return [];
    }
}

// Element manipulation utilities
function setElementStyle(element, styles) {
    if (!element || !element.style) return;
    Object.assign(element.style, styles);
}

function setAttributes(element, attributes) {
    if (!element) return;
    Object.entries(attributes).forEach(([key, value]) => {
        element.setAttribute(key, value);
    });
}

function appendChildren(parent, children) {
    if (!parent) return;
    children.forEach(child => {
        if (typeof child === 'string') {
            parent.appendChild(document.createTextNode(child));
        } else if (child instanceof Node) {
            parent.appendChild(child);
        }
    });
}

// Viewport and positioning utilities
function getElementBounds(element) {
    if (!element || !element.getBoundingClientRect) {
        return { x: 0, y: 0, width: 0, height: 0 };
    }
    const rect = element.getBoundingClientRect();
    return {
        x: rect.left,
        y: rect.top,
        width: rect.width,
        height: rect.height
    };
}

function isElementVisible(element) {
    if (!element) return false;
    const rect = element.getBoundingClientRect();
    return rect.width > 0 && rect.height > 0;
}

// Distance function (moved from types-definitions.js)
function distance(p1, p2) {
    const dx = p2.x - p1.x;
    const dy = p2.y - p1.y;
    return Math.sqrt(dx * dx + dy * dy);
}

// Performance utilities
function debounce(func, wait, immediate = false) {
    let timeout;
    return function executedFunction(...args) {
        const later = () => {
            timeout = null;
            if (!immediate) func.apply(this, args);
        };
        const callNow = immediate && !timeout;
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
        if (callNow) func.apply(this, args);
    };
}

function throttle(func, limit) {
    let inThrottle;
    return function(...args) {
        if (!inThrottle) {
            func.apply(this, args);
            inThrottle = true;
            setTimeout(() => inThrottle = false, limit);
        }
    };
}

// Object utilities
function deepClone(obj) {
    if (obj === null || typeof obj !== 'object') return obj;
    if (obj instanceof Date) return new Date(obj.getTime());
    if (obj instanceof Array) return obj.map(item => deepClone(item));
    if (typeof obj === 'object') {
        const clonedObj = {};
        for (const key in obj) {
            if (obj.hasOwnProperty(key)) {
                clonedObj[key] = deepClone(obj[key]);
            }
        }
        return clonedObj;
    }
}

// Validation utilities
function isValidCoordinate(coord) {
    return typeof coord === 'number' && !isNaN(coord) && isFinite(coord);
}

function isValidPoint(point) {
    return point && 
           typeof point === 'object' && 
           isValidCoordinate(point.x) && 
           isValidCoordinate(point.y);
}

function sanitizeInput(input, type = 'string') {
    if (input === null || input === undefined) return '';

    switch (type) {
        case 'number':
            const num = parseFloat(input);
            return isNaN(num) ? 0 : num;
        case 'integer':
            const int = parseInt(input, 10);
            return isNaN(int) ? 0 : int;
        case 'boolean':
            return Boolean(input);
        case 'string':
        default:
            return String(input).trim();
    }
}

// Array utilities
function removeArrayDuplicates(arr, keyFunc = null) {
    if (!keyFunc) return [...new Set(arr)];

    const seen = new Set();
    return arr.filter(item => {
        const key = keyFunc(item);
        if (seen.has(key)) return false;
        seen.add(key);
        return true;
    });
}

// Async utilities
function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

async function retry(fn, maxAttempts = 3, delay = 1000) {
    for (let attempt = 1; attempt <= maxAttempts; attempt++) {
        try {
            return await fn();
        } catch (error) {
            if (attempt === maxAttempts) throw error;
            await sleep(delay * attempt);
        }
    }
}

// Global exports - Enhanced utility hub
if (typeof window !== 'undefined') {
    window.generateId = generateId;
    window.clamp = clamp;
    window.lerp = lerp;
    window.distance = distance;
    window.debounce = debounce;
    window.throttle = throttle;
    window.deepClone = deepClone;
    window.isValidCoordinate = isValidCoordinate;
    window.isValidPoint = isValidPoint;
    window.sanitizeInput = sanitizeInput;
    window.removeArrayDuplicates = removeArrayDuplicates;
    window.sleep = sleep;
    window.retry = retry;
    window.TLGeometry = TLGeometry;
    window.TLEventResult = TLEventResult;

    // DOM utilities (Chunk 1B)
    window.createElement = createElement;
    window.createDiv = createDiv;
    window.createContainer = createContainer;
    window.addEventListenerSafe = addEventListenerSafe;
    window.removeEventListenerSafe = removeEventListenerSafe;
    window.querySelector = querySelector;
    window.querySelectorAll = querySelectorAll;
    window.setElementStyle = setElementStyle;
    window.setAttributes = setAttributes;
    window.appendChildren = appendChildren;
    window.getElementBounds = getElementBounds;
    window.isElementVisible = isElementVisible;

    // State management utilities (Chunk 2A)
    window.EventBus = EventBus;
    window.createStateSync = createStateSync;
    window.createStoreSubscription = createStoreSubscription;
    window.getStoreSnapshot = getStoreSnapshot;
    window.globalEventBus = globalEventBus;
    window.globalStateSync = globalStateSync;

    // Shape geometry utilities (Chunk 3A)
    window.ShapeGeometry = ShapeGeometry;
    window.rotatePoint = rotatePoint;
    window.transformBounds = transformBounds;
    window.getBoundsCenter = getBoundsCenter;
    window.expandBounds = expandBounds;
    window.boundsContainPoint = boundsContainPoint;
    window.boundsIntersect = boundsIntersect;
    window.createShapeBounds = createShapeBounds;
    window.normalizeShapeProps = normalizeShapeProps;

    // Diagnostic & performance utilities (Chunk 4A)
    window.DiagnosticUtils = DiagnosticUtils;
    window.startPerformanceTimer = startPerformanceTimer;
    window.endPerformanceTimer = endPerformanceTimer;
    window.createErrorBoundary = createErrorBoundary;
    window.getMemoryUsage = getMemoryUsage;
    window.globalDiagnostics = globalDiagnostics;
}

