   378‚Üí    ipcMain.handle('take-screenshot', async () => {
   379‚Üí      try {
   380‚Üí        console.log('üì∏ Taking screenshot...');
   381‚Üí        const { screen } = require('electron');
   382‚Üí        const primaryDisplay = screen.getPrimaryDisplay();
   383‚Üí        const { width, height } = primaryDisplay.size;
   384‚Üí
   385‚Üí        const screenshot = await this.app.mainWindow.webContents.capturePage({
   386‚Üí          x: 0,
   387‚Üí          y: 0,
   388‚Üí          width: Math.min(width, 1920),
   389‚Üí          height: Math.min(height, 1080),
   390‚Üí        });
   391‚Üí
   392‚Üí        const buffer = screenshot.toPNG();
   393‚Üí        const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
   394‚Üí        const screenshotPath = path.join(
   395‚Üí          __dirname,
   396‚Üí          '../../screenshots',
   397‚Üí          `screenshot-${timestamp}.png`
   398‚Üí        );
   399‚Üí
   400‚Üí        // Ensure screenshots directory exists
   401‚Üí        const fs = require('fs');
   402‚Üí        const screenshotsDir = path.join(__dirname, '../../screenshots');
   403‚Üí        if (!fs.existsSync(screenshotsDir)) {
   404‚Üí          fs.mkdirSync(screenshotsDir, { recursive: true });
   405‚Üí        }
   406‚Üí
   407‚Üí        fs.writeFileSync(screenshotPath, buffer);
   408‚Üí
   409‚Üí        // Send to renderer
   410‚Üí        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
   411‚Üí          this.app.mainWindow.webContents.send('screenshot-captured', {
   412‚Üí            path: screenshotPath,
   413‚Üí            timestamp: timestamp,
   414‚Üí            size: buffer.length,
   415‚Üí          });
   416‚Üí        }
   417‚Üí
   418‚Üí        return { success: true, path: screenshotPath };
   419‚Üí      } catch (error) {
   420‚Üí        console.error('‚ùå Screenshot failed:', error);
   421‚Üí        return { success: false, error: error.message };
   422‚Üí      }
   423‚Üí    });
