     1‚Üí/**
     2‚Üí * Dependency Injection Container
     3‚Üí * JavaScript implementation for the Chrome Container App
     4‚Üí * Based on the architectural refactoring plan
     5‚Üí */
     6‚Üí
     7‚Üíconst { TYPES } = require('./interfaces');
     8‚Üí
     9‚Üíclass DIContainer {
    10‚Üí  constructor() {
    11‚Üí    this.bindings = new Map();
    12‚Üí    this.instances = new Map();
    13‚Üí    this.singletons = new Set();
    14‚Üí  }
    15‚Üí
    16‚Üí  /**
    17‚Üí   * Bind an interface to an implementation
    18‚Üí   * @param {Symbol} identifier - Service identifier symbol
    19‚Üí   * @param {Function} implementation - Constructor or factory function
    20‚Üí   * @returns {BindingBuilder}
    21‚Üí   */
    22‚Üí  bind(identifier, implementation) {
    23‚Üí    return new BindingBuilder(this, identifier, implementation);
    24‚Üí  }
    25‚Üí
    26‚Üí  /**
    27‚Üí   * Register a binding
    28‚Üí   * @param {Symbol} identifier
    29‚Üí   * @param {Object} binding
    30‚Üí   */
    31‚Üí  register(identifier, binding) {
    32‚Üí    this.bindings.set(identifier, binding);
    33‚Üí  }
    34‚Üí
    35‚Üí  /**
    36‚Üí   * Get a dependency by identifier
    37‚Üí   * @param {Symbol} identifier
    38‚Üí   * @returns {*} The resolved dependency
    39‚Üí   */
    40‚Üí  get(identifier) {
    41‚Üí    const binding = this.bindings.get(identifier);
    42‚Üí
    43‚Üí    if (!binding) {
    44‚Üí      throw new Error(`No binding found for identifier: ${identifier.toString()}`);
    45‚Üí    }
    46‚Üí
    47‚Üí    // Return singleton if exists
    48‚Üí    if (this.singletons.has(identifier) && this.instances.has(identifier)) {
    49‚Üí      return this.instances.get(identifier);
    50‚Üí    }
    51‚Üí
    52‚Üí    // Create new instance
    53‚Üí    const instance = this.createInstance(binding);
    54‚Üí
    55‚Üí    // Store singleton
    56‚Üí    if (this.singletons.has(identifier)) {
    57‚Üí      this.instances.set(identifier, instance);
    58‚Üí    }
    59‚Üí
    60‚Üí    return instance;
    61‚Üí  }
    62‚Üí
    63‚Üí  /**
    64‚Üí   * Create instance from binding
    65‚Üí   * @param {Object} binding
    66‚Üí   * @returns {*}
    67‚Üí   */
    68‚Üí  createInstance(binding) {
    69‚Üí    const { implementation, dependencies = [] } = binding;
    70‚Üí
    71‚Üí    // Resolve dependencies
    72‚Üí    const resolvedDeps = dependencies.map(dep => this.get(dep));
    73‚Üí
    74‚Üí    // Create instance
    75‚Üí    if (typeof implementation === 'function') {
    76‚Üí      return new implementation(...resolvedDeps);
    77‚Üí    }
    78‚Üí
    79‚Üí    if (typeof implementation === 'object' && implementation.create) {
    80‚Üí      return implementation.create(...resolvedDeps);
    81‚Üí    }
    82‚Üí
    83‚Üí    return implementation;
    84‚Üí  }
    85‚Üí
    86‚Üí  /**
