/**
 * Dependency Injection Container
 * JavaScript implementation for the Chrome Container App
 * Based on the architectural refactoring plan
 */

const { TYPES } = require('./interfaces');

class DIContainer {
  constructor() {
    this.bindings = new Map();
    this.instances = new Map();
    this.singletons = new Set();
  }

  /**
   * Bind an interface to an implementation
   * @param {Symbol} identifier - Service identifier symbol
   * @param {Function} implementation - Constructor or factory function
   * @returns {BindingBuilder}
   */
  bind(identifier, implementation) {
    return new BindingBuilder(this, identifier, implementation);
  }

  /**
   * Register a binding
   * @param {Symbol} identifier
   * @param {Object} binding
   */
  register(identifier, binding) {
    this.bindings.set(identifier, binding);
  }

  /**
   * Get a dependency by identifier
   * @param {Symbol} identifier
   * @returns {*} The resolved dependency
   */
  get(identifier) {
    const binding = this.bindings.get(identifier);

    if (!binding) {
      throw new Error(`No binding found for identifier: ${identifier.toString()}`);
    }

    // Return singleton if exists
    if (this.singletons.has(identifier) && this.instances.has(identifier)) {
      return this.instances.get(identifier);
    }

    // Create new instance
    const instance = this.createInstance(binding);

    // Store singleton
    if (this.singletons.has(identifier)) {
      this.instances.set(identifier, instance);
    }

    return instance;
  }

  /**
   * Create instance from binding
   * @param {Object} binding
   * @returns {*}
   */
  createInstance(binding) {
    const { implementation, dependencies = [] } = binding;

    // Resolve dependencies
    const resolvedDeps = dependencies.map(dep => this.get(dep));

    // Create instance
    if (typeof implementation === 'function') {
      return new implementation(...resolvedDeps);
    }

    if (typeof implementation === 'object' && implementation.create) {
      return implementation.create(...resolvedDeps);
    }

    return implementation;
  }

  /**
   * Mark identifier as singleton
   * @param {Symbol} identifier
   */
  markAsSingleton(identifier) {
    this.singletons.add(identifier);
  }

  /**
   * Clear all bindings and instances
   */
  clear() {
    this.bindings.clear();
    this.instances.clear();
    this.singletons.clear();
  }
}

class BindingBuilder {
  constructor(container, identifier, implementation) {
    this.container = container;
    this.identifier = identifier;
    this.binding = {
      implementation,
      dependencies: [],
    };
  }

  /**
   * Specify dependencies for this binding
   * @param {...Symbol} dependencies
   * @returns {BindingBuilder}
   */
  withDependencies(...dependencies) {
    this.binding.dependencies = dependencies;
    return this;
  }

  /**
   * Mark this binding as singleton
   * @returns {BindingBuilder}
   */
  inSingletonScope() {
    this.container.markAsSingleton(this.identifier);
    this.container.register(this.identifier, this.binding);
    return this;
  }

  /**
   * Complete the binding registration
   * @returns {BindingBuilder}
   */
  to(implementation) {
    this.binding.implementation = implementation;
    this.container.register(this.identifier, this.binding);
    return this;
  }
}

// Create global container instance
const container = new DIContainer();

/**
 * Get dependency from container
 * @param {Symbol} identifier
 * @returns {*}
 */
function getDependency(identifier) {
  return container.get(identifier);
}

/**
 * Configure the container with all bindings
 */
function configureContainer() {
  // Import implementations
  const { Logger } = require('../logging/logger');
  const { EventBus } = require('../events/event-bus');
  const { Mediator } = require('../mediator/mediator');
  const { ConfigService } = require('../config/config-service');
  const { CacheService } = require('../cache/cache-service');
  const { FileService } = require('../file/file-service');
  const { BatchProcessor } = require('../ipc/batch-processor');

  // Core Services - bind constructors, not instances
  container.bind(TYPES.ILogger, Logger).inSingletonScope();
  container.bind(TYPES.IEventBus, EventBus).inSingletonScope();
  container.bind(TYPES.IMediator, Mediator).inSingletonScope();
  container.bind(TYPES.IConfigService, ConfigService).inSingletonScope();

  // Utility Services
  container.bind(TYPES.ICacheService, CacheService).inSingletonScope();
  container.bind(TYPES.IFileService, FileService).inSingletonScope();
  container.bind(TYPES.IBatchProcessor, BatchProcessor).inSingletonScope();

  console.log('‚úÖ Dependency injection container configured');
}

// Export symbols for backward compatibility
const interfaces = {
  TYPES,

  // Service interfaces (for type checking in JS)
  ILogger: 'ILogger',
  IEventBus: 'IEventBus',
  IMediator: 'IMediator',
  IConfigService: 'IConfigService',
  ICacheService: 'ICacheService',
  IFileService: 'IFileService',
  IBatchProcessor: 'IBatchProcessor',
};

module.exports = {
  container,
  getDependency,
  configureContainer,
  interfaces,
  TYPES,
  DIContainer,
  BindingBuilder,
};

