/**
 * Event Bus Implementation
 * Decoupled communication system for Chrome Container App
 * Implements publish/subscribe pattern to reduce tight coupling
 */

const { EventEmitter } = require('events');

class EventBus extends EventEmitter {
  constructor() {
    super();
    this.handlers = new Map();
    this.metrics = {
      totalEvents: 0,
      eventsByType: new Map(),
      errors: [],
    };

    // Increase max listeners for high-throughput scenarios
    this.setMaxListeners(100);

    console.log('üöå EventBus initialized');
  }

  /**
   * Subscribe to an event type
   * @param {string} eventType - The event type to listen for
   * @param {Function} handler - The event handler function
   * @returns {Function} Unsubscribe function
   */
  subscribe(eventType, handler) {
    if (!this.handlers.has(eventType)) {
      this.handlers.set(eventType, []);
    }

    this.handlers.get(eventType).push(handler);

    // Also use EventEmitter's built-in mechanism for additional reliability
    this.on(eventType, handler);

    console.log(`üì° Subscribed to event: ${eventType}`);

    // Return unsubscribe function
    return () => this.unsubscribe(eventType, handler);
  }

  /**
   * Publish an event to all subscribers
   * @param {string} eventType - The event type to publish
   * @param {*} data - The event data
   * @returns {Promise<void>}
   */
  async publish(eventType, data) {
    this.metrics.totalEvents++;
    this.metrics.eventsByType.set(eventType, (this.metrics.eventsByType.get(eventType) || 0) + 1);

    console.log(`üì¢ Publishing event: ${eventType}`, { data: typeof data });

    try {
      // Get all handlers for this event type
      const eventHandlers = this.handlers.get(eventType) || [];

      if (eventHandlers.length === 0) {
        console.warn(`‚ö†Ô∏è No handlers for event: ${eventType}`);
        return;
      }

      // Execute all handlers
      const promises = eventHandlers.map(async handler => {
        try {
          const result = handler(data);

          // Handle both sync and async handlers
          if (result && typeof result.then === 'function') {
            await result;
          }
        } catch (error) {
          console.error(`‚ùå Error in event handler for ${eventType}:`, error);
          this.metrics.errors.push({
            eventType,
            error: error.message,
            timestamp: Date.now(),
          });

          // Don't throw - we want other handlers to continue executing
        }
      });

      // Wait for all handlers to complete
      await Promise.all(promises);

      // Also emit via EventEmitter for additional subscribers
      this.emit(eventType, data);
    } catch (error) {
      console.error(`‚ùå Error publishing event ${eventType}:`, error);
      throw error;
    }
  }

  /**
   * Unsubscribe from an event
   * @param {string} eventType - The event type
   * @param {Function} handler - The handler to remove
   */
  unsubscribe(eventType, handler) {
    const eventHandlers = this.handlers.get(eventType);
    if (!eventHandlers) {
      return;
    }

    const index = eventHandlers.indexOf(handler);
    if (index > -1) {
      eventHandlers.splice(index, 1);
    }

    // Remove from EventEmitter as well
    this.removeListener(eventType, handler);

    console.log(`üì° Unsubscribed from event: ${eventType}`);
  }

  /**
   * Get all subscribers for an event type
   * @param {string} eventType
   * @returns {Function[]}
   */
  getSubscribers(eventType) {
    return this.handlers.get(eventType) || [];
  }

  /**
   * Get event metrics
   * @returns {Object}
   */
  getMetrics() {
    return {
      ...this.metrics,
      activeEventTypes: Array.from(this.handlers.keys()),
      totalSubscribers: Array.from(this.handlers.values()).reduce(
        (total, handlers) => total + handlers.length,
        0
      ),
    };
  }

  /**
   * Clear all event handlers
   */
  clear() {
    this.handlers.clear();
    this.removeAllListeners();
    console.log('üßπ EventBus cleared');
  }

  /**
   * Subscribe to multiple events at once
   * @param {Object} eventMap - Map of eventType -> handler
   * @returns {Function} Unsubscribe function for all events
   */
  subscribeMultiple(eventMap) {
    const unsubscribers = [];

    Object.entries(eventMap).forEach(([eventType, handler]) => {
      unsubscribers.push(this.subscribe(eventType, handler));
    });

    return () => {
      unsubscribers.forEach(unsubscribe => unsubscribe());
    };
  }

  /**
   * Subscribe to an event only once
   * @param {string} eventType
   * @param {Function} handler
   * @returns {Function} Unsubscribe function
   */
  subscribeOnce(eventType, handler) {
    const onceHandler = data => {
      handler(data);
      this.unsubscribe(eventType, onceHandler);
    };

    return this.subscribe(eventType, onceHandler);
  }

  /**
   * Create a filtered subscription
   * @param {string} eventType
   * @param {Function} filter - Function to test event data
   * @param {Function} handler
   * @returns {Function} Unsubscribe function
   */
  subscribeFiltered(eventType, filter, handler) {
    const filteredHandler = data => {
      if (filter(data)) {
        handler(data);
      }
    };

    return this.subscribe(eventType, filteredHandler);
  }
}

// Event type constants to prevent typos
const EventTypes = {
  // System Events
  SYSTEM_READY: 'system:ready',
  SYSTEM_SHUTDOWN: 'system:shutdown',
  SYSTEM_ERROR: 'system:error',

  // Chrome Events
  CHROME_INITIALIZED: 'chrome:initialized',
  CHROME_DISCONNECTED: 'chrome:disconnected',
  CHROME_ERROR: 'chrome:error',

  // Terminal Events
  TERMINAL_CREATED: 'terminal:created',
  TERMINAL_DATA: 'terminal:data',
  TERMINAL_CLOSED: 'terminal:closed',

  // IPC Events
  IPC_REQUEST: 'ipc:request',
  IPC_RESPONSE: 'ipc:response',
  IPC_ERROR: 'ipc:error',

  // File Events
  FILE_SAVED: 'file:saved',
  FILE_DELETED: 'file:deleted',
  FILE_ERROR: 'file:error',

  // Strike Errors Events
  STRIKE_ANALYSIS_START: 'strike:analysis:start',
  STRIKE_ANALYSIS_COMPLETE: 'strike:analysis:complete',
  STRIKE_ANALYSIS_ERROR: 'strike:analysis:error',

  // Health Events
  HEALTH_CHECK: 'health:check',
  COMPONENT_UNHEALTHY: 'health:component:unhealthy',
  PERFORMANCE_DEGRADED: 'health:performance:degraded',
};

module.exports = {
  EventBus,
  EventTypes,
};

