    29‚Üí    }
    30‚Üí
    31‚Üí    if (typeof handler !== 'function') {
    32‚Üí      throw new Error(`Handler must be a function for type: ${requestType}`);
    33‚Üí    }
    34‚Üí
    35‚Üí    this.handlers.set(requestType, handler);
    36‚Üí    console.log(`üìù Registered handler for: ${requestType}`);
    37‚Üí  }
    38‚Üí
    39‚Üí  /**
    40‚Üí   * Send a request through the mediator
    41‚Üí   * @param {string} requestType - The type of request
    42‚Üí   * @param {*} request - The request data
    43‚Üí   * @returns {Promise<*>} The response
    44‚Üí   */
    45‚Üí  async send(requestType, request) {
    46‚Üí    const startTime = Date.now();
    47‚Üí
    48‚Üí    // Update metrics
    49‚Üí    this.metrics.totalRequests++;
    50‚Üí    this.metrics.requestsByType.set(
    51‚Üí      requestType,
    52‚Üí      (this.metrics.requestsByType.get(requestType) || 0) + 1
    53‚Üí    );
    54‚Üí
    55‚Üí    try {
    56‚Üí      const handler = this.handlers.get(requestType);
    57‚Üí
    58‚Üí      if (!handler) {
    59‚Üí        throw new Error(`No handler registered for type: ${requestType}`);
    60‚Üí      }
    61‚Üí
    62‚Üí      console.log(`‚ö° Processing request: ${requestType}`);
    63‚Üí
    64‚Üí      // Apply middleware pipeline
    65‚Üí      const context = {
    66‚Üí        requestType,
