    68‚Üí        timestamp: startTime,
    69‚Üí        metadata: {},
    70‚Üí      };
    71‚Üí
    72‚Üí      // Pre-processing middleware
    73‚Üí      await this.applyMiddleware('before', context);
    74‚Üí
    75‚Üí      // Execute handler
    76‚Üí      const result = await handler(context.request);
    77‚Üí
    78‚Üí      // Post-processing middleware
    79‚Üí      context.response = result;
    80‚Üí      await this.applyMiddleware('after', context);
    81‚Üí
    82‚Üí      // Update response time metrics
    83‚Üí      const responseTime = Date.now() - startTime;
    84‚Üí      this.updateAverageResponseTime(responseTime);
    85‚Üí
    86‚Üí      console.log(`‚úÖ Completed request: ${requestType} (${responseTime}ms)`);
    87‚Üí
    88‚Üí      return context.response;
    89‚Üí    } catch (error) {
    90‚Üí      const responseTime = Date.now() - startTime;
    91‚Üí      this.updateAverageResponseTime(responseTime);
    92‚Üí
    93‚Üí      // Log error
    94‚Üí      this.metrics.errors.push({
    95‚Üí        requestType,
    96‚Üí        error: error.message,
    97‚Üí        timestamp: startTime,
    98‚Üí        responseTime,
    99‚Üí      });
   100‚Üí
