   104‚Üí  }
   105‚Üí
   106‚Üí  /**
   107‚Üí   * Add middleware to the processing pipeline
   108‚Üí   * @param {string} phase - 'before' or 'after'
   109‚Üí   * @param {Function} middleware - Middleware function
   110‚Üí   */
   111‚Üí  addMiddleware(phase, middleware) {
   112‚Üí    if (!['before', 'after'].includes(phase)) {
   113‚Üí      throw new Error("Phase must be 'before' or 'after'");
   114‚Üí    }
   115‚Üí
   116‚Üí    this.middleware.push({ phase, middleware });
   117‚Üí    console.log(`üîß Added ${phase} middleware`);
   118‚Üí  }
   119‚Üí
   120‚Üí  /**
