/**
 * Mediator Pattern Implementation
 * Centralizes complex communication and reduces coupling
 * Full implementation for Chrome Container App architecture
 */

class Mediator {
  constructor() {
    this.handlers = new Map();
    this.middleware = [];
    this.metrics = {
      totalRequests: 0,
      requestsByType: new Map(),
      averageResponseTime: 0,
      errors: [],
    };

    console.log('üéØ Mediator initialized');
  }

  /**
   * Register a request handler
   * @param {string} requestType - The type of request
   * @param {Function} handler - The handler function
   */
  register(requestType, handler) {
    if (this.handlers.has(requestType)) {
      throw new Error(`Handler already registered for type: ${requestType}`);
    }

    if (typeof handler !== 'function') {
      throw new Error(`Handler must be a function for type: ${requestType}`);
    }

    this.handlers.set(requestType, handler);
    console.log(`üìù Registered handler for: ${requestType}`);
  }

  /**
   * Send a request through the mediator
   * @param {string} requestType - The type of request
   * @param {*} request - The request data
   * @returns {Promise<*>} The response
   */
  async send(requestType, request) {
    const startTime = Date.now();

    // Update metrics
    this.metrics.totalRequests++;
    this.metrics.requestsByType.set(
      requestType,
      (this.metrics.requestsByType.get(requestType) || 0) + 1
    );

    try {
      const handler = this.handlers.get(requestType);

      if (!handler) {
        throw new Error(`No handler registered for type: ${requestType}`);
      }

      console.log(`‚ö° Processing request: ${requestType}`);

      // Apply middleware pipeline
      const context = {
        requestType,
        request,
        timestamp: startTime,
        metadata: {},
      };

      // Pre-processing middleware
      await this.applyMiddleware('before', context);

      // Execute handler
      const result = await handler(context.request);

      // Post-processing middleware
      context.response = result;
      await this.applyMiddleware('after', context);

      // Update response time metrics
      const responseTime = Date.now() - startTime;
      this.updateAverageResponseTime(responseTime);

      console.log(`‚úÖ Completed request: ${requestType} (${responseTime}ms)`);

      return context.response;
    } catch (error) {
      const responseTime = Date.now() - startTime;
      this.updateAverageResponseTime(responseTime);

      // Log error
      this.metrics.errors.push({
        requestType,
        error: error.message,
        timestamp: startTime,
        responseTime,
      });

      console.error(`‚ùå Error processing request: ${requestType}`, error);
      throw error;
    }
  }

  /**
   * Add middleware to the processing pipeline
   * @param {string} phase - 'before' or 'after'
   * @param {Function} middleware - Middleware function
   */
  addMiddleware(phase, middleware) {
    if (!['before', 'after'].includes(phase)) {
      throw new Error("Phase must be 'before' or 'after'");
    }

    this.middleware.push({ phase, middleware });
    console.log(`üîß Added ${phase} middleware`);
  }

  /**
   * Apply middleware for a specific phase
   * @param {string} phase
   * @param {Object} context
   */
  async applyMiddleware(phase, context) {
    const middlewares = this.middleware.filter(m => m.phase === phase);

    for (const { middleware } of middlewares) {
      await middleware(context);
    }
  }

  /**
   * Update average response time metric
   * @param {number} responseTime
   */
  updateAverageResponseTime(responseTime) {
    const total = this.metrics.totalRequests;
    const current = this.metrics.averageResponseTime;

    this.metrics.averageResponseTime = (current * (total - 1) + responseTime) / total;
  }

  /**
   * Get registered handlers
   * @returns {string[]}
   */
  getRegisteredTypes() {
    return Array.from(this.handlers.keys());
  }

  /**
   * Get mediator metrics
   * @returns {Object}
   */
  getMetrics() {
    return {
      ...this.metrics,
      registeredHandlers: this.handlers.size,
      middlewareCount: this.middleware.length,
    };
  }

  /**
   * Unregister a handler
   * @param {string} requestType
   */
  unregister(requestType) {
    const removed = this.handlers.delete(requestType);
    if (removed) {
      console.log(`üóëÔ∏è Unregistered handler for: ${requestType}`);
    }
    return removed;
  }

  /**
   * Clear all handlers and middleware
   */
  clear() {
    this.handlers.clear();
    this.middleware = [];
    console.log('üßπ Mediator cleared');
  }
}

/**
 * Request/Response base classes for type safety
 */
class BaseRequest {
  constructor(data = {}) {
    this.id = `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    this.timestamp = Date.now();
    this.data = data;
  }
}

class BaseResponse {
  constructor(data = {}, success = true) {
    this.success = success;
    this.data = data;
    this.timestamp = Date.now();
  }
}

/**
 * Chrome-related requests
 */
class ChromeInitializeRequest extends BaseRequest {
  constructor(options = {}) {
    super({ options });
  }
}

class ChromeNavigateRequest extends BaseRequest {
  constructor(url) {
    super({ url });
  }
}

class ChromeStatusRequest extends BaseRequest {
  constructor() {
    super();
  }
}

/**
 * Terminal-related requests
 */
class TerminalCreateRequest extends BaseRequest {
  constructor(options = {}) {
    super({ options });
  }
}

class TerminalWriteRequest extends BaseRequest {
  constructor(terminalId, data) {
    super({ terminalId, data });
  }
}

class TerminalResizeRequest extends BaseRequest {
  constructor(terminalId, cols, rows) {
    super({ terminalId, cols, rows });
  }
}

/**
 * File-related requests
 */
class FileSaveRequest extends BaseRequest {
  constructor(filePath, data, encoding = 'utf8') {
    super({ filePath, data, encoding });
  }
}

class FileReadRequest extends BaseRequest {
  constructor(filePath) {
    super({ filePath });
  }
}

class FileDeleteRequest extends BaseRequest {
  constructor(filePath) {
    super({ filePath });
  }
}

/**
 * Strike Errors-related requests
 */
class StrikeAnalyzeRequest extends BaseRequest {
  constructor(directoryPath, options = {}) {
    super({ directoryPath, options });
  }
}

class StrikeResolvePathRequest extends BaseRequest {
  constructor(targetPath, context = {}) {
    super({ targetPath, context });
  }
}

/**
 * System-related requests
 */
class SystemHealthRequest extends BaseRequest {
  constructor() {
    super();
  }
}

class SystemMetricsRequest extends BaseRequest {
  constructor() {
    super();
  }
}

/**
 * Middleware factory functions
 */
const MiddlewareFactory = {
  /**
   * Logging middleware
   */
  createLoggingMiddleware() {
    return {
      before: async context => {
        console.log(`üì• [${context.requestType}] Request received:`, {
          id: context.request.id || 'unknown',
          timestamp: context.timestamp,
        });
      },
      after: async context => {
        const duration = Date.now() - context.timestamp;
        console.log(`üì§ [${context.requestType}] Response sent:`, {
          id: context.request.id || 'unknown',
          duration: `${duration}ms`,
          success: context.response?.success !== false,
        });
      },
    };
  },

  /**
   * Validation middleware
   */
  createValidationMiddleware() {
    return {
      before: async context => {
        if (!context.request) {
          throw new Error('Request is required');
        }
      },
      after: async context => {
        // Validate response structure if needed
      },
    };
  },

  /**
   * Caching middleware
   */
  createCachingMiddleware(cache, ttl = 60000) {
    return {
      before: async context => {
        const cacheKey = `${context.requestType}:${JSON.stringify(context.request)}`;
        const cached = cache.get(cacheKey);

        if (cached) {
          context.cachedResponse = cached;
          console.log(`üíæ Cache hit for: ${context.requestType}`);
        }
      },
      after: async context => {
        if (!context.cachedResponse && context.response) {
          const cacheKey = `${context.requestType}:${JSON.stringify(context.request)}`;
          cache.set(cacheKey, context.response, ttl);
          console.log(`üíæ Cached response for: ${context.requestType}`);
        }
      },
    };
  },

  /**
   * Error handling middleware
   */
  createErrorHandlingMiddleware() {
    return {
      before: async context => {
        context.metadata.startTime = Date.now();
      },
      after: async context => {
        // This runs even if handler fails (in try/catch in mediator)
        const duration = Date.now() - context.metadata.startTime;
        if (duration > 5000) {
          // 5 second threshold
          console.warn(`‚ö†Ô∏è Slow request detected: ${context.requestType} took ${duration}ms`);
        }
      },
    };
  },
};

/**
 * Request type constants to prevent typos
 */
const RequestTypes = {
  // Chrome operations
  CHROME_INITIALIZE: 'chrome:initialize',
  CHROME_NAVIGATE: 'chrome:navigate',
  CHROME_STATUS: 'chrome:status',
  CHROME_RESTART: 'chrome:restart',
  CHROME_SHUTDOWN: 'chrome:shutdown',

  // Terminal operations
  TERMINAL_CREATE: 'terminal:create',
  TERMINAL_WRITE: 'terminal:write',
  TERMINAL_RESIZE: 'terminal:resize',
  TERMINAL_CLOSE: 'terminal:close',
  TERMINAL_LIST: 'terminal:list',

  // File operations
  FILE_SAVE: 'file:save',
  FILE_READ: 'file:read',
  FILE_DELETE: 'file:delete',
  FILE_EXISTS: 'file:exists',
  DIRECTORY_ENSURE: 'directory:ensure',

  // Strike Errors operations
  STRIKE_ANALYZE: 'strike:analyze',
  STRIKE_RESOLVE_PATH: 'strike:resolve-path',
  STRIKE_APPLY_FIXES: 'strike:apply-fixes',

  // System operations
  SYSTEM_HEALTH: 'system:health',
  SYSTEM_METRICS: 'system:metrics',
  SYSTEM_SHUTDOWN: 'system:shutdown',
};

module.exports = {
  Mediator,
  BaseRequest,
  BaseResponse,

  // Request classes
  ChromeInitializeRequest,
  ChromeNavigateRequest,
  ChromeStatusRequest,
  TerminalCreateRequest,
  TerminalWriteRequest,
  TerminalResizeRequest,
  FileSaveRequest,
  FileReadRequest,
  FileDeleteRequest,
  StrikeAnalyzeRequest,
  StrikeResolvePathRequest,
  SystemHealthRequest,
  SystemMetricsRequest,

  // Constants and utilities
  RequestTypes,
  MiddlewareFactory,
};

