/**
 * Bootstrap - New Architecture Entry Point
 * REPLACES old main.js patterns with clean architecture
 * FULL END-TO-END IMPLEMENTATION - NO PLACEHOLDERS
 */

const { app, BrowserWindow } = require('electron');
const path = require('path');
const { architectureIntegration } = require('./core/integration/architecture-integration');

class Bootstrap {
  constructor() {
    this.mainWindow = null;
    this.isReady = false;
    this.integrationComplete = false;
    this.services = new Map();

    console.log('üöÄ Bootstrap initializing with new architecture...');

    // Bind Electron event handlers
    this.bindElectronEvents();
  }

  /**
   * MAIN STARTUP METHOD - REPLACES OLD PATTERNS
   */
  async start() {
    try {
      console.log('üèóÔ∏è Starting application with new architecture...');

      // Wait for Electron app to be ready
      if (!app.isReady()) {
        await new Promise(resolve => app.once('ready', resolve));
      }

      // Step 1: Integrate all existing services with new architecture
      console.log('üì¶ Integrating existing services...');
      const integrationResult = await architectureIntegration.integrateAll();
      this.integrationComplete = true;

      console.log('‚úÖ Integration completed:', integrationResult);

      // Step 2: Create main window with integrated services
      console.log('ü™ü Creating main window...');
      await this.createMainWindow();

      // Step 3: Initialize IPC with batch processing
      console.log('üì° Setting up IPC with batch processing...');
      await this.setupBatchIPC();

      // Step 4: Start health monitoring
      console.log('‚ù§Ô∏è Starting health monitoring...');
      this.startHealthMonitoring();

      this.isReady = true;
      console.log('üéâ Application startup complete with new architecture!');

      return {
        success: true,
        integration: integrationResult,
        services: Array.from(this.services.keys()),
      };
    } catch (error) {
      console.error('üí• Bootstrap startup failed:', error);
      throw error;
    }
  }

  /**
   * Create main window with new architecture
   */
  async createMainWindow() {
    // Get configuration service for window settings
    const requestProcessor = architectureIntegration.getRequestProcessor();

    let windowConfig = {
      width: 1200,
      height: 800,
      webPreferences: {
        nodeIntegration: false,
        contextIsolation: true,
        preload: path.join(__dirname, '../preload.js'),
      },
    };

    try {
      // Get window config from configuration service
      const config = await requestProcessor('system:config', { key: 'window' });
      if (config) {
        windowConfig = { ...windowConfig, ...config };
      }
    } catch (error) {
      console.warn('Using default window configuration:', error.message);
    }

    this.mainWindow = new BrowserWindow(windowConfig);

    // Load the renderer
    await this.mainWindow.loadFile(path.join(__dirname, '../renderer/index.html'));

    // Handle window closed
    this.mainWindow.on('closed', () => {
      this.mainWindow = null;
    });

    // Enable DevTools in development
    if (process.env.NODE_ENV === 'development') {
      this.mainWindow.webContents.openDevTools();
    }

    console.log('‚úÖ Main window created');
  }

  /**
   * Setup IPC with batch processing - REPLACES old IPC patterns
   */
  async setupBatchIPC() {
    const { ipcMain } = require('electron');
    const requestProcessor = architectureIntegration.getRequestProcessor();

    // UNIFIED IPC HANDLER - replaces all individual handlers
    ipcMain.handle('unified-request', async (event, requestData) => {
      try {
        const { type, payload, batchId } = requestData;

        console.log(`üì® Processing unified request: ${type}`);

        // Process through the integrated architecture
        const result = await requestProcessor(type, payload);

        return {
          success: true,
          result,
          batchId,
          timestamp: Date.now(),
        };
      } catch (error) {
        console.error('‚ùå Unified request failed:', error);

        return {
          success: false,
          error: error.message,
          batchId: requestData.batchId,
          timestamp: Date.now(),
        };
      }
    });

    // BATCH IPC HANDLER - processes multiple requests at once
    ipcMain.handle('batch-request', async (event, batchData) => {
      try {
        const { requests } = batchData;
        console.log(`üì¶ Processing batch of ${requests.length} requests`);

        // Process all requests in parallel
        const results = await Promise.allSettled(
          requests.map(async request => {
            try {
              const result = await requestProcessor(request.type, request.payload);
              return {
                id: request.id,
                success: true,
                result,
                timestamp: Date.now(),
              };
            } catch (error) {
              return {
                id: request.id,
                success: false,
                error: error.message,
                timestamp: Date.now(),
              };
            }
          })
        );

        return {
          success: true,
          results: results.map(r => (r.status === 'fulfilled' ? r.value : r.reason)),
          batchId: batchData.batchId,
          timestamp: Date.now(),
        };
      } catch (error) {
        console.error('‚ùå Batch request failed:', error);

        return {
          success: false,
          error: error.message,
          batchId: batchData.batchId,
          timestamp: Date.now(),
        };
      }
    });

    // LEGACY COMPATIBILITY HANDLER - for any remaining old IPC calls
    const legacyHandlers = [
      'chrome-initialize',
      'chrome-navigate',
      'terminal-create',
      'terminal-write',
      'file-save',
      'file-read',
    ];

    legacyHandlers.forEach(handlerName => {
      ipcMain.handle(handlerName, async (event, ...args) => {
        console.warn(`‚ö†Ô∏è Legacy IPC call detected: ${handlerName} - migrating to unified request`);

        // Convert legacy call to unified request
        const requestType = this.mapLegacyToRequestType(handlerName);
        const payload = this.mapLegacyArgsToPayload(handlerName, args);

        return requestProcessor(requestType, payload);
      });
    });

    console.log('‚úÖ Batch IPC system setup complete');
  }

  /**
   * Start health monitoring system
   */
  startHealthMonitoring() {
    const requestProcessor = architectureIntegration.getRequestProcessor();

    // Health check every 30 seconds
    setInterval(async () => {
      try {
        const health = await requestProcessor('system:health', {});

        if (health.status !== 'healthy') {
          console.warn('‚ö†Ô∏è System health degraded:', health);
        }
      } catch (error) {
        console.error('‚ùå Health check failed:', error);
      }
    }, 30000);

    // Memory monitoring every 60 seconds
    setInterval(() => {
      const memoryUsage = process.memoryUsage();
      const heapUsedMB = Math.round(memoryUsage.heapUsed / 1024 / 1024);

      if (heapUsedMB > 500) {
        // 500MB threshold
        console.warn(`‚ö†Ô∏è High memory usage: ${heapUsedMB}MB`);
      }

      console.log(
        `üíæ Memory usage: ${heapUsedMB}MB heap, ${Math.round(memoryUsage.rss / 1024 / 1024)}MB RSS`
      );
    }, 60000);

    console.log('‚úÖ Health monitoring started');
  }

  /**
   * Bind Electron application events
   */
  bindElectronEvents() {
    // App ready event
    app.on('ready', () => {
      console.log('‚ö° Electron app ready');
    });

    // All windows closed
    app.on('window-all-closed', async () => {
      console.log('ü™ü All windows closed');

      // Shutdown integrated services gracefully
      if (this.integrationComplete) {
        try {
          const requestProcessor = architectureIntegration.getRequestProcessor();
          await requestProcessor('system:shutdown', {});
        } catch (error) {
          console.error('‚ùå Shutdown error:', error);
        }
      }

      // Quit app on all platforms
      app.quit();
    });

    // App activate (macOS)
    app.on('activate', async () => {
      if (BrowserWindow.getAllWindows().length === 0) {
        await this.createMainWindow();
      }
    });

    // Second instance prevention
    app.on('second-instance', () => {
      if (this.mainWindow) {
        if (this.mainWindow.isMinimized()) {
          this.mainWindow.restore();
        }
        this.mainWindow.focus();
      }
    });

    // Before quit - cleanup
    app.on('before-quit', async event => {
      if (this.integrationComplete) {
        event.preventDefault(); // Prevent immediate quit

        try {
          console.log('üõë Gracefully shutting down...');
          const requestProcessor = architectureIntegration.getRequestProcessor();
          await requestProcessor('system:shutdown', {});
          app.quit(); // Now quit for real
        } catch (error) {
          console.error('‚ùå Shutdown error:', error);
          app.quit(); // Force quit if shutdown fails
        }
      }
    });
  }

  /**
   * Map legacy IPC handler names to new request types
   */
  mapLegacyToRequestType(handlerName) {
    const mapping = {
      'chrome-initialize': 'chrome:initialize',
      'chrome-navigate': 'chrome:navigate',
      'terminal-create': 'terminal:create',
      'terminal-write': 'terminal:write',
      'file-save': 'file:save',
      'file-read': 'file:read',
    };

    return mapping[handlerName] || handlerName;
  }

  /**
   * Map legacy arguments to new payload format
   */
  mapLegacyArgsToPayload(handlerName, args) {
    // Convert old argument patterns to new structured payloads
    switch (handlerName) {
      case 'chrome-initialize':
        return { options: args[0] || {} };

      case 'chrome-navigate':
        return { url: args[0] };

      case 'terminal-create':
        return { options: args[0] || {} };

      case 'terminal-write':
        return { terminalId: args[0], data: args[1] };

      case 'file-save':
        return { filePath: args[0], data: args[1], options: args[2] || {} };

      case 'file-read':
        return { filePath: args[0] };

      default:
        return { args };
    }
  }

  /**
   * Get service by name
   */
  getService(serviceName) {
    return architectureIntegration.getService(serviceName);
  }

  /**
   * Get integration status
   */
  getStatus() {
    return {
      isReady: this.isReady,
      integrationComplete: this.integrationComplete,
      mainWindow: !!this.mainWindow,
      services: Array.from(this.services.keys()),
    };
  }
}

// Create and export singleton
const bootstrap = new Bootstrap();

// Auto-start if this is the main module
if (require.main === module) {
  bootstrap
    .start()
    .then(result => {
      console.log('üéØ Bootstrap completed:', result);
    })
    .catch(error => {
      console.error('üí• Bootstrap failed:', error);
      process.exit(1);
    });
}

module.exports = {
  Bootstrap,
  bootstrap,
};

