   172‚Üí      confidence: 0,
   173‚Üí    };
   174‚Üí
   175‚Üí    // Analyze file types and patterns
   176‚Üí    const fileAnalysis = this.analyzeFileTypes(files);
   177‚Üí    analysis.fileTypes = fileAnalysis.types;
   178‚Üí    analysis.confidence = fileAnalysis.confidence;
   179‚Üí
   180‚Üí    // Generate intelligent path mappings
   181‚Üí    analysis.pathMappings = await this.generatePathMappings(files, analysis.framework);
   182‚Üí
   183‚Üí    // Create recommendations
   184‚Üí    analysis.recommendations = this.generateRecommendations(analysis);
   185‚Üí
   186‚Üí    // Store for future reference
   187‚Üí    this.uploadedDirectories.set(directoryPath, analysis);
   188‚Üí
   189‚Üí    this.logToTerminal(
   190‚Üí      i18n.t('strikeErrors.aiPathIntelligence.analysisComplete', {
   191‚Üí        frameworkName: analysis.framework.name,
   192‚Üí        confidence: analysis.confidence,
   193‚Üí      })
   194‚Üí    );
   195‚Üí
   196‚Üí    return analysis;
   197‚Üí  }
   198‚Üí
   199‚Üí  async detectFramework(files) {
   200‚Üí    const indicators = {
   201‚Üí      react: 0,
   202‚Üí      node: 0,
   203‚Üí      python: 0,
   204‚Üí      web: 0,
   205‚Üí    };
   206‚Üí
   207‚Üí    files.forEach(file => {
   208‚Üí      const filename = path.basename(file);
   209‚Üí      const ext = path.extname(file);
   210‚Üí      const dir = path.dirname(file);
   211‚Üí
   212‚Üí      // React indicators
   213‚Üí      if (
   214‚Üí        filename === 'package.json' ||
   215‚Üí        filename.includes('react') ||
   216‚Üí        ext === '.jsx' ||
   217‚Üí        ext === '.tsx'
   218‚Üí      ) {
   219‚Üí        indicators.react += 2;
   220‚Üí      }
   221‚Üí
   222‚Üí      // Node.js indicators
   223‚Üí      if (filename === 'package.json' || filename === 'server.js' || filename === 'app.js') {
   224‚Üí        indicators.node += 2;
   225‚Üí      }
   226‚Üí
   227‚Üí      // Python indicators
   228‚Üí      if (ext === '.py' || filename === 'requirements.txt' || filename === 'setup.py') {
   229‚Üí        indicators.python += 2;
   230‚Üí      }
   231‚Üí
   232‚Üí      // Web indicators
   233‚Üí      if (ext === '.html' || ext === '.css' || dir.includes('public') || dir.includes('assets')) {
   234‚Üí        indicators.web += 1;
   235‚Üí      }
   236‚Üí    });
   237‚Üí
   238‚Üí    const maxFramework = Object.keys(indicators).reduce((a, b) =>
   239‚Üí      indicators[a] > indicators[b] ? a : b
   240‚Üí    );
   241‚Üí
   242‚Üí    return {
   243‚Üí      name: maxFramework,
   244‚Üí      confidence: (indicators[maxFramework] / files.length) * 100,
   245‚Üí      indicators,
   246‚Üí    };
   247‚Üí  }
   248‚Üí
   249‚Üí  analyzeDirectoryStructure(files) {
   250‚Üí    const structure = {
   251‚Üí      depth: 0,
   252‚Üí      commonPaths: new Map(),
   253‚Üí      organizationScore: 0,
   254‚Üí    };
   255‚Üí
   256‚Üí    files.forEach(file => {
   257‚Üí      const parts = file.split(path.sep);
   258‚Üí      structure.depth = Math.max(structure.depth, parts.length);
   259‚Üí
   260‚Üí      // Count common directory patterns
   261‚Üí      parts.forEach((part, index) => {
   262‚Üí        if (index < parts.length - 1) {
   263‚Üí          // Not the filename
   264‚Üí          const pathKey = parts.slice(0, index + 1).join('/');
   265‚Üí          structure.commonPaths.set(pathKey, (structure.commonPaths.get(pathKey) || 0) + 1);
   266‚Üí        }
   267‚Üí      });
   268‚Üí    });
   269‚Üí
   270‚Üí    // Calculate organization score based on common patterns
   271‚Üí    const knownPatterns = [
   272‚Üí      'src',
   273‚Üí      'lib',
   274‚Üí      'components',
   275‚Üí      'utils',
   276‚Üí      'services',
   277‚Üí      'public',
   278‚Üí      'assets',
   279‚Üí      'tests',
   280‚Üí      'config',
   281‚Üí    ];
   282‚Üí
   283‚Üí    let patternMatches = 0;
   284‚Üí    structure.commonPaths.forEach((count, pathKey) => {
   285‚Üí      const parts = pathKey.split('/');
   286‚Üí      if (parts.some(part => knownPatterns.includes(part.toLowerCase()))) {
   287‚Üí        patternMatches += count;
   288‚Üí      }
   289‚Üí    });
   290‚Üí
   291‚Üí    structure.organizationScore = Math.min(100, (patternMatches / files.length) * 100);
   292‚Üí
   293‚Üí    return structure;
   294‚Üí  }
   295‚Üí
   296‚Üí  analyzeFileTypes(files) {
   297‚Üí    const types = new Map();
   298‚Üí    let totalConfidence = 0;
   299‚Üí
   300‚Üí    files.forEach(file => {
   301‚Üí      const ext = path.extname(file).toLowerCase();
   302‚Üí      types.set(ext, (types.get(ext) || 0) + 1);
   303‚Üí    });
   304‚Üí
   305‚Üí    // Calculate confidence based on file type consistency
   306‚Üí    const dominantTypes = Array.from(types.entries())
   307‚Üí      .sort((a, b) => b[1] - a[1])
   308‚Üí      .slice(0, 3);
   309‚Üí
   310‚Üí    const totalFiles = files.length;
   311‚Üí    const dominantCount = dominantTypes.reduce((sum, [, count]) => sum + count, 0);
   312‚Üí
   313‚Üí    totalConfidence = (dominantCount / totalFiles) * 100;
   314‚Üí
   315‚Üí    return {
   316‚Üí      types: Object.fromEntries(types),
   317‚Üí      confidence: totalConfidence,
   318‚Üí    };
   319‚Üí  }
   320‚Üí
   321‚Üí  async generatePathMappings(files, framework) {
   322‚Üí    const mappings = new Map();
   323‚Üí
   324‚Üí    // Create intelligent mappings based on framework and file analysis
   325‚Üí    files.forEach(file => {
   326‚Üí      const filename = path.basename(file);
   327‚Üí      const directory = path.dirname(file);
   328‚Üí      const ext = path.extname(file);
   329‚Üí
   330‚Üí      // Generate mapping confidence score
   331‚Üí      let confidence = this.calculatePathConfidence(file, framework);
   332‚Üí
   333‚Üí      // Check against anti-patterns
   334‚Üí      const isAntiPattern = this.pathKnowledge.antiPatterns.some(pattern => file.includes(pattern));
   335‚Üí
   336‚Üí      if (isAntiPattern) {
   337‚Üí        confidence *= 0.1; // Heavily penalize anti-patterns
   338‚Üí      }
   339‚Üí
   340‚Üí      mappings.set(file, {
   341‚Üí        originalPath: file,
   342‚Üí        confidence,
   343‚Üí        framework: framework.name,
   344‚Üí        purpose: this.detectFilePurpose(filename, directory),
   345‚Üí        recommended: confidence >= this.options.confidenceThreshold,
   346‚Üí      });
   347‚Üí    });
   348‚Üí
   349‚Üí    return mappings;
   350‚Üí  }
   351‚Üí
   352‚Üí  calculatePathConfidence(filePath, framework) {
   353‚Üí    let confidence = 50; // Base confidence
   354‚Üí
   355‚Üí    const parts = filePath.split(path.sep);
   356‚Üí    const filename = path.basename(filePath);
   357‚Üí    const ext = path.extname(filePath);
   358‚Üí
   359‚Üí    // Framework-specific boost
   360‚Üí    const frameworkInfo = this.pathKnowledge.frameworks[framework.name];
   361‚Üí    if (frameworkInfo) {
   362‚Üí      // Check file extension match
   363‚Üí      if (frameworkInfo.fileTypes.includes(ext)) {
   364‚Üí        confidence += 20;
   365‚Üí      }
   366‚Üí
   367‚Üí      // Check directory pattern match
   368‚Üí      const matchingPatterns = frameworkInfo.patterns.filter(pattern => filePath.includes(pattern));
   369‚Üí      confidence += matchingPatterns.length * 15;
   370‚Üí    }
   371‚Üí
   372‚Üí    // Purpose-based boost
   373‚Üí    const purpose = this.detectFilePurpose(filename, path.dirname(filePath));
   374‚Üí    if (purpose) {
   375‚Üí      confidence += 10;
   376‚Üí    }
   377‚Üí
   378‚Üí    // Depth penalty (too deep = likely build/temp files)
   379‚Üí    if (parts.length > 6) {
   380‚Üí      confidence -= (parts.length - 6) * 5;
   381‚Üí    }
   382‚Üí
   383‚Üí    // Common file patterns boost
   384‚Üí    if (filename.match(/\.(config|settings|env|constants?)\./)) {
   385‚Üí      confidence += 15;
   386‚Üí    }
   387‚Üí
   388‚Üí    return Math.max(0, Math.min(100, confidence));
   389‚Üí  }
   390‚Üí
   391‚Üí  detectFilePurpose(filename, directory) {
   392‚Üí    const lowerFilename = filename.toLowerCase();
   393‚Üí    const lowerDirectory = directory.toLowerCase();
   394‚Üí
   395‚Üí    for (const [purpose, info] of Object.entries(this.pathKnowledge.purposes)) {
   396‚Üí      // Check filename keywords
   397‚Üí      const filenameMatch = info.keywords.some(keyword => lowerFilename.includes(keyword));
   398‚Üí
   399‚Üí      // Check directory location
   400‚Üí      const locationMatch = info.typical_locations.some(location =>
   401‚Üí        lowerDirectory.includes(location.replace('/', ''))
   402‚Üí      );
   403‚Üí
   404‚Üí      if (filenameMatch || locationMatch) {
   405‚Üí        return {
   406‚Üí          type: purpose,
   407‚Üí          confidence: info.confidence * (filenameMatch && locationMatch ? 1 : 0.7),
   408‚Üí        };
   409‚Üí      }
   410‚Üí    }
   411‚Üí
   412‚Üí    return null;
   413‚Üí  }
   414‚Üí
   415‚Üí  generateRecommendations(analysis) {
   416‚Üí    const recommendations = [];
   417‚Üí
   418‚Üí    // Framework-specific recommendations
   419‚Üí    if (analysis.framework.confidence < 70) {
   420‚Üí      recommendations.push({
   421‚Üí        type: 'framework',
   422‚Üí        priority: 'medium',
   423‚Üí        message: i18n.t('strikeErrors.aiPathIntelligence.frameworkUncertain', {
   424‚Üí          confidence: analysis.framework.confidence,
   425‚Üí        }),
   426‚Üí      });
   427‚Üí    }
   428‚Üí
   429‚Üí    // Organization recommendations
   430‚Üí    if (analysis.structure.organizationScore < 50) {
   431‚Üí      recommendations.push({
   432‚Üí        type: 'organization',
   433‚Üí        priority: 'high',
   434‚Üí        message: i18n.t('strikeErrors.aiPathIntelligence.directoryDisorganized'),
   435‚Üí      });
   436‚Üí    }
   437‚Üí
   438‚Üí    // File count recommendations
   439‚Üí    if (analysis.totalFiles > 1000) {
   440‚Üí      recommendations.push({
   441‚Üí        type: 'performance',
   442‚Üí        priority: 'medium',
   443‚Üí        message: i18n.t('strikeErrors.aiPathIntelligence.largeProject', {
   444‚Üí          totalFiles: analysis.totalFiles,
   445‚Üí        }),
   446‚Üí      });
   447‚Üí    }
   448‚Üí
   449‚Üí    // Path mapping recommendations
   450‚Üí    const lowConfidencePaths = Array.from(analysis.pathMappings.values()).filter(
   451‚Üí      mapping => mapping.confidence < this.options.confidenceThreshold
   452‚Üí    ).length;
   453‚Üí
   454‚Üí    if (lowConfidencePaths > analysis.totalFiles * 0.3) {
   455‚Üí      recommendations.push({
   456‚Üí        type: 'paths',
   457‚Üí        priority: 'high',
   458‚Üí        message: i18n.t('strikeErrors.aiPathIntelligence.lowConfidencePaths', {
   459‚Üí          lowConfidencePaths,
   460‚Üí        }),
   461‚Üí      });
   462‚Üí    }
   463‚Üí
   464‚Üí    return recommendations;
   465‚Üí  }
   466‚Üí
   467‚Üí  async intelligentPathResolution(targetPath, context = {}) {
   468‚Üí    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.resolvingPath', { targetPath }));
   469‚Üí
   470‚Üí    const suggestions = [];
   471‚Üí
   472‚Üí    // Check all uploaded directories for potential matches
   473‚Üí    for (const [dirPath, analysis] of this.uploadedDirectories) {
   474‚Üí      const matches = await this.findPathMatches(targetPath, analysis, context);
   475‚Üí      suggestions.push(...matches);
   476‚Üí    }
   477‚Üí
   478‚Üí    // Sort by confidence and return top suggestions
   479‚Üí    suggestions.sort((a, b) => b.confidence - a.confidence);
   480‚Üí
   481‚Üí    const topSuggestions = suggestions.slice(0, this.options.maxSuggestions);
   482‚Üí
   483‚Üí    this.logToTerminal(
   484‚Üí      i18n.t('strikeErrors.aiPathIntelligence.foundSuggestions', { count: topSuggestions.length })
   485‚Üí    );
   486‚Üí
   487‚Üí    return {
   488‚Üí      originalPath: targetPath,
   489‚Üí      suggestions: topSuggestions,
   490‚Üí      bestMatch: topSuggestions[0] || null,
   491‚Üí      confidence: topSuggestions[0]?.confidence || 0,
   492‚Üí    };
   493‚Üí  }
   494‚Üí
   495‚Üí  async findPathMatches(targetPath, analysis, context) {
   496‚Üí    const matches = [];
   497‚Üí    const targetBasename = path.basename(targetPath);
   498‚Üí    const targetExt = path.extname(targetPath);
   499‚Üí
   500‚Üí    // Search through all mapped paths
   501‚Üí    for (const [filePath, mapping] of analysis.pathMappings) {
   502‚Üí      const fileBasename = path.basename(filePath);
   503‚Üí      const fileExt = path.extname(filePath);
   504‚Üí
   505‚Üí      let matchConfidence = 0;
   506‚Üí
   507‚Üí      // Exact filename match
   508‚Üí      if (fileBasename === targetBasename) {
   509‚Üí        matchConfidence = 95;
   510‚Üí      }
   511‚Üí      // Extension and similar name
   512‚Üí      else if (
   513‚Üí        fileExt === targetExt &&
   514‚Üí        this.calculateStringSimilarity(fileBasename, targetBasename) > 0.7
   515‚Üí      ) {
   516‚Üí        matchConfidence = 80;
   517‚Üí      }
   518‚Üí      // Extension match only
   519‚Üí      else if (fileExt === targetExt) {
   520‚Üí        matchConfidence = 40;
   521‚Üí      }
   522‚Üí      // Similar name, different extension
   523‚Üí      else if (this.calculateStringSimilarity(fileBasename, targetBasename) > 0.8) {
   524‚Üí        matchConfidence = 60;
   525‚Üí      }
   526‚Üí
   527‚Üí      // Apply context boost
   528‚Üí      if (context.framework && context.framework === analysis.framework.name) {
   529‚Üí        matchConfidence += 10;
   530‚Üí      }
   531‚Üí
   532‚Üí      // Apply mapping confidence
   533‚Üí      matchConfidence = (matchConfidence * mapping.confidence) / 100;
   534‚Üí
   535‚Üí      if (matchConfidence >= 30) {
   536‚Üí        // Minimum viable confidence
   537‚Üí        matches.push({
   538‚Üí          originalPath: targetPath,
   539‚Üí          resolvedPath: filePath,
   540‚Üí          confidence: Math.round(matchConfidence),
   541‚Üí          reasoning: this.generateMatchReasoning(targetPath, filePath, matchConfidence, mapping),
   542‚Üí          framework: analysis.framework.name,
   543‚Üí          purpose: mapping.purpose,
   544‚Üí        });
   545‚Üí      }
   546‚Üí    }
   547‚Üí
   548‚Üí    return matches;
   549‚Üí  }
   550‚Üí
   551‚Üí  calculateStringSimilarity(str1, str2) {
   552‚Üí    // Simple Levenshtein distance-based similarity
   553‚Üí    const longer = str1.length > str2.length ? str1 : str2;
   554‚Üí    const shorter = str1.length > str2.length ? str2 : str1;
   555‚Üí
   556‚Üí    if (longer.length === 0) return 1.0;
   557‚Üí
   558‚Üí    const distance = this.levenshteinDistance(longer, shorter);
   559‚Üí    return (longer.length - distance) / longer.length;
   560‚Üí  }
   561‚Üí
   562‚Üí  levenshteinDistance(str1, str2) {
   563‚Üí    const matrix = [];
   564‚Üí
   565‚Üí    for (let i = 0; i <= str2.length; i++) {
   566‚Üí      matrix[i] = [i];
   567‚Üí    }
   568‚Üí
   569‚Üí    for (let j = 0; j <= str1.length; j++) {
   570‚Üí      matrix[0][j] = j;
   571‚Üí    }
   572‚Üí
   573‚Üí    for (let i = 1; i <= str2.length; i++) {
   574‚Üí      for (let j = 1; j <= str1.length; j++) {
   575‚Üí        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
   576‚Üí          matrix[i][j] = matrix[i - 1][j - 1];
   577‚Üí        } else {
   578‚Üí          matrix[i][j] = Math.min(
   579‚Üí            matrix[i - 1][j - 1] + 1,
   580‚Üí            matrix[i][j - 1] + 1,
   581‚Üí            matrix[i - 1][j] + 1
   582‚Üí          );
   583‚Üí        }
   584‚Üí      }
   585‚Üí    }
   586‚Üí
   587‚Üí    return matrix[str2.length][str1.length];
   588‚Üí  }
   589‚Üí
   590‚Üí  generateMatchReasoning(targetPath, resolvedPath, confidence, mapping) {
   591‚Üí    const reasons = [];
   592‚Üí
   593‚Üí    if (path.basename(targetPath) === path.basename(resolvedPath)) {
   594‚Üí      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.exactFilenameMatch'));
   595‚Üí    }
   596‚Üí
   597‚Üí    if (path.extname(targetPath) === path.extname(resolvedPath)) {
   598‚Üí      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.sameFileExtension'));
   599‚Üí    }
   600‚Üí
   601‚Üí    if (mapping.purpose) {
   602‚Üí      reasons.push(
   603‚Üí        i18n.t('strikeErrors.aiPathIntelligence.matchesPattern', {
   604‚Üí          purposeType: mapping.purpose.type,
   605‚Üí        })
   606‚Üí      );
   607‚Üí    }
   608‚Üí
   609‚Üí    if (confidence > 80) {
   610‚Üí      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.highConfidenceMatch'));
   611‚Üí    }
   612‚Üí
   613‚Üí    return reasons.join(', ');
   614‚Üí  }
   615‚Üí
   616‚Üí  logToTerminal(message) {
   617‚Üí    const timestamp = new Date().toLocaleTimeString();
   618‚Üí    const logMessage = `[${timestamp}] [AI Path Intelligence] ${message}`;
   619‚Üí
   620‚Üí    if (this.terminalConnection && typeof this.terminalConnection.write === 'function') {
   621‚Üí      this.terminalConnection.write(logMessage + '\r\n');
   622‚Üí    } else if (this.terminalConnection && typeof this.terminalConnection.send === 'function') {
   623‚Üí      this.terminalConnection.send(logMessage);
   624‚Üí    }
   625‚Üí
   626‚Üí    console.log(logMessage);
   627‚Üí  }
   628‚Üí
   629‚Üí  // Public API methods
   630‚Üí  getAnalysisFor(directoryPath) {
   631‚Üí    return this.uploadedDirectories.get(directoryPath);
   632‚Üí  }
   633‚Üí
   634‚Üí  getAllAnalyses() {
   635‚Üí    return Array.from(this.uploadedDirectories.values());
   636‚Üí  }
   637‚Üí
   638‚Üí  clearAnalyses() {
   639‚Üí    this.uploadedDirectories.clear();
   640‚Üí    this.pathHistory = [];
   641‚Üí    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.pathIntelligenceCleared'));
   642‚Üí  }
   643‚Üí
   644‚Üí  getPathHistory() {
   645‚Üí    return this.pathHistory.slice(-20); // Return last 20 entries
   646‚Üí  }
   647‚Üí
   648‚Üí  updateThreshold(newThreshold) {
   649‚Üí    this.options.confidenceThreshold = Math.max(0, Math.min(100, newThreshold));
   650‚Üí    this.logToTerminal(
   651‚Üí      i18n.t('strikeErrors.aiPathIntelligence.thresholdUpdated', {
   652‚Üí        threshold: this.options.confidenceThreshold,
   653‚Üí      })
   654‚Üí    );
   655‚Üí  }
   656‚Üí}
   657‚Üí
   658‚Üímodule.exports = {
   659‚Üí  AIPathIntelligence,
   660‚Üí  createAIPathIntelligence: (options = {}) => new AIPathIntelligence(options),
   661‚Üí};
