   144‚Üí    );
   145‚Üí    console.log(
   146‚Üí      i18n.t('strikeErrors.connectionEngine.preservationRate', {
   147‚Üí        rate: this.results.preservationRate,
   148‚Üí      })
   149‚Üí    );
   150‚Üí
   151‚Üí    return this.results;
   152‚Üí  }
   153‚Üí
   154‚Üí  async scanTargetFiles(directory) {
   155‚Üí    const targetFiles = [];
   156‚Üí
   157‚Üí    const scanRecursive = dir => {
   158‚Üí      const items = fs.readdirSync(dir);
   159‚Üí
   160‚Üí      items.forEach(item => {
   161‚Üí        const fullPath = path.join(dir, item);
   162‚Üí        const stat = fs.statSync(fullPath);
   163‚Üí
   164‚Üí        if (stat.isDirectory()) {
   165‚Üí          if (!['node_modules', '.git', '__pycache__', 'dist'].includes(item)) {
   166‚Üí            scanRecursive(fullPath);
   167‚Üí          }
   168‚Üí        } else {
   169‚Üí          const extension = path.extname(item).toLowerCase();
   170‚Üí          if (['.js', '.py', '.html', '.css'].includes(extension)) {
   171‚Üí            targetFiles.push({
