   215‚Üí        bestConnection: connectionScores[0],
   216‚Üí        alternativeConnections: connectionScores.slice(1, 3), // Top 3 alternatives
   217‚Üí        confidence: connectionScores[0].confidence,
   218‚Üí        preservationValue: orphan.preservationValue || 50,
   219‚Üí      };
   220‚Üí    }
   221‚Üí
   222‚Üí    return null;
   223‚Üí  }
   224‚Üí
   225‚Üí  async calculateConnectionScore(orphan, targetFile) {
   226‚Üí    let totalScore = 0;
   227‚Üí    let totalWeight = 0;
   228‚Üí    const reasoning = [];
   229‚Üí    let primaryStrategy = null;
   230‚Üí    let implementationMethod = 'reference';
   231‚Üí
   232‚Üí    // Apply all connection strategies
   233‚Üí    for (const [strategyName, strategy] of Object.entries(this.connectionStrategies)) {
   234‚Üí      const analysis = await strategy.analyze(orphan, targetFile);
   235‚Üí      const weightedScore = analysis.score * strategy.weight;
   236‚Üí
   237‚Üí      totalScore += weightedScore;
   238‚Üí      totalWeight += strategy.weight;
   239‚Üí
   240‚Üí      if (analysis.score > 0) {
   241‚Üí        reasoning.push({
   242‚Üí          strategy: strategyName,
   243‚Üí          score: analysis.score,
   244‚Üí          reason: analysis.reason,
   245‚Üí        });
   246‚Üí
   247‚Üí        if (!primaryStrategy || analysis.score > primaryStrategy.score) {
   248‚Üí          primaryStrategy = { name: strategyName, ...analysis };
   249‚Üí          implementationMethod = analysis.implementationMethod || 'reference';
   250‚Üí        }
   251‚Üí      }
   252‚Üí    }
   253‚Üí
   254‚Üí    const confidence = totalWeight > 0 ? Math.round((totalScore / totalWeight) * 100) : 0;
   255‚Üí
   256‚Üí    return {
   257‚Üí      confidence,
   258‚Üí      reasoning,
   259‚Üí      strategy: primaryStrategy?.name || 'none',
   260‚Üí      implementationMethod,
   261‚Üí      primaryReason: primaryStrategy?.reason || 'No clear connection found',
   262‚Üí    };
   263‚Üí  }
   264‚Üí
   265‚Üí  async analyzeSemanticMatch(orphan, targetFile) {
   266‚Üí    const score = { score: 0, reason: '', implementationMethod: 'reference' };
   267‚Üí
   268‚Üí    // Check file name patterns
   269‚Üí    const fileName = path.basename(targetFile.name, path.extname(targetFile.name));
   270‚Üí    const filePattern = this.semanticMatcher.filePatterns[targetFile.name];
   271‚Üí
   272‚Üí    if (filePattern) {
   273‚Üí      const keywordMatches = filePattern.keywords.filter(keyword =>
   274‚Üí        orphan.name.toLowerCase().includes(keyword)
   275‚Üí      );
   276‚Üí
   277‚Üí      if (keywordMatches.length > 0) {
   278‚Üí        score.score = filePattern.weight * (keywordMatches.length / filePattern.keywords.length);
   279‚Üí        score.reason = i18n.t('strikeErrors.connectionEngine.functionNameContainsKeywords', {
   280‚Üí          keywords: keywordMatches.join(', '),
   281‚Üí        });
   282‚Üí        score.implementationMethod = 'move';
   283‚Üí        return score;
   284‚Üí      }
   285‚Üí    }
   286‚Üí
   287‚Üí    // Check function category patterns
   288‚Üí    for (const [category, categoryInfo] of Object.entries(
   289‚Üí      this.semanticMatcher.functionCategories
   290‚Üí    )) {
   291‚Üí      const patternMatch = categoryInfo.patterns.some(pattern => pattern.test(orphan.name));
   292‚Üí
   293‚Üí      if (patternMatch) {
   294‚Üí        const homeMatch = categoryInfo.homes.some(home =>
   295‚Üí          targetFile.name.includes(home.replace('.js', ''))
   296‚Üí        );
   297‚Üí
   298‚Üí        if (homeMatch) {
   299‚Üí          score.score = 0.8;
   300‚Üí          score.reason = i18n.t('strikeErrors.connectionEngine.functionMatchesPattern', {
   301‚Üí            category,
   302‚Üí          });
   303‚Üí          score.implementationMethod = 'move';
   304‚Üí          return score;
   305‚Üí        }
   306‚Üí      }
   307‚Üí    }
   308‚Üí
   309‚Üí    // Check semantic similarity in file name
   310‚Üí    const semanticSimilarity = this.calculateSemanticSimilarity(orphan.name, fileName);
   311‚Üí    if (semanticSimilarity > 0.3) {
   312‚Üí      score.score = semanticSimilarity;
   313‚Üí      score.reason = i18n.t('strikeErrors.connectionEngine.semanticSimilarity', {
   314‚Üí        percentage: Math.round(semanticSimilarity * 100),
   315‚Üí      });
   316‚Üí      score.implementationMethod = 'reference';
   317‚Üí    }
   318‚Üí
   319‚Üí    return score;
   320‚Üí  }
   321‚Üí
   322‚Üí  async analyzePatternMatch(orphan, targetFile) {
   323‚Üí    const score = { score: 0, reason: '', implementationMethod: 'reference' };
   324‚Üí
   325‚Üí    // Load target file content if needed
   326‚Üí    if (!targetFile.content) {
   327‚Üí      try {
   328‚Üí        targetFile.content = fs.readFileSync(targetFile.path, 'utf8');
   329‚Üí      } catch (error) {
   330‚Üí        return score;
   331‚Üí      }
   332‚Üí    }
   333‚Üí
   334‚Üí    // Look for similar function patterns in the target file
   335‚Üí    const functionPattern = /function\s+(\w+)|const\s+(\w+)\s*=|(\w+)\s*:/g;
   336‚Üí    const existingFunctions = [];
   337‚Üí
   338‚Üí    let match;
   339‚Üí    while ((match = functionPattern.exec(targetFile.content)) !== null) {
   340‚Üí      const funcName = match[1] || match[2] || match[3];
   341‚Üí      if (funcName) existingFunctions.push(funcName);
   342‚Üí    }
   343‚Üí
   344‚Üí    // Calculate pattern similarity
   345‚Üí    let maxSimilarity = 0;
   346‚Üí    let bestMatch = null;
   347‚Üí
   348‚Üí    existingFunctions.forEach(existingFunc => {
   349‚Üí      const similarity = this.calculatePatternSimilarity(orphan.name, existingFunc);
   350‚Üí      if (similarity > maxSimilarity) {
   351‚Üí        maxSimilarity = similarity;
   352‚Üí        bestMatch = existingFunc;
   353‚Üí      }
   354‚Üí    });
   355‚Üí
   356‚Üí    if (maxSimilarity > 0.4) {
   357‚Üí      score.score = maxSimilarity;
   358‚Üí      score.reason = i18n.t('strikeErrors.connectionEngine.similarToExistingFunction', {
   359‚Üí        bestMatch,
   360‚Üí        percentage: Math.round(maxSimilarity * 100),
   361‚Üí      });
   362‚Üí      score.implementationMethod = 'move';
   363‚Üí    }
   364‚Üí
   365‚Üí    return score;
   366‚Üí  }
   367‚Üí
   368‚Üí  async analyzeStructuralMatch(orphan, targetFile) {
   369‚Üí    const score = { score: 0, reason: '', implementationMethod: 'reference' };
   370‚Üí
   371‚Üí    // Favor files that aren't too large (easier to maintain)
   372‚Üí    const fileSizeScore = this.calculateFileSizeScore(targetFile.size);
   373‚Üí
   374‚Üí    // Check if file already has similar structural patterns
   375‚Üí    if (!targetFile.content) {
   376‚Üí      try {
   377‚Üí        targetFile.content = fs.readFileSync(targetFile.path, 'utf8');
   378‚Üí      } catch (error) {
   379‚Üí        return score;
   380‚Üí      }
   381‚Üí    }
   382‚Üí
   383‚Üí    // Count existing functions in file
   384‚Üí    const functionCount = (
   385‚Üí      targetFile.content.match(/function\s+\w+|const\s+\w+\s*=.*=>|const\s+\w+\s*=.*function/g) ||
   386‚Üí      []
   387‚Üí    ).length;
   388‚Üí
   389‚Üí    // Prefer files with moderate function count (not empty, not overcrowded)
   390‚Üí    let structuralScore = 0;
   391‚Üí    if (functionCount >= 3 && functionCount <= 15) {
   392‚Üí      structuralScore = 0.7;
   393‚Üí    } else if (functionCount >= 1 && functionCount <= 25) {
   394‚Üí      structuralScore = 0.5;
   395‚Üí    } else if (functionCount === 0) {
   396‚Üí      structuralScore = 0.3; // Empty file, could be good home
   397‚Üí    }
   398‚Üí
   399‚Üí    score.score = (fileSizeScore + structuralScore) / 2;
   400‚Üí    score.reason = i18n.t('strikeErrors.connectionEngine.goodStructuralFit', {
   401‚Üí      functionCount,
   402‚Üí      fileSize: Math.round(targetFile.size / 1024),
   403‚Üí    });
   404‚Üí    score.implementationMethod = functionCount === 0 ? 'move' : 'reference';
   405‚Üí
   406‚Üí    return score;
   407‚Üí  }
   408‚Üí
   409‚Üí  async analyzeContextualMatch(orphan, targetFile) {
   410‚Üí    const score = { score: 0, reason: '', implementationMethod: 'reference' };
   411‚Üí
   412‚Üí    // This would analyze the context where the orphan was originally used
   413‚Üí    // For now, provide a basic implementation based on file location patterns
   414‚Üí
   415‚Üí    const fileDir = path.dirname(targetFile.relativePath);
   416‚Üí    const fileDirName = path.basename(fileDir);
   417‚Üí
   418‚Üí    // Boost score if orphan category matches directory structure
   419‚Üí    const contextualKeywords = {
   420‚Üí      components: ['component', 'widget', 'element'],
   421‚Üí      utils: ['util', 'helper', 'tool'],
   422‚Üí      services: ['service', 'api', 'client'],
   423‚Üí      validators: ['validate', 'check', 'verify'],
   424‚Üí      math: ['calc', 'compute', 'math', 'geometry'],
   425‚Üí    };
   426‚Üí
   427‚Üí    const dirKeywords = contextualKeywords[fileDirName] || [];
   428‚Üí    const keywordMatch = dirKeywords.some(keyword => orphan.name.toLowerCase().includes(keyword));
   429‚Üí
   430‚Üí    if (keywordMatch) {
   431‚Üí      score.score = 0.6;
   432‚Üí      score.reason = i18n.t('strikeErrors.connectionEngine.functionContextMatches', {
   433‚Üí        dirName: fileDirName,
   434‚Üí      });
