   441‚Üí  calculateSemanticSimilarity(str1, str2) {
   442‚Üí    // Simple semantic similarity based on common substrings and patterns
   443‚Üí    const s1 = str1.toLowerCase();
   444‚Üí    const s2 = str2.toLowerCase();
   445‚Üí
   446‚Üí    // Exact match
   447‚Üí    if (s1 === s2) return 1.0;
   448‚Üí
   449‚Üí    // Substring match
   450‚Üí    if (s1.includes(s2) || s2.includes(s1)) return 0.8;
   451‚Üí
   452‚Üí    // Common prefix/suffix
   453‚Üí    let commonPrefix = 0;
   454‚Üí    for (let i = 0; i < Math.min(s1.length, s2.length); i++) {
   455‚Üí      if (s1[i] === s2[i]) commonPrefix++;
   456‚Üí      else break;
   457‚Üí    }
   458‚Üí
   459‚Üí    let commonSuffix = 0;
   460‚Üí    for (let i = 1; i <= Math.min(s1.length, s2.length); i++) {
   461‚Üí      if (s1[s1.length - i] === s2[s2.length - i]) commonSuffix++;
   462‚Üí      else break;
   463‚Üí    }
   464‚Üí
   465‚Üí    const maxLength = Math.max(s1.length, s2.length);
   466‚Üí    const similarity = (commonPrefix + commonSuffix) / maxLength;
   467‚Üí
   468‚Üí    return Math.min(similarity, 0.7); // Cap at 0.7 for partial matches
   469‚Üí  }
   470‚Üí
   471‚Üí  calculatePatternSimilarity(str1, str2) {
   472‚Üí    // Calculate similarity based on naming patterns
   473‚Üí    const pattern1 = this.extractNamingPattern(str1);
   474‚Üí    const pattern2 = this.extractNamingPattern(str2);
   475‚Üí
   476‚Üí    let similarity = 0;
   477‚Üí
   478‚Üí    // Same pattern type
   479‚Üí    if (pattern1.type === pattern2.type) similarity += 0.3;
   480‚Üí
   481‚Üí    // Similar prefixes
   482‚Üí    if (pattern1.prefix === pattern2.prefix && pattern1.prefix) similarity += 0.4;
   483‚Üí
   484‚Üí    // Similar suffixes
   485‚Üí    if (pattern1.suffix === pattern2.suffix && pattern1.suffix) similarity += 0.3;
   486‚Üí
   487‚Üí    return Math.min(similarity, 1.0);
   488‚Üí  }
   489‚Üí
   490‚Üí  extractNamingPattern(functionName) {
   491‚Üí    const patterns = {
   492‚Üí      camelCase: /^[a-z][a-zA-Z0-9]*$/,
   493‚Üí      PascalCase: /^[A-Z][a-zA-Z0-9]*$/,
   494‚Üí      snake_case: /^[a-z][a-z0-9_]*$/,
   495‚Üí      CONSTANT: /^[A-Z][A-Z0-9_]*$/,
   496‚Üí    };
   497‚Üí
   498‚Üí    let type = 'unknown';
   499‚Üí    for (const [patternName, regex] of Object.entries(patterns)) {
   500‚Üí      if (regex.test(functionName)) {
   501‚Üí        type = patternName;
   502‚Üí        break;
   503‚Üí      }
   504‚Üí    }
   505‚Üí
   506‚Üí    // Extract common prefixes/suffixes
   507‚Üí    const commonPrefixes = [
   508‚Üí      'get',
   509‚Üí      'set',
   510‚Üí      'is',
   511‚Üí      'has',
   512‚Üí      'can',
   513‚Üí      'should',
   514‚Üí      'create',
