   516‚Üí      'make',
   517‚Üí      'handle',
   518‚Üí      'process',
   519‚Üí    ];
   520‚Üí    const commonSuffixes = ['er', 'or', 'Handler', 'Manager', 'Service', 'Utils', 'Helper'];
   521‚Üí
   522‚Üí    let prefix = '';
   523‚Üí    let suffix = '';
   524‚Üí
   525‚Üí    const lowerName = functionName.toLowerCase();
   526‚Üí    commonPrefixes.forEach(p => {
