   529‚Üí
   530‚Üí    commonSuffixes.forEach(s => {
   531‚Üí      if (lowerName.endsWith(s.toLowerCase())) suffix = s;
   532‚Üí    });
   533‚Üí
   534‚Üí    return { type, prefix, suffix };
