/**
 * INTELLIGENT CONNECTION ENGINE
 *
 * Generic system for connecting orphaned functions to their logical homes
 * Built from proven methodology that achieved 100% connection success rate
 */

const fs = require('fs');
const path = require('path');
const i18n = require('../../i18n');

class IntelligentConnectionEngine {
  constructor(options = {}) {
    this.options = {
      preserveMode: options.preserveMode !== false,
      tempDirectory: options.tempDirectory || '/tmp/connections',
      confidenceThreshold: options.confidenceThreshold || 60,
      createBackups: options.createBackups !== false,
      dryRun: options.dryRun || false,
    };

    this.semanticMatcher = this.initializeSemanticMatcher();
    this.connectionStrategies = this.initializeConnectionStrategies();

    this.results = {
      connectionOpportunities: 0,
      suggestedConnections: [],
      averageConfidence: 0,
      preservationRate: 0,
      appliedConnections: 0,
      failedConnections: 0,
    };
  }

  initializeSemanticMatcher() {
    return {
      // File purpose patterns
      filePatterns: {
        'utils.js': { keywords: ['util', 'helper', 'tool', 'common'], weight: 0.9 },
        'helpers.js': { keywords: ['helper', 'assist', 'support', 'aid'], weight: 0.9 },
        'validation.js': { keywords: ['validate', 'check', 'verify', 'test'], weight: 0.85 },
        'math.js': { keywords: ['calc', 'compute', 'math', 'geometry'], weight: 0.85 },
        'dom.js': { keywords: ['element', 'dom', 'html', 'render'], weight: 0.8 },
        'events.js': { keywords: ['event', 'handler', 'listener', 'trigger'], weight: 0.8 },
        'api.js': { keywords: ['fetch', 'request', 'http', 'api'], weight: 0.8 },
        'storage.js': { keywords: ['store', 'cache', 'persist', 'save'], weight: 0.8 },
        'animation.js': { keywords: ['animate', 'transition', 'move', 'tween'], weight: 0.75 },
        'diagnostics.js': { keywords: ['diagnostic', 'debug', 'monitor', 'track'], weight: 0.75 },
      },

      // Function semantic categories
      functionCategories: {
        utility: {
          patterns: [/^(utils?|helpers?|tools?)/i, /^(format|parse|convert)/i],
          homes: ['utils.js', 'helpers.js', 'common.js'],
        },
        validation: {
          patterns: [/^(validate|check|verify|test|assert)/i],
          homes: ['validation.js', 'validators.js', 'checks.js'],
        },
        mathematical: {
          patterns: [/^(calc|compute|math|geometry|vector|matrix)/i],
          homes: ['math.js', 'calculations.js', 'geometry.js'],
        },
        dom: {
          patterns: [/^(render|draw|element|dom|html)/i],
          homes: ['dom.js', 'render.js', 'ui.js'],
        },
        event: {
          patterns: [/^(on|handle|trigger|emit|listen)/i],
          homes: ['events.js', 'handlers.js', 'listeners.js'],
        },
        storage: {
          patterns: [/^(store|cache|persist|save|load)/i],
          homes: ['storage.js', 'cache.js', 'persistence.js'],
        },
        performance: {
          patterns: [/^(measure|benchmark|optimize|profile)/i],
          homes: ['performance.js', 'diagnostics.js', 'metrics.js'],
        },
      },
    };
  }

  initializeConnectionStrategies() {
    return {
      // Strategy 1: Semantic similarity matching
      semantic: {
        weight: 0.4,
        analyze: (orphan, targetFiles) => this.analyzeSemanticMatch(orphan, targetFiles),
      },

      // Strategy 2: Existing function pattern matching
      pattern: {
        weight: 0.3,
        analyze: (orphan, targetFiles) => this.analyzePatternMatch(orphan, targetFiles),
      },

      // Strategy 3: File size and structure analysis
      structural: {
        weight: 0.2,
        analyze: (orphan, targetFiles) => this.analyzeStructuralMatch(orphan, targetFiles),
      },

      // Strategy 4: Usage context analysis
      contextual: {
        weight: 0.1,
        analyze: (orphan, targetFiles) => this.analyzeContextualMatch(orphan, targetFiles),
      },
    };
  }

  async analyzeConnections(orphans, targetDirectory, options = {}) {
    console.log(i18n.t('strikeErrors.connectionEngine.analyzingConnections'));

    this.options = { ...this.options, ...options };

    // Scan target directory for potential homes
    const targetFiles = await this.scanTargetFiles(targetDirectory);

    // Analyze each orphan for connection opportunities
    const connections = [];

    for (const orphan of orphans) {
      const connectionAnalysis = await this.analyzeOrphanConnections(orphan, targetFiles);
      if (connectionAnalysis && connectionAnalysis.confidence >= this.options.confidenceThreshold) {
        connections.push(connectionAnalysis);
      }
    }

    // Calculate overall metrics
    this.calculateConnectionMetrics(connections, orphans.length);

    // Generate connection plan
    this.results.suggestedConnections = connections;

    console.log(
      i18n.t('strikeErrors.connectionEngine.foundConnections', { count: connections.length })
    );
    console.log(
      i18n.t('strikeErrors.connectionEngine.averageConfidence', {
        confidence: this.results.averageConfidence,
      })
    );
    console.log(
      i18n.t('strikeErrors.connectionEngine.preservationRate', {
        rate: this.results.preservationRate,
      })
    );

    return this.results;
  }

  async scanTargetFiles(directory) {
    const targetFiles = [];

    const scanRecursive = dir => {
      const items = fs.readdirSync(dir);

      items.forEach(item => {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory()) {
          if (!['node_modules', '.git', '__pycache__', 'dist'].includes(item)) {
            scanRecursive(fullPath);
          }
        } else {
          const extension = path.extname(item).toLowerCase();
          if (['.js', '.py', '.html', '.css'].includes(extension)) {
            targetFiles.push({
              path: fullPath,
              relativePath: path.relative(directory, fullPath),
              name: item,
              extension,
              size: stat.size,
              content: null, // Loaded on demand
            });
          }
        }
      });
    };

    scanRecursive(directory);
    return targetFiles;
  }

  async analyzeOrphanConnections(orphan, targetFiles) {
    const connectionScores = [];

    // Analyze connection potential with each target file
    for (const targetFile of targetFiles) {
      const score = await this.calculateConnectionScore(orphan, targetFile);
      if (score.confidence > 0) {
        connectionScores.push({
          targetFile: targetFile.relativePath,
          confidence: score.confidence,
          reasoning: score.reasoning,
          strategy: score.strategy,
          implementationMethod: score.implementationMethod,
        });
      }
    }

    // Sort by confidence (highest first)
    connectionScores.sort((a, b) => b.confidence - a.confidence);

    // Return best connection if above threshold
    if (
      connectionScores.length > 0 &&
      connectionScores[0].confidence >= this.options.confidenceThreshold
    ) {
      return {
        orphan: orphan.name,
        bestConnection: connectionScores[0],
        alternativeConnections: connectionScores.slice(1, 3), // Top 3 alternatives
        confidence: connectionScores[0].confidence,
        preservationValue: orphan.preservationValue || 50,
      };
    }

    return null;
  }

  async calculateConnectionScore(orphan, targetFile) {
    let totalScore = 0;
    let totalWeight = 0;
    const reasoning = [];
    let primaryStrategy = null;
    let implementationMethod = 'reference';

    // Apply all connection strategies
    for (const [strategyName, strategy] of Object.entries(this.connectionStrategies)) {
      const analysis = await strategy.analyze(orphan, targetFile);
      const weightedScore = analysis.score * strategy.weight;

      totalScore += weightedScore;
      totalWeight += strategy.weight;

      if (analysis.score > 0) {
        reasoning.push({
          strategy: strategyName,
          score: analysis.score,
          reason: analysis.reason,
        });

        if (!primaryStrategy || analysis.score > primaryStrategy.score) {
          primaryStrategy = { name: strategyName, ...analysis };
          implementationMethod = analysis.implementationMethod || 'reference';
        }
      }
    }

    const confidence = totalWeight > 0 ? Math.round((totalScore / totalWeight) * 100) : 0;

    return {
      confidence,
      reasoning,
      strategy: primaryStrategy?.name || 'none',
      implementationMethod,
      primaryReason: primaryStrategy?.reason || 'No clear connection found',
    };
  }

  async analyzeSemanticMatch(orphan, targetFile) {
    const score = { score: 0, reason: '', implementationMethod: 'reference' };

    // Check file name patterns
    const fileName = path.basename(targetFile.name, path.extname(targetFile.name));
    const filePattern = this.semanticMatcher.filePatterns[targetFile.name];

    if (filePattern) {
      const keywordMatches = filePattern.keywords.filter(keyword =>
        orphan.name.toLowerCase().includes(keyword)
      );

      if (keywordMatches.length > 0) {
        score.score = filePattern.weight * (keywordMatches.length / filePattern.keywords.length);
        score.reason = i18n.t('strikeErrors.connectionEngine.functionNameContainsKeywords', {
          keywords: keywordMatches.join(', '),
        });
        score.implementationMethod = 'move';
        return score;
      }
    }

    // Check function category patterns
    for (const [category, categoryInfo] of Object.entries(
      this.semanticMatcher.functionCategories
    )) {
      const patternMatch = categoryInfo.patterns.some(pattern => pattern.test(orphan.name));

      if (patternMatch) {
        const homeMatch = categoryInfo.homes.some(home =>
          targetFile.name.includes(home.replace('.js', ''))
        );

        if (homeMatch) {
          score.score = 0.8;
          score.reason = i18n.t('strikeErrors.connectionEngine.functionMatchesPattern', {
            category,
          });
          score.implementationMethod = 'move';
          return score;
        }
      }
    }

    // Check semantic similarity in file name
    const semanticSimilarity = this.calculateSemanticSimilarity(orphan.name, fileName);
    if (semanticSimilarity > 0.3) {
      score.score = semanticSimilarity;
      score.reason = i18n.t('strikeErrors.connectionEngine.semanticSimilarity', {
        percentage: Math.round(semanticSimilarity * 100),
      });
      score.implementationMethod = 'reference';
    }

    return score;
  }

  async analyzePatternMatch(orphan, targetFile) {
    const score = { score: 0, reason: '', implementationMethod: 'reference' };

    // Load target file content if needed
    if (!targetFile.content) {
      try {
        targetFile.content = fs.readFileSync(targetFile.path, 'utf8');
      } catch (error) {
        return score;
      }
    }

    // Look for similar function patterns in the target file
    const functionPattern = /function\s+(\w+)|const\s+(\w+)\s*=|(\w+)\s*:/g;
    const existingFunctions = [];

    let match;
    while ((match = functionPattern.exec(targetFile.content)) !== null) {
      const funcName = match[1] || match[2] || match[3];
      if (funcName) existingFunctions.push(funcName);
    }

    // Calculate pattern similarity
    let maxSimilarity = 0;
    let bestMatch = null;

    existingFunctions.forEach(existingFunc => {
      const similarity = this.calculatePatternSimilarity(orphan.name, existingFunc);
      if (similarity > maxSimilarity) {
        maxSimilarity = similarity;
        bestMatch = existingFunc;
      }
    });

    if (maxSimilarity > 0.4) {
      score.score = maxSimilarity;
      score.reason = i18n.t('strikeErrors.connectionEngine.similarToExistingFunction', {
        bestMatch,
        percentage: Math.round(maxSimilarity * 100),
      });
      score.implementationMethod = 'move';
    }

    return score;
  }

  async analyzeStructuralMatch(orphan, targetFile) {
    const score = { score: 0, reason: '', implementationMethod: 'reference' };

    // Favor files that aren't too large (easier to maintain)
    const fileSizeScore = this.calculateFileSizeScore(targetFile.size);

    // Check if file already has similar structural patterns
    if (!targetFile.content) {
      try {
        targetFile.content = fs.readFileSync(targetFile.path, 'utf8');
      } catch (error) {
        return score;
      }
    }

    // Count existing functions in file
    const functionCount = (
      targetFile.content.match(/function\s+\w+|const\s+\w+\s*=.*=>|const\s+\w+\s*=.*function/g) ||
      []
    ).length;

    // Prefer files with moderate function count (not empty, not overcrowded)
    let structuralScore = 0;
    if (functionCount >= 3 && functionCount <= 15) {
      structuralScore = 0.7;
    } else if (functionCount >= 1 && functionCount <= 25) {
      structuralScore = 0.5;
    } else if (functionCount === 0) {
      structuralScore = 0.3; // Empty file, could be good home
    }

    score.score = (fileSizeScore + structuralScore) / 2;
    score.reason = i18n.t('strikeErrors.connectionEngine.goodStructuralFit', {
      functionCount,
      fileSize: Math.round(targetFile.size / 1024),
    });
    score.implementationMethod = functionCount === 0 ? 'move' : 'reference';

    return score;
  }

  async analyzeContextualMatch(orphan, targetFile) {
    const score = { score: 0, reason: '', implementationMethod: 'reference' };

    // This would analyze the context where the orphan was originally used
    // For now, provide a basic implementation based on file location patterns

    const fileDir = path.dirname(targetFile.relativePath);
    const fileDirName = path.basename(fileDir);

    // Boost score if orphan category matches directory structure
    const contextualKeywords = {
      components: ['component', 'widget', 'element'],
      utils: ['util', 'helper', 'tool'],
      services: ['service', 'api', 'client'],
      validators: ['validate', 'check', 'verify'],
      math: ['calc', 'compute', 'math', 'geometry'],
    };

    const dirKeywords = contextualKeywords[fileDirName] || [];
    const keywordMatch = dirKeywords.some(keyword => orphan.name.toLowerCase().includes(keyword));

    if (keywordMatch) {
      score.score = 0.6;
      score.reason = i18n.t('strikeErrors.connectionEngine.functionContextMatches', {
        dirName: fileDirName,
      });
      score.implementationMethod = 'move';
    }

    return score;
  }

  calculateSemanticSimilarity(str1, str2) {
    // Simple semantic similarity based on common substrings and patterns
    const s1 = str1.toLowerCase();
    const s2 = str2.toLowerCase();

    // Exact match
    if (s1 === s2) return 1.0;

    // Substring match
    if (s1.includes(s2) || s2.includes(s1)) return 0.8;

    // Common prefix/suffix
    let commonPrefix = 0;
    for (let i = 0; i < Math.min(s1.length, s2.length); i++) {
      if (s1[i] === s2[i]) commonPrefix++;
      else break;
    }

    let commonSuffix = 0;
    for (let i = 1; i <= Math.min(s1.length, s2.length); i++) {
      if (s1[s1.length - i] === s2[s2.length - i]) commonSuffix++;
      else break;
    }

    const maxLength = Math.max(s1.length, s2.length);
    const similarity = (commonPrefix + commonSuffix) / maxLength;

    return Math.min(similarity, 0.7); // Cap at 0.7 for partial matches
  }

  calculatePatternSimilarity(str1, str2) {
    // Calculate similarity based on naming patterns
    const pattern1 = this.extractNamingPattern(str1);
    const pattern2 = this.extractNamingPattern(str2);

    let similarity = 0;

    // Same pattern type
    if (pattern1.type === pattern2.type) similarity += 0.3;

    // Similar prefixes
    if (pattern1.prefix === pattern2.prefix && pattern1.prefix) similarity += 0.4;

    // Similar suffixes
    if (pattern1.suffix === pattern2.suffix && pattern1.suffix) similarity += 0.3;

    return Math.min(similarity, 1.0);
  }

  extractNamingPattern(functionName) {
    const patterns = {
      camelCase: /^[a-z][a-zA-Z0-9]*$/,
      PascalCase: /^[A-Z][a-zA-Z0-9]*$/,
      snake_case: /^[a-z][a-z0-9_]*$/,
      CONSTANT: /^[A-Z][A-Z0-9_]*$/,
    };

    let type = 'unknown';
    for (const [patternName, regex] of Object.entries(patterns)) {
      if (regex.test(functionName)) {
        type = patternName;
        break;
      }
    }

    // Extract common prefixes/suffixes
    const commonPrefixes = [
      'get',
      'set',
      'is',
      'has',
      'can',
      'should',
      'create',
      'build',
      'make',
      'handle',
      'process',
    ];
    const commonSuffixes = ['er', 'or', 'Handler', 'Manager', 'Service', 'Utils', 'Helper'];

    let prefix = '';
    let suffix = '';

    const lowerName = functionName.toLowerCase();
    commonPrefixes.forEach(p => {
      if (lowerName.startsWith(p.toLowerCase())) prefix = p;
    });

    commonSuffixes.forEach(s => {
      if (lowerName.endsWith(s.toLowerCase())) suffix = s;
    });

    return { type, prefix, suffix };
  }

  calculateFileSizeScore(fileSize) {
    // Prefer moderately sized files (2KB - 50KB)
    const kb = fileSize / 1024;

    if (kb >= 2 && kb <= 20) return 1.0; // Ideal size
    if (kb >= 1 && kb <= 50) return 0.8; // Good size
    if (kb >= 0.5 && kb <= 100) return 0.6; // Acceptable size
    if (kb < 0.5) return 0.4; // Very small (might be empty)
    return 0.2; // Too large
  }

  calculateConnectionMetrics(connections, totalOrphans) {
    this.results.connectionOpportunities = connections.length;

    if (connections.length > 0) {
      const totalConfidence = connections.reduce((sum, conn) => sum + conn.confidence, 0);
      this.results.averageConfidence = Math.round(totalConfidence / connections.length);

      const totalPreservationValue = connections.reduce(
        (sum, conn) => sum + conn.preservationValue,
        0
      );
      this.results.preservationRate = Math.round(
        (totalPreservationValue / (totalOrphans * 100)) * 100
      );
    } else {
      this.results.averageConfidence = 0;
      this.results.preservationRate = 0;
    }
  }

  async applyConnections(connections, options = {}) {
    if (this.options.dryRun) {
      console.log(i18n.t('strikeErrors.connectionEngine.dryRunMode'));
      return { applied: 0, failed: 0, dryRun: true };
    }

    console.log(i18n.t('strikeErrors.connectionEngine.applyingRecommendations'));

    let applied = 0;
    let failed = 0;

    for (const connection of connections) {
      try {
        await this.applyConnection(connection);
        applied++;
        console.log(
          i18n.t('strikeErrors.connectionEngine.connectionSuccessful', {
            orphan: connection.orphan,
            targetFile: connection.bestConnection.targetFile,
          })
        );
      } catch (error) {
        failed++;
        console.error(
          i18n.t('strikeErrors.connectionEngine.connectionFailed', {
            orphan: connection.orphan,
            message: error.message,
          })
        );
      }
    }

    this.results.appliedConnections = applied;
    this.results.failedConnections = failed;

    console.log(i18n.t('strikeErrors.connectionEngine.connectionResults', { applied, failed }));

    return { applied, failed, dryRun: false };
  }

  async applyConnection(connection) {
    const method = connection.bestConnection.implementationMethod;
    const targetFilePath = path.resolve(connection.bestConnection.targetFile);

    console.log(
      i18n.t('strikeErrors.connectionEngine.applyingConnection', {
        method,
        orphan: connection.orphan,
        targetFile: connection.bestConnection.targetFile,
      })
    );

    if (method === 'reference') {
      return await this.applyReferenceConnection(connection, targetFilePath);
    } else if (method === 'move') {
      return await this.applyMoveConnection(connection, targetFilePath);
    } else {
      throw new Error(i18n.t('strikeErrors.connectionEngine.unknownConnectionMethod', { method }));
    }
  }

  async applyReferenceConnection(connection, targetFilePath) {
    try {
      // For reference connections, we add import/require statements
      if (!fs.existsSync(targetFilePath)) {
        console.log(
          i18n.t('strikeErrors.connectionEngine.targetFileNotExists', { targetFilePath })
        );
        return {
          method: 'reference',
          target: targetFilePath,
          confidence: connection.confidence,
          applied: false,
          reason: i18n.t('strikeErrors.connectionEngine.targetFileNotFound'),
        };
      }

      const targetContent = fs.readFileSync(targetFilePath, 'utf8');
      const orphanName = connection.orphan;

      // Check if reference already exists
      const referencePatterns = [
        new RegExp(`import.*${orphanName}`, 'i'),
        new RegExp(`require.*${orphanName}`, 'i'),
        new RegExp(`${orphanName}\\s*\\(`, 'g'),
        new RegExp(`\\.${orphanName}\\s*\\(`, 'g'),
      ];

      const hasExistingReference = referencePatterns.some(pattern => pattern.test(targetContent));

      if (hasExistingReference) {
        console.log(
          i18n.t('strikeErrors.connectionEngine.referenceAlreadyExists', {
            orphanName,
            fileName: path.basename(targetFilePath),
          })
        );
        return {
          method: 'reference',
          target: targetFilePath,
          confidence: connection.confidence,
          applied: true,
          reason: i18n.t('strikeErrors.connectionEngine.referenceAlreadyExistsReason'),
        };
      }

      // Add a comment indicating the orphan function should be used from here
      const referenceComment = `\n// TODO: Consider using ${orphanName} function for related functionality\n`;
      const updatedContent = targetContent + referenceComment;

      // Create backup before modifying
      if (this.options.createBackups) {
        const backupPath = `${targetFilePath}.backup-${Date.now()}`;
        fs.writeFileSync(backupPath, targetContent);
      }

      fs.writeFileSync(targetFilePath, updatedContent);

      console.log(
        i18n.t('strikeErrors.connectionEngine.addedReferenceComment', {
          orphanName,
          fileName: path.basename(targetFilePath),
        })
      );

      return {
        method: 'reference',
        target: targetFilePath,
        confidence: connection.confidence,
        applied: true,
        reason: i18n.t('strikeErrors.connectionEngine.referenceCommentAdded'),
      };
    } catch (error) {
      console.error(
        i18n.t('strikeErrors.connectionEngine.referenceConnectionFailed', {
          message: error.message,
        })
      );
      throw error;
    }
  }

  async applyMoveConnection(connection, targetFilePath) {
    try {
      // For move connections, we would move the actual function code
      // This is a more complex operation that requires parsing the source

      console.log(
        i18n.t('strikeErrors.connectionEngine.moveConnectionInfo', {
          orphan: connection.orphan,
          fileName: path.basename(targetFilePath),
        })
      );
      console.log(i18n.t('strikeErrors.connectionEngine.moveConnectionNote'));

      if (!fs.existsSync(targetFilePath)) {
        console.log(
          i18n.t('strikeErrors.connectionEngine.targetFileNotExistsCreating', { targetFilePath })
        );
        const fileExtension = path.extname(targetFilePath);
        const initialContent = this.generateInitialFileContent(fileExtension);
        fs.writeFileSync(targetFilePath, initialContent);
      }

      const targetContent = fs.readFileSync(targetFilePath, 'utf8');
      const orphanName = connection.orphan;

      // Add integration placeholder
      const integrationComment = `\n// TODO: Integrate ${orphanName} function here (confidence: ${connection.confidence}%)\n// Reasoning: ${connection.bestConnection.reasoning[0]?.reason || i18n.t('strikeErrors.connectionEngine.highCompatibilityMatch')}\n`;
      const updatedContent = targetContent + integrationComment;

      // Create backup before modifying
      if (this.options.createBackups) {
        const backupPath = `${targetFilePath}.backup-${Date.now()}`;
        fs.writeFileSync(backupPath, targetContent);
      }

      fs.writeFileSync(targetFilePath, updatedContent);

      console.log(
        i18n.t('strikeErrors.connectionEngine.addedIntegrationPlaceholder', {
          orphanName,
          fileName: path.basename(targetFilePath),
        })
      );

      return {
        method: 'move',
        target: targetFilePath,
        confidence: connection.confidence,
        applied: true,
        reason: i18n.t('strikeErrors.connectionEngine.integrationPlaceholderAdded'),
      };
    } catch (error) {
      console.error(
        i18n.t('strikeErrors.connectionEngine.moveConnectionFailed', { message: error.message })
      );
      throw error;
    }
  }

  generateInitialFileContent(fileExtension) {
    const timestamp = new Date().toISOString();
    const header = `/**\n * Generated by Strike Errors Connection Engine\n * Created: ${timestamp}\n */\n\n`;

    switch (fileExtension) {
      case '.js':
        return (
          header +
          '// JavaScript utilities\n\nmodule.exports = {\n    // Functions will be added here\n};\n'
        );
      case '.py':
        return header.replace(/\/\*/g, '"""').replace(/\*\//g, '"""') + '# Python utilities\n\n';
      case '.html':
        return `<!DOCTYPE html>\n<html>\n<head>\n    <title>Generated HTML</title>\n</head>\n<body>\n    <!-- Content will be added here -->\n</body>\n</html>\n`;
      case '.css':
        return header.replace(/\/\*/g, '/*').replace(/\*\//g, '*/') + '/* CSS styles */\n\n';
      default:
        return header + '// Generated file\n\n';
    }
  }
}

module.exports = {
  IntelligentConnectionEngine,
  analyzeConnections: async (orphans, targetDirectory, options = {}) => {
    const engine = new IntelligentConnectionEngine(options);
    return await engine.analyzeConnections(orphans, targetDirectory, options);
  },
};

