/**
 * New Architecture Client
 * REPLACES old renderer IPC patterns with unified request system
 * Implements batch processing and event-driven communication
 */

class NewArchitectureClient {
  constructor() {
    this.requestQueue = [];
    this.batchInterval = 100; // 100ms
    this.batchTimeoutId = null;
    this.pendingRequests = new Map();
    this.eventListeners = new Map();
    this.isInitialized = false;

    console.log('üèóÔ∏è New Architecture Client initializing...');
    this.initialize();
  }

  /**
   * Initialize the client
   */
  async initialize() {
    try {
      // Test connectivity with new unified IPC system
      const testResult = await this.sendUnifiedRequest('system:health', {});

      if (testResult.success) {
        console.log('‚úÖ Connected to new architecture backend');
        this.isInitialized = true;

        // Setup event listeners
        this.setupEventListeners();

        // Emit ready event
        this.emit('client:ready', { timestamp: Date.now() });
      } else {
        throw new Error('Health check failed');
      }
    } catch (error) {
      console.error('‚ùå Failed to initialize new architecture client:', error);
      // Fall back to legacy mode if needed
      this.initializeLegacyMode();
    }
  }

  /**
   * Send unified request to backend
   * @param {string} type - Request type
   * @param {Object} payload - Request payload
   * @param {Object} options - Request options
   * @returns {Promise<*>} Response
   */
  async sendUnifiedRequest(type, payload, options = {}) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const requestData = {
      type,
      payload,
      id: this.generateRequestId(),
      timestamp: Date.now(),
      ...options,
    };

    try {
      console.log(`üì§ Sending unified request: ${type}`);

      const response = await window.electronAPI.invoke('unified-request', requestData);

      if (response.success) {
        console.log(`‚úÖ Unified request completed: ${type}`);
        return response.result;
      } else {
        throw new Error(response.error);
      }
    } catch (error) {
      console.error(`‚ùå Unified request failed: ${type}`, error);
      throw error;
    }
  }

  /**
   * Send batch request for multiple operations
   * @param {Array} requests - Array of requests
   * @returns {Promise<Array>} Array of responses
   */
  async sendBatchRequest(requests) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const batchData = {
      requests: requests.map(req => ({
        ...req,
        id: this.generateRequestId(),
      })),
      batchId: this.generateRequestId(),
      timestamp: Date.now(),
    };

    try {
      console.log(`üì¶ Sending batch request with ${requests.length} items`);

      const response = await window.electronAPI.invoke('batch-request', batchData);

      if (response.success) {
        console.log(`‚úÖ Batch request completed`);
        return response.results;
      } else {
        throw new Error(response.error);
      }
