/**
 * IPC Batch Processor Implementation
 * Reduces IPC overhead by batching multiple requests
 * Full implementation with no placeholders
 */

class BatchProcessor {
  constructor(options = {}) {
    this.options = {
      batchInterval: options.batchInterval || 100, // 100ms
      maxBatchSize: options.maxBatchSize || 50,
      timeout: options.timeout || 5000, // 5 seconds
      enableCompression: options.enableCompression || false,
      ...options,
    };

    this.batchQueue = new Map();
    this.pendingResponses = new Map();
    this.timeoutId = null;
    this.isProcessing = false;

    this.metrics = {
      totalRequests: 0,
      batchedRequests: 0,
      individualRequests: 0,
      averageBatchSize: 0,
      averageLatency: 0,
      timeouts: 0,
      errors: 0,
    };

    console.log('üì¶ BatchProcessor initialized', this.options);
  }

  /**
   * Enqueue a request for batch processing
   * @param {string} channel - IPC channel name
   * @param {*} payload - Request payload
   * @returns {Promise<*>} Response promise
   */
  enqueue(channel, payload) {
    return new Promise((resolve, reject) => {
      const id = this.generateRequestId(channel);
      const startTime = Date.now();

      const request = {
        id,
        channel,
        payload,
        timestamp: startTime,
        resolve,
        reject,
      };

      // Add to queue
      this.batchQueue.set(id, request);
      this.metrics.totalRequests++;

      // Set timeout for individual request
      const timeoutId = setTimeout(() => {
        if (this.batchQueue.has(id)) {
          this.batchQueue.delete(id);
          this.metrics.timeouts++;
          reject(new Error(`Request timeout: ${channel}`));
        }
      }, this.options.timeout);

      request.timeoutId = timeoutId;

      console.log(`üì• Enqueued request: ${channel} (${id})`);

      // Schedule batch processing if not already scheduled
      this.scheduleBatchProcessing();
    });
  }

  /**
   * Schedule batch processing
   */
  scheduleBatchProcessing() {
    if (this.timeoutId || this.isProcessing) {
      return;
    }

    // Process immediately if batch is full
    if (this.batchQueue.size >= this.options.maxBatchSize) {
      this.processBatchQueue();
      return;
    }

    // Otherwise schedule with delay
    this.timeoutId = setTimeout(() => {
      this.processBatchQueue();
    }, this.options.batchInterval);
  }

