      };
    }

    // Try global store instance
    if (window.store) {
      return {
        shapes: window.store.getCurrentPageShapes?.() || window.store.getAllShapes?.() || [],
        selection: window.store.getSelectedShapes?.() || [],
        currentTool: window.store.getCurrentTool?.() || 'select',
        camera: window.store.getCurrentCamera?.() || { x: 0, y: 0, z: 1 },
        timestamp: Date.now(),
        source: 'global-store'
      };
    }

    // Fallback: empty state
    console.warn('‚ö†Ô∏è No canvas store found, returning empty state');
    return {
      shapes: [],
      selection: [],
      currentTool: 'select',
      camera: { x: 0, y: 0, z: 1 },
      timestamp: Date.now(),
      source: 'fallback'
    };

  } catch (error) {
    console.error('‚ùå Error getting canvas state:', error);
    throw error;
  }
}

/**
 * Setup canvas event forwarding to backend for Strike/AI analysis
 */
setupCanvasEventForwarding() {
  console.log('üîÑ Setting up canvas event forwarding...');

  // Forward significant canvas events to backend
  const eventsToForward = [
    'shape:created',
    'shape:updated', 
    'shape:deleted',
    'selection:changed',
    'tool:changed',
    'camera:moved'
  ];

  // Try to hook into various event systems
  try {
    // Hook into global event bus if available
    if (window.globalEventBus?.on) {
      eventsToForward.forEach(eventType => {
        window.globalEventBus.on(eventType, (data) => {
          this.forwardCanvasEvent(eventType, data);
        });
      });
      console.log('‚úÖ Hooked into globalEventBus for canvas events');
    }

    // Hook into TLDraw store events if available
    if (window.tlEditor?.store?.on) {
      window.tlEditor.store.on('change', (change) => {
        this.forwardCanvasEvent('store:change', {
          added: change.added,
          updated: change.updated,
          removed: change.removed
        });
      });
      console.log('‚úÖ Hooked into TLDraw store events');
    }

    // Periodic canvas state sync for Strike/AI
    this.startCanvasStateSync();

  } catch (error) {
    console.error('‚ùå Failed to setup canvas event forwarding:', error);
  }
}

/**
 * Forward canvas event to backend for Strike/AI processing
 */
async forwardCanvasEvent(eventType, data) {
  try {
    const result = await window.electronAPI?.invoke?.('forward-canvas-event', {
      type: eventType,
      data: data,
      timestamp: Date.now(),
      source: 'frontend-bridge'
    });

    if (result?.success) {
      console.log(`üåâ Event forwarded: ${eventType}`);
    } else {
      console.warn(`‚ö†Ô∏è Event forward failed: ${eventType}`, result);
    }
  } catch (error) {
    console.error(`‚ùå Failed to forward event ${eventType}:`, error);
  }
}

/**
 * Start periodic canvas state sync for Strike/AI systems
 */
startCanvasStateSync() {
  // Sync canvas state every 5 seconds for Strike/AI analysis
  setInterval(() => {
    try {
      const canvasState = this.getCurrentCanvasState();

      // Only sync if there are shapes or active interactions
      if (canvasState.shapes.length > 0 || canvasState.selection.length > 0 || canvasState.currentTool !== 'select') {
        this.forwardCanvasEvent('canvas:sync', {
          summary: {
            shapeCount: canvasState.shapes.length,
            selectionCount: canvasState.selection.length,
            currentTool: canvasState.currentTool,
            cameraZoom: canvasState.camera.z
          },
          lastSync: Date.now()
        });
      }
    } catch (error) {
      // Silent fail - don't spam console with sync errors
    }
  }, 5000);

  console.log('‚è∞ Canvas state sync started (5s interval)');
}

