/**
 * AI PATH INTELLIGENCE
 *
 * Uses AI to intelligently identify correct file paths from uploaded directories
 * Connected to terminal for real-time path resolution and validation
 */

const fs = require('fs');
const path = require('path');
const i18n = require('../../i18n');

class AIPathIntelligence {
  constructor(options = {}) {
    this.options = {
      terminalIntegration: options.terminalIntegration !== false,
      enableLearning: options.enableLearning !== false,
      confidenceThreshold: options.confidenceThreshold || 75,
      maxSuggestions: options.maxSuggestions || 5,
    };

    // AI knowledge base for path patterns
    this.pathKnowledge = this.initializePathKnowledge();
    this.uploadedDirectories = new Map();
    this.pathHistory = [];
    this.terminalConnection = null;

    console.log(i18n.t('strikeErrors.aiPathIntelligence.initialized'));
  }

  initializePathKnowledge() {
    return {
      // Framework-specific patterns
      frameworks: {
        react: {
          patterns: ['src/', 'components/', 'hooks/', 'utils/', 'services/'],
          fileTypes: ['.jsx', '.tsx', '.js', '.ts'],
          confidence: 0.9,
        },
        node: {
          patterns: ['src/', 'lib/', 'routes/', 'middleware/', 'controllers/'],
          fileTypes: ['.js', '.ts', '.mjs'],
          confidence: 0.9,
        },
        python: {
          patterns: ['src/', 'lib/', 'modules/', 'packages/', 'tests/'],
          fileTypes: ['.py', '.pyx', '.pyi'],
          confidence: 0.85,
        },
        web: {
          patterns: ['public/', 'assets/', 'static/', 'dist/', 'build/'],
          fileTypes: ['.html', '.css', '.js', '.scss', '.less'],
          confidence: 0.8,
        },
      },

      // File purpose patterns
      purposes: {
        utils: {
          keywords: ['util', 'helper', 'common', 'shared', 'tool'],
          typical_locations: ['utils/', 'helpers/', 'lib/', 'common/'],
          confidence: 0.85,
        },
        components: {
          keywords: ['component', 'widget', 'ui', 'view'],
          typical_locations: ['components/', 'widgets/', 'ui/', 'views/'],
          confidence: 0.9,
        },
        config: {
          keywords: ['config', 'settings', 'env', 'constant'],
          typical_locations: ['config/', 'settings/', 'constants/', 'env/'],
          confidence: 0.95,
        },
        tests: {
          keywords: ['test', 'spec', 'mock', '__tests__'],
          typical_locations: ['tests/', 'test/', '__tests__/', 'spec/'],
          confidence: 0.9,
        },
      },

      // Anti-patterns (files to avoid)
      antiPatterns: [
        'node_modules/',
        '.git/',
        'dist/',
        'build/',
        '.cache/',
        'coverage/',
        '.nyc_output/',
        'temp/',
        'tmp/',
      ],
    };
  }

  async connectTerminal(terminalConnection) {
    this.terminalConnection = terminalConnection;
    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.connectedToTerminal'));

    // Set up real-time learning from terminal commands
    if (this.options.enableLearning) {
      this.setupTerminalLearning();
    }

    return true;
  }

  setupTerminalLearning() {
    if (!this.terminalConnection) return;

    // Learn from successful file operations in terminal
    this.terminalConnection.on('command-success', (command, output) => {
      this.learnFromTerminalCommand(command, output);
    });

    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.terminalLearningEnabled'));
  }

  learnFromTerminalCommand(command, output) {
    // Extract path patterns from successful terminal commands
    const pathPatterns = this.extractPathPatternsFromCommand(command);

    if (pathPatterns.length > 0) {
      pathPatterns.forEach(pattern => {
        this.pathHistory.push({
          pattern,
          command,
          timestamp: Date.now(),
          success: true,
        });
      });

      // Limit history to last 100 entries
      if (this.pathHistory.length > 100) {
        this.pathHistory = this.pathHistory.slice(-100);
      }
    }
  }

  extractPathPatternsFromCommand(command) {
    const patterns = [];

    // Extract file paths from common commands
    const pathRegex = /[^\s'"]+\.[a-zA-Z0-9]{1,4}/g;
    const matches = command.match(pathRegex);

    if (matches) {
      matches.forEach(match => {
        const dir = path.dirname(match);
        if (dir && dir !== '.') {
          patterns.push(dir);
        }
      });
    }

    return patterns;
  }

  async analyzeUploadedDirectory(directoryPath, files) {
    this.logToTerminal(
      i18n.t('strikeErrors.aiPathIntelligence.analyzingDirectory', {
        directoryName: path.basename(directoryPath),
      })
    );

    const analysis = {
      directoryPath,
      totalFiles: files.length,
      framework: await this.detectFramework(files),
      structure: this.analyzeDirectoryStructure(files),
      recommendations: [],
      pathMappings: new Map(),
      confidence: 0,
    };

    // Analyze file types and patterns
    const fileAnalysis = this.analyzeFileTypes(files);
    analysis.fileTypes = fileAnalysis.types;
    analysis.confidence = fileAnalysis.confidence;

    // Generate intelligent path mappings
    analysis.pathMappings = await this.generatePathMappings(files, analysis.framework);

    // Create recommendations
    analysis.recommendations = this.generateRecommendations(analysis);

    // Store for future reference
    this.uploadedDirectories.set(directoryPath, analysis);

    this.logToTerminal(
      i18n.t('strikeErrors.aiPathIntelligence.analysisComplete', {
        frameworkName: analysis.framework.name,
        confidence: analysis.confidence,
      })
    );

    return analysis;
  }

  async detectFramework(files) {
    const indicators = {
      react: 0,
      node: 0,
      python: 0,
      web: 0,
    };

    files.forEach(file => {
      const filename = path.basename(file);
      const ext = path.extname(file);
      const dir = path.dirname(file);

      // React indicators
      if (
        filename === 'package.json' ||
        filename.includes('react') ||
        ext === '.jsx' ||
        ext === '.tsx'
      ) {
        indicators.react += 2;
      }

      // Node.js indicators
      if (filename === 'package.json' || filename === 'server.js' || filename === 'app.js') {
        indicators.node += 2;
      }

      // Python indicators
      if (ext === '.py' || filename === 'requirements.txt' || filename === 'setup.py') {
        indicators.python += 2;
      }

      // Web indicators
      if (ext === '.html' || ext === '.css' || dir.includes('public') || dir.includes('assets')) {
        indicators.web += 1;
      }
    });

    const maxFramework = Object.keys(indicators).reduce((a, b) =>
      indicators[a] > indicators[b] ? a : b
    );

    return {
      name: maxFramework,
      confidence: (indicators[maxFramework] / files.length) * 100,
      indicators,
    };
  }

  analyzeDirectoryStructure(files) {
    const structure = {
      depth: 0,
      commonPaths: new Map(),
      organizationScore: 0,
    };

    files.forEach(file => {
      const parts = file.split(path.sep);
      structure.depth = Math.max(structure.depth, parts.length);

      // Count common directory patterns
      parts.forEach((part, index) => {
        if (index < parts.length - 1) {
          // Not the filename
          const pathKey = parts.slice(0, index + 1).join('/');
          structure.commonPaths.set(pathKey, (structure.commonPaths.get(pathKey) || 0) + 1);
        }
      });
    });

    // Calculate organization score based on common patterns
    const knownPatterns = [
      'src',
      'lib',
      'components',
      'utils',
      'services',
      'public',
      'assets',
      'tests',
      'config',
    ];

    let patternMatches = 0;
    structure.commonPaths.forEach((count, pathKey) => {
      const parts = pathKey.split('/');
      if (parts.some(part => knownPatterns.includes(part.toLowerCase()))) {
        patternMatches += count;
      }
    });

    structure.organizationScore = Math.min(100, (patternMatches / files.length) * 100);

    return structure;
  }

  analyzeFileTypes(files) {
    const types = new Map();
    let totalConfidence = 0;

    files.forEach(file => {
      const ext = path.extname(file).toLowerCase();
      types.set(ext, (types.get(ext) || 0) + 1);
    });

    // Calculate confidence based on file type consistency
    const dominantTypes = Array.from(types.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3);

    const totalFiles = files.length;
    const dominantCount = dominantTypes.reduce((sum, [, count]) => sum + count, 0);

    totalConfidence = (dominantCount / totalFiles) * 100;

    return {
      types: Object.fromEntries(types),
      confidence: totalConfidence,
    };
  }

  async generatePathMappings(files, framework) {
    const mappings = new Map();

    // Create intelligent mappings based on framework and file analysis
    files.forEach(file => {
      const filename = path.basename(file);
      const directory = path.dirname(file);
      const ext = path.extname(file);

      // Generate mapping confidence score
      let confidence = this.calculatePathConfidence(file, framework);

      // Check against anti-patterns
      const isAntiPattern = this.pathKnowledge.antiPatterns.some(pattern => file.includes(pattern));

      if (isAntiPattern) {
        confidence *= 0.1; // Heavily penalize anti-patterns
      }

      mappings.set(file, {
        originalPath: file,
        confidence,
        framework: framework.name,
        purpose: this.detectFilePurpose(filename, directory),
        recommended: confidence >= this.options.confidenceThreshold,
      });
    });

    return mappings;
  }

  calculatePathConfidence(filePath, framework) {
    let confidence = 50; // Base confidence

    const parts = filePath.split(path.sep);
    const filename = path.basename(filePath);
    const ext = path.extname(filePath);

    // Framework-specific boost
    const frameworkInfo = this.pathKnowledge.frameworks[framework.name];
    if (frameworkInfo) {
      // Check file extension match
      if (frameworkInfo.fileTypes.includes(ext)) {
        confidence += 20;
      }

      // Check directory pattern match
      const matchingPatterns = frameworkInfo.patterns.filter(pattern => filePath.includes(pattern));
      confidence += matchingPatterns.length * 15;
    }

    // Purpose-based boost
    const purpose = this.detectFilePurpose(filename, path.dirname(filePath));
    if (purpose) {
      confidence += 10;
    }

    // Depth penalty (too deep = likely build/temp files)
    if (parts.length > 6) {
      confidence -= (parts.length - 6) * 5;
    }

    // Common file patterns boost
    if (filename.match(/\.(config|settings|env|constants?)\./)) {
      confidence += 15;
    }

    return Math.max(0, Math.min(100, confidence));
  }

  detectFilePurpose(filename, directory) {
    const lowerFilename = filename.toLowerCase();
    const lowerDirectory = directory.toLowerCase();

    for (const [purpose, info] of Object.entries(this.pathKnowledge.purposes)) {
      // Check filename keywords
      const filenameMatch = info.keywords.some(keyword => lowerFilename.includes(keyword));

      // Check directory location
      const locationMatch = info.typical_locations.some(location =>
        lowerDirectory.includes(location.replace('/', ''))
      );

      if (filenameMatch || locationMatch) {
        return {
          type: purpose,
          confidence: info.confidence * (filenameMatch && locationMatch ? 1 : 0.7),
        };
      }
    }

    return null;
  }

  generateRecommendations(analysis) {
    const recommendations = [];

    // Framework-specific recommendations
    if (analysis.framework.confidence < 70) {
      recommendations.push({
        type: 'framework',
        priority: 'medium',
        message: i18n.t('strikeErrors.aiPathIntelligence.frameworkUncertain', {
          confidence: analysis.framework.confidence,
        }),
      });
    }

    // Organization recommendations
    if (analysis.structure.organizationScore < 50) {
      recommendations.push({
        type: 'organization',
        priority: 'high',
        message: i18n.t('strikeErrors.aiPathIntelligence.directoryDisorganized'),
      });
    }

    // File count recommendations
    if (analysis.totalFiles > 1000) {
      recommendations.push({
        type: 'performance',
        priority: 'medium',
        message: i18n.t('strikeErrors.aiPathIntelligence.largeProject', {
          totalFiles: analysis.totalFiles,
        }),
      });
    }

    // Path mapping recommendations
    const lowConfidencePaths = Array.from(analysis.pathMappings.values()).filter(
      mapping => mapping.confidence < this.options.confidenceThreshold
    ).length;

    if (lowConfidencePaths > analysis.totalFiles * 0.3) {
      recommendations.push({
        type: 'paths',
        priority: 'high',
        message: i18n.t('strikeErrors.aiPathIntelligence.lowConfidencePaths', {
          lowConfidencePaths,
        }),
      });
    }

    return recommendations;
  }

  async intelligentPathResolution(targetPath, context = {}) {
    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.resolvingPath', { targetPath }));

    const suggestions = [];

    // Check all uploaded directories for potential matches
    for (const [dirPath, analysis] of this.uploadedDirectories) {
      const matches = await this.findPathMatches(targetPath, analysis, context);
      suggestions.push(...matches);
    }

    // Sort by confidence and return top suggestions
    suggestions.sort((a, b) => b.confidence - a.confidence);

    const topSuggestions = suggestions.slice(0, this.options.maxSuggestions);

    this.logToTerminal(
      i18n.t('strikeErrors.aiPathIntelligence.foundSuggestions', { count: topSuggestions.length })
    );

    return {
      originalPath: targetPath,
      suggestions: topSuggestions,
      bestMatch: topSuggestions[0] || null,
      confidence: topSuggestions[0]?.confidence || 0,
    };
  }

  async findPathMatches(targetPath, analysis, context) {
    const matches = [];
    const targetBasename = path.basename(targetPath);
    const targetExt = path.extname(targetPath);

    // Search through all mapped paths
    for (const [filePath, mapping] of analysis.pathMappings) {
      const fileBasename = path.basename(filePath);
      const fileExt = path.extname(filePath);

      let matchConfidence = 0;

      // Exact filename match
      if (fileBasename === targetBasename) {
        matchConfidence = 95;
      }
      // Extension and similar name
      else if (
        fileExt === targetExt &&
        this.calculateStringSimilarity(fileBasename, targetBasename) > 0.7
      ) {
        matchConfidence = 80;
      }
      // Extension match only
      else if (fileExt === targetExt) {
        matchConfidence = 40;
      }
      // Similar name, different extension
      else if (this.calculateStringSimilarity(fileBasename, targetBasename) > 0.8) {
        matchConfidence = 60;
      }

      // Apply context boost
      if (context.framework && context.framework === analysis.framework.name) {
        matchConfidence += 10;
      }

      // Apply mapping confidence
      matchConfidence = (matchConfidence * mapping.confidence) / 100;

      if (matchConfidence >= 30) {
        // Minimum viable confidence
        matches.push({
          originalPath: targetPath,
          resolvedPath: filePath,
          confidence: Math.round(matchConfidence),
          reasoning: this.generateMatchReasoning(targetPath, filePath, matchConfidence, mapping),
          framework: analysis.framework.name,
          purpose: mapping.purpose,
        });
      }
    }

    return matches;
  }

  calculateStringSimilarity(str1, str2) {
    // Simple Levenshtein distance-based similarity
    const longer = str1.length > str2.length ? str1 : str2;
    const shorter = str1.length > str2.length ? str2 : str1;

    if (longer.length === 0) return 1.0;

    const distance = this.levenshteinDistance(longer, shorter);
    return (longer.length - distance) / longer.length;
  }

  levenshteinDistance(str1, str2) {
    const matrix = [];

    for (let i = 0; i <= str2.length; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= str1.length; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= str2.length; i++) {
      for (let j = 1; j <= str1.length; j++) {
        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
          matrix[i][j] = matrix[i - 1][j - 1];
        } else {
          matrix[i][j] = Math.min(
            matrix[i - 1][j - 1] + 1,
            matrix[i][j - 1] + 1,
            matrix[i - 1][j] + 1
          );
        }
      }
    }

    return matrix[str2.length][str1.length];
  }

  generateMatchReasoning(targetPath, resolvedPath, confidence, mapping) {
    const reasons = [];

    if (path.basename(targetPath) === path.basename(resolvedPath)) {
      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.exactFilenameMatch'));
    }

    if (path.extname(targetPath) === path.extname(resolvedPath)) {
      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.sameFileExtension'));
    }

    if (mapping.purpose) {
      reasons.push(
        i18n.t('strikeErrors.aiPathIntelligence.matchesPattern', {
          purposeType: mapping.purpose.type,
        })
      );
    }

    if (confidence > 80) {
      reasons.push(i18n.t('strikeErrors.aiPathIntelligence.highConfidenceMatch'));
    }

    return reasons.join(', ');
  }

  logToTerminal(message) {
    const timestamp = new Date().toLocaleTimeString();
    const logMessage = `[${timestamp}] [AI Path Intelligence] ${message}`;

    if (this.terminalConnection && typeof this.terminalConnection.write === 'function') {
      this.terminalConnection.write(logMessage + '\r\n');
    } else if (this.terminalConnection && typeof this.terminalConnection.send === 'function') {
      this.terminalConnection.send(logMessage);
    }

    console.log(logMessage);
  }

  // Public API methods
  getAnalysisFor(directoryPath) {
    return this.uploadedDirectories.get(directoryPath);
  }

  getAllAnalyses() {
    return Array.from(this.uploadedDirectories.values());
  }

  clearAnalyses() {
    this.uploadedDirectories.clear();
    this.pathHistory = [];
    this.logToTerminal(i18n.t('strikeErrors.aiPathIntelligence.pathIntelligenceCleared'));
  }

  getPathHistory() {
    return this.pathHistory.slice(-20); // Return last 20 entries
  }

  updateThreshold(newThreshold) {
    this.options.confidenceThreshold = Math.max(0, Math.min(100, newThreshold));
    this.logToTerminal(
      i18n.t('strikeErrors.aiPathIntelligence.thresholdUpdated', {
        threshold: this.options.confidenceThreshold,
      })
    );
  }
}

module.exports = {
  AIPathIntelligence,
  createAIPathIntelligence: (options = {}) => new AIPathIntelligence(options),
};

