  /**
   * Generate DOT file for targeted dependency graph
   */
  async generateDotFile(outputPath) {
    const lines = [
      'digraph TargetedFunctionGraph {',
      '  rankdir=TB;',
      '  node [shape=box, style="rounded,filled", fontname="Consolas", fontsize=10];',
      '  edge [fontname="Consolas", fontsize=8, arrowsize=0.7];',
      '',
      '  // Target Functions (highlighted)',
      '  subgraph cluster_targets {'
    ];

    // Add target function nodes
    for (const target of this.analysisResults.targetedFunctions) {
      const nodeId = this.sanitizeNodeId(target.fullIdentifier || `${target.name}@${target.filePath}`);
      const label = `${target.name}\\n${this.getRelativePath(target.filePath)}:${target.line || '?'}`;

      lines.push(`    ${nodeId} [label="${label}", fillcolor="#ffcccc", style="rounded,filled,bold"];`);
    }

    lines.push('  }');
    lines.push('');

    // Add dependency nodes and edges
    for (const [id, node] of this.dependencyGraph) {
      const nodeId = this.sanitizeNodeId(id);
      const func = node.function;
      const label = `${func.name}\\n${this.getRelativePath(func.filePath)}:${func.line || '?'}`;

      // Color coding based on function type/status
      let fillColor = '#e8f4fd'; // Default blue
      if (this.isTargetFunction(func)) {
        fillColor = '#ffcccc'; // Target functions - red
      } else if (this.isDuplicateFunction(func)) {
        fillColor = '#fff2cc'; // Duplicates - yellow
      } else if (this.isRecommendedForRemoval(func)) {
        fillColor = '#ffcccc'; // To remove - red
      }

      lines.push(`  ${nodeId} [label="${label}", fillcolor="${fillColor}"];`);

      // Add dependency edges
      for (const depId of node.dependencies) {
        const depNodeId = this.sanitizeNodeId(depId);
        lines.push(`  ${nodeId} -> ${depNodeId};`);
      }
    }

    lines.push('}');

    fs.writeFileSync(outputPath, lines.join('\n'));
    this.log(`üìä DOT file generated: ${outputPath}`);
  }

  /**
   * Generate SVG from DOT file
   */
  async generateSVGFile(dotPath, svgPath) {
    try {
      const { spawn } = require('child_process');

      return new Promise((resolve, reject) => {
        const dot = spawn('dot', ['-Tsvg', dotPath, '-o', svgPath]);

        dot.on('close', (code) => {
          if (code === 0) {
            this.log(`üé® SVG generated: ${svgPath}`);
            resolve(svgPath);
          } else {
            reject(new Error(`Graphviz dot failed with code ${code}`));
          }
        });

        dot.on('error', (error) => {
          this.log(`‚ö†Ô∏è Graphviz not available, skipping SVG generation: ${error.message}`);
          resolve(null);
        });
      });
    } catch (error) {
      this.log(`‚ö†Ô∏è Could not generate SVG: ${error.message}`);
      return null;
    }
  }

