      `- **Optimization Recommendations:** ${this.analysisResults.recommendations.length}`,
      '',
      '## TARGETED FUNCTION ANALYSIS',
      ''
    ];

    // Target function details
    for (const target of this.analysisResults.targetedFunctions) {
      lines.push(`### ${target.name} (${this.getRelativePath(target.filePath)})`);
      lines.push('');
      lines.push(`- **Location:** ${target.filePath}:${target.line || '?'}`);
      lines.push(`- **Direct Dependencies:** ${target.directDependencies || 0}`);
      lines.push(`- **Analysis Depth:** ${target.depth}`);

      if (target.dependencies && target.dependencies.length > 0) {
        lines.push('- **Key Dependencies:**');
        for (const dep of target.dependencies.slice(0, 5)) {
          lines.push(`  - ${dep.name} (${this.getRelativePath(dep.filePath)})`);
        }
      }
      lines.push('');
    }

    // Duplicates analysis
    if (this.analysisResults.duplicates.length > 0) {
      lines.push('## DUPLICATE/COMPETING IMPLEMENTATIONS');
      lines.push('');

      for (const dup of this.analysisResults.duplicates) {
        lines.push(`### ${dup.functionName}`);
        lines.push('');
        lines.push(`**Recommended Implementation:** ${this.getRelativePath(dup.recommended.filePath)} (score: ${dup.recommended.score.total})`);
        lines.push('');
        lines.push('**Implementations to Remove:**');
        for (const impl of dup.toRemove) {
          lines.push(`- ${this.getRelativePath(impl.filePath)} (score: ${impl.score.total}) - ${impl.score.reason}`);
        }
        lines.push('');
        lines.push('**Reasoning:**');
        for (const reason of dup.reasoning) {
          lines.push(`- ${reason}`);
        }
        lines.push('');
      }
    }

    // Optimization recommendations
    lines.push('## OPTIMIZATION RECOMMENDATIONS');
    lines.push('');

    const recommendations = this.analysisResults.recommendations.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
    });

    for (const rec of recommendations) {
      lines.push(`### ${rec.type.toUpperCase()}: ${rec.function}`);
      lines.push('');
      lines.push(`- **File:** ${this.getRelativePath(rec.file)}`);
      lines.push(`- **Action:** ${rec.action.toUpperCase()}`);
      lines.push(`- **Priority:** ${rec.priority.toUpperCase()}`);
      lines.push(`- **Effort:** ${rec.estimated_effort}`);
      if (rec.confidence) {
        lines.push(`- **Confidence:** ${rec.confidence}%`);
      }
      lines.push('');
      lines.push(`**Details:** ${rec.details}`);
      lines.push('');
    }

    // Implementation checklist
    lines.push('## IMPLEMENTATION CHECKLIST');
    lines.push('');
    lines.push('### High Priority Actions');
    const highPriority = recommendations.filter(r => r.priority === 'high');
    for (const rec of highPriority) {
      lines.push(`- [ ] ${rec.action.toUpperCase()}: ${rec.function} in ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    lines.push('### Medium Priority Actions');
    const mediumPriority = recommendations.filter(r => r.priority === 'medium');
    for (const rec of mediumPriority) {
      lines.push(`- [ ] ${rec.action.toUpperCase()}: ${rec.function} in ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    lines.push('### Files to Remove');
    const toRemove = recommendations.filter(r => r.action === 'remove');
    for (const rec of toRemove) {
      lines.push(`- [ ] DELETE: ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    // Statistics
    lines.push('## ANALYSIS STATISTICS');
    lines.push('');
    lines.push(`- **Total Functions Scanned:** ${this.functionMap.size}`);
    lines.push(`- **Dependency Graph Nodes:** ${this.dependencyGraph.size}`);
    lines.push(`- **Functions to Remove:** ${toRemove.length}`);
    lines.push(`- **Functions to Refactor:** ${recommendations.filter(r => r.action === 'refactor').length}`);
    lines.push(`- **Missing Connections:** ${recommendations.filter(r => r.action === 'connect').length}`);

    fs.writeFileSync(outputPath, lines.join('\n'));
    this.log(`üìã Actionable report generated: ${outputPath}`);
  }

  // Helper methods
  async getJavaScriptFiles() {
    const { glob } = require('glob');
    const files = [];

    for (const pattern of this.options.includePaths) {
      const matches = await glob(pattern, {
        cwd: this.options.projectRoot,
        ignore: this.options.excludePaths,
        absolute: true
      });
      files.push(...matches);
    }

    return [...new Set(files)].filter(f => f.endsWith('.js') && fs.existsSync(f));
  }

  async extractFunctions(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const ast = acorn.parse(content, { 
        ecmaVersion: 2020, 
        sourceType: 'module',
        locations: true 
      });

      const functions = [];

      this.walkAST(ast, (node) => {
        if (node.type === 'FunctionDeclaration' || 
            node.type === 'FunctionExpression' ||
            node.type === 'ArrowFunctionExpression') {

          let name = 'anonymous';
          if (node.id && node.id.name) {
            name = node.id.name;
          } else if (node.type === 'FunctionExpression' || node.type === 'ArrowFunctionExpression') {
            // Try to get name from parent assignment
            name = this.getAssignmentName(node) || 'anonymous';
          }

          functions.push({
            name,
            type: node.type,
            line: node.loc?.start?.line,
            column: node.loc?.start?.column,
            params: node.params?.length || 0,
            async: node.async || false,
            generator: node.generator || false
          });
        }
      });

      return functions;
    } catch (error) {
      this.log(`‚ö†Ô∏è Error extracting functions from ${filePath}: ${error.message}`);
      return [];
    }
  }

  walkAST(node, callback) {
    if (!node || typeof node !== 'object') return;

    callback(node);

    for (const key in node) {
      if (key === 'parent') continue;
      const child = node[key];
      if (Array.isArray(child)) {
        child.forEach(item => this.walkAST(item, callback));
      } else if (child && typeof child === 'object') {
        this.walkAST(child, callback);
      }
    }
  }

  getCalleeIdentifier(callee) {
    if (callee.type === 'Identifier') {
      return callee.name;
    } else if (callee.type === 'MemberExpression') {
      return callee.property.name;
    }
    return null;
  }

  getAssignmentName(node) {
    // This would need more sophisticated logic to handle various assignment patterns
    return 'anonymous';
  }

  sanitizeNodeId(id) {
    return id.replace(/[^a-zA-Z0-9_]/g, '_');
  }

  getRelativePath(filePath) {
    return path.relative(this.options.projectRoot, filePath);
  }

  isTargetFunction(func) {
    return this.options.targetFunctions.includes(func.name);
  }

  isDuplicateFunction(func) {
    return this.duplicateMap.has(func.name);
  }

  isRecommendedForRemoval(func) {
    const duplicate = this.duplicateMap.get(func.name);
    if (!duplicate) return false;
    return duplicate.toRemove.some(impl => impl.filePath === func.filePath);
  }

  isCircularDependency(func1, func2) {
    // Simple circular dependency check - could be enhanced
    return func1.filePath === func2.filePath && func1.name === func2.name;
  }

  findMissingDependencies(analysis) {
    // Placeholder - would analyze function calls vs. available functions
    return [];
  }

  findCircularDependencies(analysis) {
    // Placeholder - would detect circular dependency chains
    return [];
  }

  isUnusedFunction(analysis) {
    // Check if function is called by any other function in the dependency graph
    const id = analysis.fullIdentifier || `${analysis.name}@${analysis.filePath}`;
    const node = this.dependencyGraph.get(id);
    return node && node.dependents.size === 0;
  }

  extractFunctionContent(fileContent, func) {
    const lines = fileContent.split('\n');
    const startLine = (func.line || 1) - 1;
    // Simple heuristic - could be enhanced with proper AST analysis
    const endLine = Math.min(startLine + 50, lines.length);
    return lines.slice(startLine, endLine).join('\n');
  }
}

// CLI Interface
if (require.main === module) {
  const args = process.argv.slice(2);
  const options = {
    verbose: args.includes('--verbose') || args.includes('-v'),
    generateSVG: !args.includes('--no-svg'),
    generateReport: !args.includes('--no-report')
  };

  // Parse target functions from arguments
  const targetIndex = args.indexOf('--targets');
  if (targetIndex >= 0 && args[targetIndex + 1]) {
    options.targetFunctions = args[targetIndex + 1].split(',').map(t => t.trim());
  } else {
    console.error('‚ùå No target functions specified. Use --targets "func1,func2,..."');
    process.exit(1);
  }

  const codemap = new TargetedCodemap(options);

  codemap.analyzeTargets(options.targetFunctions)
    .then(result => {
      console.log('‚úÖ Targeted codemap analysis complete:');
      console.log(`   - Functions analyzed: ${result.targetedFunctions}`);
      console.log(`   - Dependencies mapped: ${result.totalDependencies}`);
      console.log(`   - Duplicates found: ${result.duplicatesFound}`);
      console.log(`   - Output: ${result.outputPath.svgFile || result.outputPath.dotFile}`);
      process.exit(0);
    })
    .catch(error => {
      console.error('‚ùå Analysis failed:', error.message);
      process.exit(1);
    });
}

module.exports = TargetedCodemap;

