#!/usr/bin/env node

/**
 * TARGETED CODEMAP TOOL - OPTIMIZED AGENT-STEERED VERSION
 * 
 * Agent-steered function-level dependency analysis tool
 * - Targets specific functions for focused analysis
 * - Creates optimized dependency graphs
 * - Removes duplicates and competing implementations
 * - Generates actionable reports for targeted fixes
 * - 100% objective analysis without questioning DEZ requests
 */

const fs = require('fs');
const path = require('path');
const acorn = require('acorn');

class TargetedCodemap {
  constructor(options = {}) {
    this.options = {
      projectRoot: options.projectRoot || process.cwd(),
      targetFunctions: options.targetFunctions || [],
      outputDir: options.outputDir || path.join(process.cwd(), 'tmp'),
      analysisDepth: options.analysisDepth || 3,
      includePaths: options.includePaths || ['**/*.js'],
      excludePaths: options.excludePaths || ['node_modules/**', 'dist/**', 'build/**'],
      verbose: options.verbose || false,
      generateSVG: options.generateSVG !== false,
      generateReport: options.generateReport !== false
    };

    this.functionMap = new Map();
    this.dependencyGraph = new Map();
    this.duplicateMap = new Map();
    this.analysisResults = {
      targetedFunctions: [],
      dependencies: [],
      duplicates: [],
      recommendations: [],
      timestamp: new Date().toISOString()
    };

    this.log('üéØ Targeted Codemap Tool initialized');
  }

  log(message) {
    if (this.options.verbose) {
      console.log(`[${new Date().toISOString()}] ${message}`);
    }
  }

  /**
   * Main analysis entry point - steered by agent
   */
  async analyzeTargets(targets) {
    this.log('üîç Starting targeted function analysis...');
    this.options.targetFunctions = Array.isArray(targets) ? targets : [targets];

    // Phase 1: Scan codebase for all functions
    await this.scanCodebase();

    // Phase 2: Focus analysis on target functions
    await this.analyzeTargetedFunctions();

    // Phase 3: Build dependency relationships
    await this.buildDependencyGraph();

    // Phase 4: Identify duplicates and competing implementations
    await this.identifyDuplicatesAndCompetitors();

    // Phase 5: Generate optimized recommendations
    await this.generateOptimizedRecommendations();

    // Phase 6: Output results
    const outputPath = await this.generateOutputs();

    this.log('‚úÖ Targeted analysis complete');
    return {
      success: true,
      outputPath,
      results: this.analysisResults,
      targetedFunctions: this.options.targetFunctions.length,
      totalDependencies: this.dependencyGraph.size,
      duplicatesFound: this.analysisResults.duplicates.length
    };
  }

  /**
   * Scan codebase for all JavaScript functions
   */
  async scanCodebase() {
    this.log('üìÅ Scanning codebase for functions...');

    const files = await this.getJavaScriptFiles();
    let totalFunctions = 0;

    for (const filePath of files) {
      try {
        const functions = await this.extractFunctions(filePath);
        for (const func of functions) {
          this.functionMap.set(`${func.name}@${filePath}`, {
            ...func,
            filePath,
            fullIdentifier: `${func.name}@${filePath}`
          });
          totalFunctions++;
        }
      } catch (error) {
        this.log(`‚ö†Ô∏è Error parsing ${filePath}: ${error.message}`);
      }
    }

    this.log(`üìä Found ${totalFunctions} functions across ${files.length} files`);
  }

  /**
   * Focus analysis on target functions
   */
  async analyzeTargetedFunctions() {
    this.log('üéØ Analyzing targeted functions...');

    for (const targetName of this.options.targetFunctions) {
      const matches = this.findFunctionMatches(targetName);

      for (const match of matches) {
        const analysis = await this.analyzeFunction(match, 0);
        this.analysisResults.targetedFunctions.push(analysis);
        this.log(`üîç Analyzed: ${match.name} (${match.filePath})`);
      }
    }

    this.log(`‚úÖ Targeted analysis complete for ${this.analysisResults.targetedFunctions.length} functions`);
  }

  /**
   * Find all functions matching target name (supports partial matching)
   */
  findFunctionMatches(targetName) {
    const matches = [];

    for (const [identifier, func] of this.functionMap) {
      // Exact match
      if (func.name === targetName) {
        matches.push(func);
        continue;
      }

      // Partial match (case-insensitive)
      if (func.name.toLowerCase().includes(targetName.toLowerCase())) {
        matches.push(func);
        continue;
      }

      // Pattern match (supports wildcards)
      if (this.matchesPattern(func.name, targetName)) {
        matches.push(func);
      }
    }

    return matches;
  }

  /**
   * Pattern matching with wildcard support
   */
  matchesPattern(functionName, pattern) {
    if (pattern.includes('*')) {
      const regex = new RegExp(pattern.replace(/\*/g, '.*'), 'i');
      return regex.test(functionName);
    }
    return false;
  }

  /**
   * Deep analysis of a specific function and its dependencies
   */
  async analyzeFunction(func, depth) {
    if (depth >= this.options.analysisDepth) {
      return { ...func, dependencies: [], depth, truncated: true };
    }

    const dependencies = await this.findFunctionDependencies(func);
    const analysis = {
      ...func,
      dependencies: [],
      depth,
      directDependencies: dependencies.length,
      truncated: false
    };

    // Recursively analyze dependencies
    for (const dep of dependencies.slice(0, 10)) { // Limit to prevent explosion
      const depFunc = this.functionMap.get(dep.identifier);
      if (depFunc && !this.isCircularDependency(func, depFunc)) {
        const depAnalysis = await this.analyzeFunction(depFunc, depth + 1);
        analysis.dependencies.push(depAnalysis);
      }
    }

    return analysis;
  }

  /**
   * Find dependencies of a function by analyzing its AST
   */
  async findFunctionDependencies(func) {
    const dependencies = [];

    try {
      const fileContent = fs.readFileSync(func.filePath, 'utf8');
      const ast = acorn.parse(fileContent, { ecmaVersion: 2020, sourceType: 'module' });

      this.walkAST(ast, (node) => {
        if (node.type === 'CallExpression') {
          const calleeName = this.getCalleeIdentifier(node.callee);
          if (calleeName && this.functionMap.has(`${calleeName}@${func.filePath}`)) {
            dependencies.push({
              name: calleeName,
              identifier: `${calleeName}@${func.filePath}`,
              type: 'function_call',
              line: node.loc?.start?.line
            });
          }
        }
      });
    } catch (error) {
      this.log(`‚ö†Ô∏è Error analyzing dependencies for ${func.name}: ${error.message}`);
    }

    return dependencies;
  }

  /**
   * Build comprehensive dependency graph
   */
  async buildDependencyGraph() {
    this.log('üï∏Ô∏è Building dependency graph...');

    for (const targetAnalysis of this.analysisResults.targetedFunctions) {
      this.addToGraph(targetAnalysis);
    }

    this.log(`üìä Dependency graph built with ${this.dependencyGraph.size} nodes`);
  }

  /**
   * Recursively add function and dependencies to graph
   */
  addToGraph(analysis, visited = new Set()) {
    const id = analysis.fullIdentifier || `${analysis.name}@${analysis.filePath}`;

    if (visited.has(id)) return;
    visited.add(id);

    if (!this.dependencyGraph.has(id)) {
      this.dependencyGraph.set(id, {
        function: analysis,
        dependencies: new Set(),
        dependents: new Set(),
        depth: analysis.depth || 0
      });
    }

    const node = this.dependencyGraph.get(id);

    // Add dependencies
    if (analysis.dependencies) {
      for (const dep of analysis.dependencies) {
        const depId = dep.fullIdentifier || `${dep.name}@${dep.filePath}`;
        node.dependencies.add(depId);

        // Add reverse dependency
        if (!this.dependencyGraph.has(depId)) {
          this.dependencyGraph.set(depId, {
            function: dep,
            dependencies: new Set(),
            dependents: new Set(),
            depth: dep.depth || 0
          });
        }
        this.dependencyGraph.get(depId).dependents.add(id);

        // Recurse
        this.addToGraph(dep, visited);
      }
    }
  }

  /**
   * Identify duplicate functions and competing implementations
   */
  async identifyDuplicatesAndCompetitors() {
    this.log('üîç Identifying duplicates and competing implementations...');

    const nameGroups = new Map();

    // Group functions by name
    for (const [identifier, func] of this.functionMap) {
      if (!nameGroups.has(func.name)) {
        nameGroups.set(func.name, []);
      }
      nameGroups.get(func.name).push(func);
    }

    // Analyze groups with multiple implementations
    for (const [name, implementations] of nameGroups) {
      if (implementations.length > 1) {
        const analysis = await this.analyzeDuplicateGroup(name, implementations);
        this.analysisResults.duplicates.push(analysis);

        // Store in duplicate map for quick access
        this.duplicateMap.set(name, analysis);
      }
    }

    this.log(`üîç Found ${this.analysisResults.duplicates.length} sets of duplicate/competing functions`);
  }

  /**
   * Analyze a group of duplicate/competing implementations
   */
  async analyzeDuplicateGroup(name, implementations) {
    const analysis = {
      functionName: name,
      implementations: [],
      recommended: null,
      toRemove: [],
      confidence: 0,
      reasoning: []
    };

    for (const impl of implementations) {
      const score = await this.scoreFunctionImplementation(impl);
      analysis.implementations.push({
        ...impl,
        score,
        metrics: score.metrics
      });
    }

    // Sort by score (higher is better)
    analysis.implementations.sort((a, b) => b.score.total - a.score.total);

    // Best implementation becomes recommended
    analysis.recommended = analysis.implementations[0];
    analysis.toRemove = analysis.implementations.slice(1);
    analysis.confidence = analysis.recommended.score.total;

    // Generate reasoning
    analysis.reasoning.push(`Recommended: ${analysis.recommended.filePath} (score: ${analysis.recommended.score.total})`);
    for (const impl of analysis.toRemove) {
      analysis.reasoning.push(`Remove: ${impl.filePath} (score: ${impl.score.total}) - ${impl.score.reason}`);
    }

    return analysis;
  }

  /**
   * Score function implementation quality
   */
  async scoreFunctionImplementation(func) {
    const metrics = {
      linesOfCode: 0,
      complexity: 0,
      dependencies: 0,
      lastModified: 0,
      hasTests: false,
      hasComments: false,
      isES6: false,
      errorHandling: false
    };

    try {
      const fileContent = fs.readFileSync(func.filePath, 'utf8');
      const funcContent = this.extractFunctionContent(fileContent, func);

      // Lines of code
      metrics.linesOfCode = funcContent.split('\n').length;

      // Check for modern features
      metrics.isES6 = /(?:const|let|=>|async|await|\.\.\.)/.test(funcContent);

      // Check for error handling
      metrics.errorHandling = /(?:try|catch|throw|Error)/.test(funcContent);

      // Check for comments
      metrics.hasComments = /(?:\/\*|\/\/|@param|@return)/.test(funcContent);

      // File modification time
      const stats = fs.statSync(func.filePath);
      metrics.lastModified = stats.mtime.getTime();

      // Check for tests (heuristic)
      const testFiles = ['test', 'spec', '__tests__'];
      metrics.hasTests = testFiles.some(pattern => 
        fs.existsSync(func.filePath.replace(/\.js$/, `.${pattern}.js`))
      );

    } catch (error) {
      this.log(`‚ö†Ô∏è Error scoring ${func.name}: ${error.message}`);
    }

    // Calculate total score
    let score = 0;
    let reason = [];

    // Prefer newer files
    const ageScore = Math.min(10, (Date.now() - metrics.lastModified) / (1000 * 60 * 60 * 24 * 30)); // Max 10 points for files < 30 days old
    score += 10 - ageScore;
    if (ageScore < 5) reason.push('recent');

    // Prefer ES6+ features
    if (metrics.isES6) {
      score += 15;
      reason.push('modern syntax');
    }

    // Prefer error handling
    if (metrics.errorHandling) {
      score += 10;
      reason.push('error handling');
    }

    // Prefer documented code
    if (metrics.hasComments) {
      score += 8;
      reason.push('documented');
    }

    // Prefer tested code
    if (metrics.hasTests) {
      score += 12;
      reason.push('tested');
    }

    // Prefer reasonable size (not too small, not too large)
    if (metrics.linesOfCode > 5 && metrics.linesOfCode < 100) {
      score += 5;
      reason.push('appropriate size');
    }

    return {
      total: Math.round(score),
      metrics,
      reason: reason.join(', ') || 'basic implementation'
    };
  }

  /**
   * Generate optimized recommendations for targeted fixes
   */
  async generateOptimizedRecommendations() {
    this.log('üí° Generating optimized recommendations...');

    // Recommendations for target functions
    for (const targetAnalysis of this.analysisResults.targetedFunctions) {
      const recommendations = await this.generateFunctionRecommendations(targetAnalysis);
      this.analysisResults.recommendations.push(...recommendations);
    }

    // Recommendations for duplicates
    for (const duplicateAnalysis of this.analysisResults.duplicates) {
      const recommendations = await this.generateDuplicateRecommendations(duplicateAnalysis);
      this.analysisResults.recommendations.push(...recommendations);
    }

    this.log(`üí° Generated ${this.analysisResults.recommendations.length} optimization recommendations`);
  }

  /**
   * Generate recommendations for a specific function
   */
  async generateFunctionRecommendations(analysis) {
    const recommendations = [];

    // Missing dependencies
    const missingDeps = this.findMissingDependencies(analysis);
    if (missingDeps.length > 0) {
      recommendations.push({
        type: 'missing_dependencies',
        function: analysis.name,
        file: analysis.filePath,
        action: 'connect',
        details: `Connect to missing dependencies: ${missingDeps.join(', ')}`,
        priority: 'high',
        estimated_effort: 'medium'
      });
    }

    // Circular dependencies
    const circularDeps = this.findCircularDependencies(analysis);
    if (circularDeps.length > 0) {
      recommendations.push({
        type: 'circular_dependency',
        function: analysis.name,
        file: analysis.filePath,
        action: 'refactor',
        details: `Break circular dependency chain: ${circularDeps.join(' -> ')}`,
        priority: 'high',
        estimated_effort: 'high'
      });
    }

    // Unused functions
    if (this.isUnusedFunction(analysis)) {
      recommendations.push({
        type: 'unused_function',
        function: analysis.name,
        file: analysis.filePath,
        action: 'remove',
        details: 'Function appears to be unused and can be safely removed',
        priority: 'low',
        estimated_effort: 'low'
      });
    }

    return recommendations;
  }

  /**
   * Generate recommendations for duplicate functions
   */
  async generateDuplicateRecommendations(duplicateAnalysis) {
    const recommendations = [];

    // Remove inferior implementations
    for (const impl of duplicateAnalysis.toRemove) {
      recommendations.push({
        type: 'remove_duplicate',
        function: impl.name,
        file: impl.filePath,
        action: 'remove',
        details: `Remove inferior implementation (score: ${impl.score.total}). Keep: ${duplicateAnalysis.recommended.filePath}`,
        priority: 'medium',
        estimated_effort: 'low',
        confidence: duplicateAnalysis.confidence
      });
    }

    // Consolidate usage
    if (duplicateAnalysis.toRemove.length > 0) {
      recommendations.push({
        type: 'consolidate_usage',
        function: duplicateAnalysis.functionName,
        file: duplicateAnalysis.recommended.filePath,
        action: 'refactor',
        details: `Update all imports/calls to use recommended implementation`,
        priority: 'medium',
        estimated_effort: 'medium',
        affected_files: duplicateAnalysis.toRemove.length
      });
    }

    return recommendations;
  }

  /**
   * Generate all outputs (SVG, DOT, reports)
   */
  async generateOutputs() {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const outputBase = path.join(this.options.outputDir, `targeted-fix-${timestamp}`);

    // Ensure output directory exists
    if (!fs.existsSync(this.options.outputDir)) {
      fs.mkdirSync(this.options.outputDir, { recursive: true });
    }

    const outputs = {
      dotFile: `${outputBase}.dot`,
      svgFile: `${outputBase}.svg`,
      reportFile: `${outputBase}.md`,
      jsonFile: `${outputBase}.json`
    };

    // Generate DOT file
    await this.generateDotFile(outputs.dotFile);

    // Generate SVG if requested
    if (this.options.generateSVG) {
      await this.generateSVGFile(outputs.dotFile, outputs.svgFile);
    }

    // Generate actionable report
    if (this.options.generateReport) {
      await this.generateActionableReport(outputs.reportFile);
    }

    // Generate JSON output
    fs.writeFileSync(outputs.jsonFile, JSON.stringify(this.analysisResults, null, 2));

    this.log(`üìÑ Outputs generated: ${Object.keys(outputs).length} files`);
    return outputs;
  }

  /**
   * Generate DOT file for targeted dependency graph
   */
  async generateDotFile(outputPath) {
    const lines = [
      'digraph TargetedFunctionGraph {',
      '  rankdir=TB;',
      '  node [shape=box, style="rounded,filled", fontname="Consolas", fontsize=10];',
      '  edge [fontname="Consolas", fontsize=8, arrowsize=0.7];',
      '',
      '  // Target Functions (highlighted)',
      '  subgraph cluster_targets {'
    ];

    // Add target function nodes
    for (const target of this.analysisResults.targetedFunctions) {
      const nodeId = this.sanitizeNodeId(target.fullIdentifier || `${target.name}@${target.filePath}`);
      const label = `${target.name}\\n${this.getRelativePath(target.filePath)}:${target.line || '?'}`;

      lines.push(`    ${nodeId} [label="${label}", fillcolor="#ffcccc", style="rounded,filled,bold"];`);
    }

    lines.push('  }');
    lines.push('');

    // Add dependency nodes and edges
    for (const [id, node] of this.dependencyGraph) {
      const nodeId = this.sanitizeNodeId(id);
      const func = node.function;
      const label = `${func.name}\\n${this.getRelativePath(func.filePath)}:${func.line || '?'}`;

      // Color coding based on function type/status
      let fillColor = '#e8f4fd'; // Default blue
      if (this.isTargetFunction(func)) {
        fillColor = '#ffcccc'; // Target functions - red
      } else if (this.isDuplicateFunction(func)) {
        fillColor = '#fff2cc'; // Duplicates - yellow
      } else if (this.isRecommendedForRemoval(func)) {
        fillColor = '#ffcccc'; // To remove - red
      }

      lines.push(`  ${nodeId} [label="${label}", fillcolor="${fillColor}"];`);

      // Add dependency edges
      for (const depId of node.dependencies) {
        const depNodeId = this.sanitizeNodeId(depId);
        lines.push(`  ${nodeId} -> ${depNodeId};`);
      }
    }

    lines.push('}');

    fs.writeFileSync(outputPath, lines.join('\n'));
    this.log(`üìä DOT file generated: ${outputPath}`);
  }

  /**
   * Generate SVG from DOT file
   */
  async generateSVGFile(dotPath, svgPath) {
    try {
      const { spawn } = require('child_process');

      return new Promise((resolve, reject) => {
        const dot = spawn('dot', ['-Tsvg', dotPath, '-o', svgPath]);

        dot.on('close', (code) => {
          if (code === 0) {
            this.log(`üé® SVG generated: ${svgPath}`);
            resolve(svgPath);
          } else {
            reject(new Error(`Graphviz dot failed with code ${code}`));
          }
        });

        dot.on('error', (error) => {
          this.log(`‚ö†Ô∏è Graphviz not available, skipping SVG generation: ${error.message}`);
          resolve(null);
        });
      });
    } catch (error) {
      this.log(`‚ö†Ô∏è Could not generate SVG: ${error.message}`);
      return null;
    }
  }

  /**
   * Generate comprehensive actionable report
   */
  async generateActionableReport(outputPath) {
    const timestamp = new Date().toISOString();
    const lines = [
      '# TARGETED FIX ANALYSIS REPORT',
      '',
      `**Generated:** ${timestamp}`,
      `**Target Functions:** ${this.options.targetFunctions.join(', ')}`,
      `**Analysis Depth:** ${this.options.analysisDepth}`,
      '',
      '## EXECUTIVE SUMMARY',
      '',
      `- **Targeted Functions Analyzed:** ${this.analysisResults.targetedFunctions.length}`,
      `- **Total Dependencies Mapped:** ${this.dependencyGraph.size}`,
      `- **Duplicates/Competing Implementations:** ${this.analysisResults.duplicates.length}`,
      `- **Optimization Recommendations:** ${this.analysisResults.recommendations.length}`,
      '',
      '## TARGETED FUNCTION ANALYSIS',
      ''
    ];

    // Target function details
    for (const target of this.analysisResults.targetedFunctions) {
      lines.push(`### ${target.name} (${this.getRelativePath(target.filePath)})`);
      lines.push('');
      lines.push(`- **Location:** ${target.filePath}:${target.line || '?'}`);
      lines.push(`- **Direct Dependencies:** ${target.directDependencies || 0}`);
      lines.push(`- **Analysis Depth:** ${target.depth}`);

      if (target.dependencies && target.dependencies.length > 0) {
        lines.push('- **Key Dependencies:**');
        for (const dep of target.dependencies.slice(0, 5)) {
          lines.push(`  - ${dep.name} (${this.getRelativePath(dep.filePath)})`);
        }
      }
      lines.push('');
    }

    // Duplicates analysis
    if (this.analysisResults.duplicates.length > 0) {
      lines.push('## DUPLICATE/COMPETING IMPLEMENTATIONS');
      lines.push('');

      for (const dup of this.analysisResults.duplicates) {
        lines.push(`### ${dup.functionName}`);
        lines.push('');
        lines.push(`**Recommended Implementation:** ${this.getRelativePath(dup.recommended.filePath)} (score: ${dup.recommended.score.total})`);
        lines.push('');
        lines.push('**Implementations to Remove:**');
        for (const impl of dup.toRemove) {
          lines.push(`- ${this.getRelativePath(impl.filePath)} (score: ${impl.score.total}) - ${impl.score.reason}`);
        }
        lines.push('');
        lines.push('**Reasoning:**');
        for (const reason of dup.reasoning) {
          lines.push(`- ${reason}`);
        }
        lines.push('');
      }
    }

    // Optimization recommendations
    lines.push('## OPTIMIZATION RECOMMENDATIONS');
    lines.push('');

    const recommendations = this.analysisResults.recommendations.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
    });

    for (const rec of recommendations) {
      lines.push(`### ${rec.type.toUpperCase()}: ${rec.function}`);
      lines.push('');
      lines.push(`- **File:** ${this.getRelativePath(rec.file)}`);
      lines.push(`- **Action:** ${rec.action.toUpperCase()}`);
      lines.push(`- **Priority:** ${rec.priority.toUpperCase()}`);
      lines.push(`- **Effort:** ${rec.estimated_effort}`);
      if (rec.confidence) {
        lines.push(`- **Confidence:** ${rec.confidence}%`);
      }
      lines.push('');
      lines.push(`**Details:** ${rec.details}`);
      lines.push('');
    }

    // Implementation checklist
    lines.push('## IMPLEMENTATION CHECKLIST');
    lines.push('');
    lines.push('### High Priority Actions');
    const highPriority = recommendations.filter(r => r.priority === 'high');
    for (const rec of highPriority) {
      lines.push(`- [ ] ${rec.action.toUpperCase()}: ${rec.function} in ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    lines.push('### Medium Priority Actions');
    const mediumPriority = recommendations.filter(r => r.priority === 'medium');
    for (const rec of mediumPriority) {
      lines.push(`- [ ] ${rec.action.toUpperCase()}: ${rec.function} in ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    lines.push('### Files to Remove');
    const toRemove = recommendations.filter(r => r.action === 'remove');
    for (const rec of toRemove) {
      lines.push(`- [ ] DELETE: ${this.getRelativePath(rec.file)}`);
    }
    lines.push('');

    // Statistics
    lines.push('## ANALYSIS STATISTICS');
    lines.push('');
    lines.push(`- **Total Functions Scanned:** ${this.functionMap.size}`);
    lines.push(`- **Dependency Graph Nodes:** ${this.dependencyGraph.size}`);
    lines.push(`- **Functions to Remove:** ${toRemove.length}`);
    lines.push(`- **Functions to Refactor:** ${recommendations.filter(r => r.action === 'refactor').length}`);
    lines.push(`- **Missing Connections:** ${recommendations.filter(r => r.action === 'connect').length}`);

    fs.writeFileSync(outputPath, lines.join('\n'));
    this.log(`üìã Actionable report generated: ${outputPath}`);
  }

  // Helper methods
  async getJavaScriptFiles() {
    const { glob } = require('glob');
    const files = [];

    for (const pattern of this.options.includePaths) {
      const matches = await glob(pattern, {
        cwd: this.options.projectRoot,
        ignore: this.options.excludePaths,
        absolute: true
      });
      files.push(...matches);
    }

    return [...new Set(files)].filter(f => f.endsWith('.js') && fs.existsSync(f));
  }

  async extractFunctions(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const ast = acorn.parse(content, { 
        ecmaVersion: 2020, 
        sourceType: 'module',
        locations: true 
      });

      const functions = [];

      this.walkAST(ast, (node) => {
        if (node.type === 'FunctionDeclaration' || 
            node.type === 'FunctionExpression' ||
            node.type === 'ArrowFunctionExpression') {

          let name = 'anonymous';
          if (node.id && node.id.name) {
            name = node.id.name;
          } else if (node.type === 'FunctionExpression' || node.type === 'ArrowFunctionExpression') {
            // Try to get name from parent assignment
            name = this.getAssignmentName(node) || 'anonymous';
          }

          functions.push({
            name,
            type: node.type,
            line: node.loc?.start?.line,
            column: node.loc?.start?.column,
            params: node.params?.length || 0,
            async: node.async || false,
            generator: node.generator || false
          });
        }
      });

      return functions;
    } catch (error) {
      this.log(`‚ö†Ô∏è Error extracting functions from ${filePath}: ${error.message}`);
      return [];
    }
  }

  walkAST(node, callback) {
    if (!node || typeof node !== 'object') return;

    callback(node);

    for (const key in node) {
      if (key === 'parent') continue;
      const child = node[key];
      if (Array.isArray(child)) {
        child.forEach(item => this.walkAST(item, callback));
      } else if (child && typeof child === 'object') {
        this.walkAST(child, callback);
      }
    }
  }

  getCalleeIdentifier(callee) {
    if (callee.type === 'Identifier') {
      return callee.name;
    } else if (callee.type === 'MemberExpression') {
      return callee.property.name;
    }
    return null;
  }

  getAssignmentName(node) {
    // This would need more sophisticated logic to handle various assignment patterns
    return 'anonymous';
  }

  sanitizeNodeId(id) {
    return id.replace(/[^a-zA-Z0-9_]/g, '_');
  }

  getRelativePath(filePath) {
    return path.relative(this.options.projectRoot, filePath);
  }

  isTargetFunction(func) {
    return this.options.targetFunctions.includes(func.name);
  }

  isDuplicateFunction(func) {
    return this.duplicateMap.has(func.name);
  }

  isRecommendedForRemoval(func) {
    const duplicate = this.duplicateMap.get(func.name);
    if (!duplicate) return false;
    return duplicate.toRemove.some(impl => impl.filePath === func.filePath);
  }

  isCircularDependency(func1, func2) {
    // Simple circular dependency check - could be enhanced
    return func1.filePath === func2.filePath && func1.name === func2.name;
  }

  findMissingDependencies(analysis) {
    // Placeholder - would analyze function calls vs. available functions
    return [];
  }

  findCircularDependencies(analysis) {
    // Placeholder - would detect circular dependency chains
    return [];
  }

  isUnusedFunction(analysis) {
    // Check if function is called by any other function in the dependency graph
    const id = analysis.fullIdentifier || `${analysis.name}@${analysis.filePath}`;
    const node = this.dependencyGraph.get(id);
    return node && node.dependents.size === 0;
  }

  extractFunctionContent(fileContent, func) {
    const lines = fileContent.split('\n');
    const startLine = (func.line || 1) - 1;
    // Simple heuristic - could be enhanced with proper AST analysis
    const endLine = Math.min(startLine + 50, lines.length);
    return lines.slice(startLine, endLine).join('\n');
  }
}

// CLI Interface
if (require.main === module) {
  const args = process.argv.slice(2);
  const options = {
    verbose: args.includes('--verbose') || args.includes('-v'),
    generateSVG: !args.includes('--no-svg'),
    generateReport: !args.includes('--no-report')
  };

  // Parse target functions from arguments
  const targetIndex = args.indexOf('--targets');
  if (targetIndex >= 0 && args[targetIndex + 1]) {
    options.targetFunctions = args[targetIndex + 1].split(',').map(t => t.trim());
  } else {
    console.error('‚ùå No target functions specified. Use --targets "func1,func2,..."');
    process.exit(1);
  }

  const codemap = new TargetedCodemap(options);

  codemap.analyzeTargets(options.targetFunctions)
    .then(result => {
      console.log('‚úÖ Targeted codemap analysis complete:');
      console.log(`   - Functions analyzed: ${result.targetedFunctions}`);
      console.log(`   - Dependencies mapped: ${result.totalDependencies}`);
      console.log(`   - Duplicates found: ${result.duplicatesFound}`);
      console.log(`   - Output: ${result.outputPath.svgFile || result.outputPath.dotFile}`);
      process.exit(0);
    })
    .catch(error => {
      console.error('‚ùå Analysis failed:', error.message);
      process.exit(1);
    });
}

module.exports = TargetedCodemap;

