const { ipcMain, dialog } = require('electron');
const CDP = require('chrome-remote-interface');
const fs = require('fs');
const path = require('path');
const i18n = require('../i18n');

class IPCHandlers {
  constructor(app) {
    this.app = app;
  }

  setupIPC() {
    console.log(i18n.t('ipc.setup'));

    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-chrome-status' }));
    ipcMain.handle('get-chrome-status', async event => {
      try {
        const isRunning = this.app.chromeInstances && this.app.chromeInstances.length > 0;
        const status = {
          running: isRunning,
          pid: this.app.chromeInstance?.pid || null,
          port: this.app.chromeInstance?.port || null,
          instanceCount: this.app.chromeInstances?.length || 0,
          memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
        };

        console.log(
          i18n.t('ipc.handlers.chromeStatus.sending', { status: JSON.stringify(status) })
        );
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.chromeStatus.error', { error }));
        return {
          running: false,
          error: error.message,
          instanceCount: 0,
        };
      }
    });

    // Canvas state handler for Strike/AI systems
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-canvas-state' }));
    ipcMain.handle('get-canvas-state', async event => {
      try {
        // Request canvas state from frontend via event bridge
        const canvasState = await this.requestCanvasStateFromRenderer(event);

        console.log('üé® Canvas state requested for Strike/AI analysis:', {
          shapeCount: canvasState?.shapes?.length || 0,
          currentTool: canvasState?.currentTool || 'unknown',
          hasSelection: (canvasState?.selection?.length || 0) > 0
        });

        return {
          success: true,
          data: canvasState,
          timestamp: Date.now(),
          source: 'canvas-bridge'
        };
      } catch (error) {
        console.error('‚ùå Failed to get canvas state:', error);
        return {
          success: false,
          error: error.message,
          fallback: true
        };
      }
    });

    // Event forwarding handler for frontend-backend event bridge
    console.log(i18n.t('ipc.handlers.registering', { handler: 'forward-canvas-event' }));
    ipcMain.handle('forward-canvas-event', async (event, { type, data }) => {
      try {
        // Forward canvas events to Strike/AI systems
        if (this.app.eventBus) {
          this.app.eventBus.emit(`canvas:${type}`, {
            ...data,
            timestamp: Date.now(),
            source: 'frontend-bridge'
          });

          console.log(`üåâ Event forwarded: canvas:${type}`, { dataKeys: Object.keys(data) });
          return { success: true, forwarded: true };
        } else {
          console.warn('‚ö†Ô∏è No event bus available for forwarding');
          return { success: false, error: 'No event bus' };
        }
      } catch (error) {
        console.error('‚ùå Failed to forward canvas event:', error);
        return { success: false, error: error.message };
      }
    });

    // System metrics handler for live data (enhanced with canvas metrics)
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-system-metrics' }));
    ipcMain.handle('get-system-metrics', async event => {
      try {
        let cpuUsage = 0;
        try {
          if (typeof app.getCPUUsage === 'function') {
            cpuUsage = app.getCPUUsage().percentCPUUsage;
          } else {
            cpuUsage = process.cpuUsage().user / 1000000;
          }
        } catch (error) {
          cpuUsage = 0;
        }

        const memoryUsage = process.memoryUsage();
        let chromeTabs = 0;

        if (this.app.cdpClient) {
          try {
            const targets = await this.app.cdpClient.Target.getTargets();
            chromeTabs = targets.targetInfos.filter(
              target => target.type === 'page' && !target.url.startsWith('devtools://')
            ).length;
          } catch (error) {
            chromeTabs = 0;
          }
        }

        // Get canvas metrics if available
        let canvasMetrics = {};
        try {
          const canvasState = await this.requestCanvasStateFromRenderer();
          canvasMetrics = {
            shapeCount: canvasState?.shapes?.length || 0,
            currentTool: canvasState?.currentTool || 'select',
            hasSelection: (canvasState?.selection?.length || 0) > 0,
            cameraZoom: canvasState?.camera?.z || 1
          };
        } catch (error) {
          // Canvas metrics not available, continue without them
          canvasMetrics = {
            shapeCount: 0,
            currentTool: 'unknown',
            hasSelection: false,
            cameraZoom: 1
          };
        }

        const metrics = {
          cpu: parseFloat(cpuUsage.toFixed(1)),
          memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
          chromeTabs: chromeTabs,
          canvas: canvasMetrics,
          timestamp: Date.now(),
        };

        console.log(
          i18n.t('ipc.handlers.systemMetrics.sending', { metrics: JSON.stringify(metrics) })
        );
        return metrics;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.systemMetrics.error', { error }));
        return {
          cpu: 0,
          memory: 0,
          chromeTabs: 0,
          error: error.message,
        };
      }
    });

    // Connection status handler for live data
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-connection-status' }));
    ipcMain.handle('get-connection-status', async event => {
      try {
        const status = {
          connections: this.app.connectionPool?.getActiveConnectionCount() || 0,
          websocketRunning: this.app.connectionPool?.isServerRunning() || false,
          websocketPort: 9223,
          cdpPort: this.app.chromeInstance?.port || 9222,
          cdpConnected: this.app.cdpClient !== null,
          timestamp: Date.now(),
        };

        console.log(
          i18n.t('ipc.handlers.connectionStatus.sending', { status: JSON.stringify(status) })
        );
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.connectionStatus.error', { error }));
        return {
          connections: 0,
          websocketRunning: false,
          websocketPort: 9223,
          cdpPort: 9222,
          cdpConnected: false,
          error: error.message,
        };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'reconnect-devtools' }));
    ipcMain.handle('reconnect-devtools', async event => {
      try {
        console.log(i18n.t('ipc.handlers.devtools.reconnecting'));

        if (!this.app.chromeInstance || !this.app.chromeInstance.port) {
          throw new Error(i18n.t('ipc.handlers.devtools.noChromeInstance'));
        }

        if (this.app.cdpClient) {
          try {
            await this.app.cdpClient.close();
            console.log(i18n.t('ipc.handlers.devtools.closedConnection'));
          } catch (error) {
            console.log(i18n.t('ipc.handlers.devtools.errorClosing', { message: error.message }));
          }
          this.app.cdpClient = null;
        }

        await this.waitForChromeReady(this.app.chromeInstance.port, 5000);
        const connected = await this.connectToChrome(this.app.chromeInstance.port);

        if (connected) {
          console.log(i18n.t('ipc.handlers.devtools.successful'));
          this.app.updateRendererStatus();
          return { success: true, message: i18n.t('ipc.handlers.devtools.successMessage') };
        } else {
          throw new Error(i18n.t('ipc.handlers.devtools.failedConnection'));
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.devtools.failed', { error }));
        this.app.updateRendererStatus();
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('import-md-file', async () => {
      try {
        const result = await dialog.showOpenDialog(this.app.mainWindow, {
          properties: ['openFile', 'multiSelections'],
          filters: [
            { name: 'Markdown Files', extensions: ['md'] },
            { name: 'Text Files', extensions: ['txt'] },
            { name: 'All Files', extensions: ['*'] },
          ],
        });

        if (!result.canceled && result.filePaths.length > 0) {
          const files = [];
          for (const filePath of result.filePaths) {
            try {
              const content = fs.readFileSync(filePath, 'utf8');
              const fileName = path.basename(filePath);
              files.push({
                name: fileName,
                path: filePath,
                content: content,
                size: content.length,
              });
              console.log(
                i18n.t('ipc.handlers.mdImport.fileLoaded', { fileName, chars: content.length })
              );
            } catch (error) {
              console.error(i18n.t('ipc.handlers.mdImport.fileError', { filePath, error }));
            }
          }
          return { success: true, files: files, count: files.length };
        } else {
          return { success: false, error: 'No files selected', files: [] };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.mdImport.error', { error }));
        return { success: false, error: error.message, files: [] };
      }
    });

    ipcMain.handle('clear-sessions', async () => {
      console.log(i18n.t('ipc.handlers.sessions.clearing'));
      await this.app.connectionPool.clearAllSessions();
      return true;
    });

    ipcMain.handle('force-kill-websockets', async () => {
      console.log(i18n.t('ipc.handlers.websocket.forceKilling'));
      this.app.connectionPool.forceKillAllConnections();
      return { success: true, message: 'All WebSocket connections force killed' };
    });

    ipcMain.handle('restart-websocket-server', async () => {
      try {
        console.log(i18n.t('ipc.handlers.websocket.restarting'));
        await this.app.connectionPool.switchToNewServer();
        return { success: true, message: 'WebSocket server restarted successfully' };
      } catch (error) {
        console.error('‚ùå WebSocket server restart failed:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('queue-user-message', (event, message) => {
      console.log('üí¨ User message queued:', message);
      this.app.connectionPool.queueUserMessage(message);
      return true;
    });

    // CONSOLIDATED Terminal Write Handler - Routes to PTY Manager
    ipcMain.on('terminal-write', (event, data) => {
      console.log('üì§ Received terminal-write:', JSON.stringify(data));
      if (this.app.ptyManager) {
        const success = this.app.ptyManager.writeToTerminal(1, data); // Default to primary terminal
        if (!success) {
          console.error('‚ùå Failed to write to primary terminal (ID: 1)');
        }
      } else {
        console.error('‚ùå PTY Manager not available for terminal-write');
      }
    });

    ipcMain.on('write-to-terminal', (event, data, terminalId) => {
      console.log(
        'üì§ Received write-to-terminal:',
        JSON.stringify(data),
        'terminalId:',
        terminalId
      );
      if (this.app.ptyManager) {
        const targetId = terminalId || 1;
        const success = this.app.ptyManager.writeToTerminal(targetId, data);
        if (!success) {
          console.error(`‚ùå Failed to write to terminal ${targetId} - terminal may not exist`);
          // Try to create terminal if it doesn't exist
          if (!this.app.ptyManager.terminals.has(targetId)) {
            console.log(`üîÑ Attempting to create missing terminal ${targetId}`);
            this.app.ptyManager
              .setupTerminal(targetId)
              .then(() => {
                this.app.ptyManager.writeToTerminal(targetId, data);
              })
              .catch(err => {
                console.error(`‚ùå Failed to create terminal ${targetId}:`, err);
              });
          }
        }
      } else {
        console.error('‚ùå PTY Manager not available for write-to-terminal');
      }
    });

    // ENHANCED Terminal Write Handler for specific terminals
    ipcMain.on('write-to-specific-terminal', (event, { data, terminalId }) => {
      console.log(
        'üì§ Received write-to-specific-terminal:',
        JSON.stringify(data),
        'terminalId:',
        terminalId
      );
      if (this.app.ptyManager) {
        const success = this.app.ptyManager.writeToTerminal(terminalId, data);
        if (!success) {
          console.error(`‚ùå Failed to write to specific terminal ${terminalId}`);
        }
      } else {
        console.error('‚ùå PTY Manager not available for write-to-specific-terminal');
      }
    });

    ipcMain.on('terminal-resize', (event, { cols, rows, terminalId }) => {
      if (terminalId && this.app.ptyManager) {
        this.app.ptyManager.resizeTerminal(terminalId, cols, rows);
      } else if (this.app.ptyProcess) {
        this.app.ptyProcess.resize(cols, rows);
      }
    });

    // Enhanced Terminal Management IPC Handlers
    ipcMain.handle('create-new-terminal', async (event, options = {}) => {
      try {
        if (!this.app.ptyManager) {
          throw new Error('PTY Manager not available');
        }
        const terminalId = await this.app.ptyManager.createNewTerminal(options);
        return { success: true, terminalId };
      } catch (error) {
        console.error('‚ùå Failed to create new terminal:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('close-terminal', async (event, terminalId) => {
      try {
        if (!this.app.ptyManager) {
          throw new Error('PTY Manager not available');
        }
        const success = this.app.ptyManager.closeTerminal(terminalId);
        return { success };
      } catch (error) {
        console.error('‚ùå Failed to close terminal:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('switch-terminal', async (event, terminalId) => {
      try {
        if (!this.app.ptyManager) {
          throw new Error('PTY Manager not available');
        }
        const success = this.app.ptyManager.switchToTerminal(terminalId);
        return { success };
      } catch (error) {
        console.error('‚ùå Failed to switch terminal:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('get-terminal-list', async event => {
      try {
        if (!this.app.ptyManager) {
          return { success: false, error: 'PTY Manager not available' };
        }
        const terminals = this.app.ptyManager.getTerminalList();
        return { success: true, terminals };
      } catch (error) {
        console.error('‚ùå Failed to get terminal list:', error);
        return { success: false, error: error.message };
      }
    });

    // REMOVED: Duplicate handler - already defined above

    ipcMain.handle('get-terminal-debug-info', async event => {
      try {
        if (!this.app.ptyManager) {
          return { success: false, error: 'PTY Manager not available' };
        }
        const debugInfo = this.app.ptyManager.getDebugInfo();
        return { success: true, debugInfo };
      } catch (error) {
        console.error('‚ùå Failed to get terminal debug info:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.on('webview-error', (event, error) => {
      console.error(`‚ùå Error from DevTools Viewport: ${error.message}`);
      const errorObject = {
        source: error.source || 'WebView',
        message: error.message,
        timestamp: new Date().toISOString(),
        type: 'error',
      };
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('new-error-log', errorObject);
      }
    });

    // Screenshot handlers
    ipcMain.handle('take-screenshot', async () => {
      try {
        console.log('üì∏ Taking screenshot...');
        const { screen } = require('electron');
        const primaryDisplay = screen.getPrimaryDisplay();
        const { width, height } = primaryDisplay.size;

        const screenshot = await this.app.mainWindow.webContents.capturePage({
          x: 0,
          y: 0,
          width: Math.min(width, 1920),
          height: Math.min(height, 1080),
        });

        const buffer = screenshot.toPNG();
        const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
        const screenshotPath = path.join(
          __dirname,
          '../../screenshots',
          `screenshot-${timestamp}.png`
        );

        // Ensure screenshots directory exists
        const fs = require('fs');
        const screenshotsDir = path.join(__dirname, '../../screenshots');
        if (!fs.existsSync(screenshotsDir)) {
          fs.mkdirSync(screenshotsDir, { recursive: true });
        }

        fs.writeFileSync(screenshotPath, buffer);

        // Send to renderer
        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('screenshot-captured', {
            path: screenshotPath,
            timestamp: timestamp,
            size: buffer.length,
          });
        }

        return { success: true, path: screenshotPath };
      } catch (error) {
        console.error('‚ùå Screenshot failed:', error);
        return { success: false, error: error.message };
      }
    });

    // Strike Errors IPC Handlers
    ipcMain.handle('strike-errors:open', async () => {
      try {
        console.log('üéØ Opening Strike Errors interface...');

        // Path to Strike Errors UI
        const uiPath = path.join(__dirname, '../strike-errors/ui/file-upload.html');

        if (fs.existsSync(uiPath)) {
          return {
            success: true,
            uiPath: `file://${uiPath}`,
            message: 'Strike Errors interface ready',
          };
        } else {
          throw new Error('Strike Errors UI not found');
        }
      } catch (error) {
        console.error('‚ùå Strike Errors open failed:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('strike-errors:open-file-dialog', async () => {
      try {
        const result = await dialog.showOpenDialog(this.app.mainWindow, {
          properties: ['openFile', 'multiSelections'],
          filters: [
            {
              name: 'Code Files',
              extensions: ['js', 'jsx', 'ts', 'tsx', 'py', 'html', 'css', 'scss', 'less'],
            },
            { name: 'JavaScript', extensions: ['js', 'jsx'] },
            { name: 'TypeScript', extensions: ['ts', 'tsx'] },
            { name: 'Python', extensions: ['py'] },
            { name: 'Web Files', extensions: ['html', 'css', 'scss', 'less'] },
            { name: 'All Files', extensions: ['*'] },
          ],
        });

        if (!result.canceled && result.filePaths.length > 0) {
          const files = [];
          for (const filePath of result.filePaths) {
            try {
              const content = fs.readFileSync(filePath, 'utf8');
              const fileName = path.basename(filePath);
              const ext = path.extname(filePath).toLowerCase();

              files.push({
                name: fileName,
                path: filePath,
                content: content,
                size: content.length,
                type: this.getFileType(ext),
              });

              console.log(`‚úÖ Strike Errors: Loaded ${fileName} (${content.length} chars)`);
            } catch (error) {
              console.error(`‚ùå Strike Errors: Error reading ${filePath}:`, error);
            }
          }

          return { success: true, files: files, count: files.length };
        } else {
          return { success: false, cancelled: true, files: [] };
        }
      } catch (error) {
        console.error('‚ùå Strike Errors file dialog error:', error);
        return { success: false, error: error.message, files: [] };
      }
    });

    ipcMain.on('strike-errors:status-update', (event, data) => {
      console.log('üéØ Strike Errors status update:', data);
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('strike-errors:status-update', data);
      }
    });

    ipcMain.on('strike-errors:progress', (event, data) => {
      console.log('üìä Strike Errors progress:', data);
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('strike-errors:progress', data);
      }
    });

    ipcMain.on('strike-errors:analysis-complete', (event, results) => {
      console.log('üéâ Strike Errors analysis complete:', results);
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('strike-errors:analysis-complete', results);
      }
    });

    ipcMain.on('strike-errors:analysis-error', (event, error) => {
      console.error('‚ùå Strike Errors analysis error:', error);
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('strike-errors:analysis-error', error);
      }
    });

    ipcMain.on('terminal-input', (event, message) => {
      console.log('üì§ Terminal input from Strike Errors:', message);
      // Forward to terminal system
      if (this.app.ptyProcess) {
        // Don't write directly, just log for now
        console.log('üìù Strike Errors message logged to terminal');
      }
    });

    // Missing IPC Handlers - Adding to complete ELECTRONUI connections

    // AI Status handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-ai-status' }));
    ipcMain.handle('get-ai-status', async event => {
      try {
        const status = {
          active: false,
          processing: false,
          lastAnalysis: null,
          timestamp: Date.now(),
        };
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.aiStatus.error', { error }));
        return { active: false, error: error.message };
      }
    });

    // Strike Status handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-strike-status' }));
    ipcMain.handle('get-strike-status', async event => {
      try {
        const status = {
          initialized: true,
          analyzing: false,
          filesProcessed: 0,
          timestamp: Date.now(),
        };
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeStatus.error', { error }));
        return { initialized: false, error: error.message };
      }
    });

    // File deletion handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'delete-file' }));
    ipcMain.handle('delete-file', async (event, filePath) => {
      try {
        if (fs.existsSync(filePath)) {
          fs.unlinkSync(filePath);
          console.log(i18n.t('ipc.handlers.fileDeleted', { filePath }));
          return { success: true, message: 'File deleted successfully' };
        } else {
          return { success: false, error: 'File not found' };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.deleteFile.error', { filePath, error }));
        return { success: false, error: error.message };
      }
    });

    // Directory deletion handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'delete-dir' }));
    ipcMain.handle('delete-dir', async (event, dirPath) => {
      try {
        if (fs.existsSync(dirPath)) {
          fs.rmSync(dirPath, { recursive: true, force: true });
          console.log(i18n.t('ipc.handlers.dirDeleted', { dirPath }));
          return { success: true, message: 'Directory deleted successfully' };
        } else {
          return { success: false, error: 'Directory not found' };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.deleteDir.error', { dirPath, error }));
        return { success: false, error: error.message };
      }
    });

    // Screen capture handler (alternative method)
    console.log(i18n.t('ipc.handlers.registering', { handler: 'capture-screen' }));
    ipcMain.handle('capture-screen', async event => {
      try {
        console.log(i18n.t('ipc.handlers.screenCapture.starting'));
        const { screen } = require('electron');
        const sources = await screen.getAllDisplays();

        return {
          success: true,
          displays: sources.map(display => ({
            id: display.id,
            bounds: display.bounds,
            scaleFactor: display.scaleFactor,
          })),
        };
      } catch (error) {
        console.error(i18n.t('ipc.handlers.screenCapture.error', { error }));
        return { success: false, error: error.message };
      }
    });

    // Open planning tab handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'open-planning-tab' }));
    ipcMain.handle('open-planning-tab', async event => {
      try {
        console.log(i18n.t('ipc.handlers.planningTab.opening'));

        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('open-planning-tab');
          return { success: true, message: 'Planning tab opened' };
        } else {
          return { success: false, error: 'Main window not available' };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.planningTab.error', { error }));
        return { success: false, error: error.message };
      }
    });

    // Chrome navigation handlers - MISSING FROM ORIGINAL
    console.log(i18n.t('ipc.handlers.registering', { handler: 'navigate-canvas' }));
    ipcMain.handle('navigate-canvas', async event => {
      try {
        console.log(i18n.t('ipc.handlers.canvas.navigating'));
        if (this.app.cdpClient) {
          const canvasUrl = 'http://localhost:8000/canvas.html';
          console.log(i18n.t('info.chrome.navigating', { url: canvasUrl }));
          await this.app.cdpClient.Page.navigate({ url: canvasUrl });
          console.log(i18n.t('info.chrome.navigated'));
          return { success: true, message: 'Navigated to canvas' };
        } else {
          throw new Error(i18n.t('errors.chrome.notConnected'));
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.canvas.error', { error }));
        return { success: false, error: error.message };
      }
    });

    // Strike Errors AI initialization handlers - MISSING FROM ORIGINAL
    console.log(i18n.t('ipc.handlers.registering', { handler: 'initialize-strike-errors-ai' }));
    ipcMain.handle('initialize-strike-errors-ai', async event => {
      try {
        console.log(i18n.t('ipc.handlers.strikeAI.initializing'));
        // Initialize Strike Errors AI system
        return { success: true, message: 'Strike Errors AI initialized' };
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.error', { error }));
        return { success: false, error: error.message };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'strike-errors-ai-launch' }));
    ipcMain.handle('strike-errors-ai-launch', async event => {
      try {
        console.log(i18n.t('ipc.handlers.strikeAI.launching'));
        // Launch Strike Errors AI analysis
        return { success: true, message: 'Strike Errors AI analysis launched' };
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.launchError', { error }));
        return { success: false, error: error.message };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'strike-errors-ai-open-directory' }));
    ipcMain.handle('strike-errors-ai-open-directory', async event => {
      try {
        const result = await dialog.showOpenDialog(this.app.mainWindow, {
          properties: ['openDirectory'],
          title: 'Select Directory for Strike Errors Analysis',
        });

        if (!result.canceled && result.filePaths.length > 0) {
          const dirPath = result.filePaths[0];
          console.log(i18n.t('ipc.handlers.strikeAI.directorySelected', { dirPath }));
          return { success: true, path: dirPath };
        } else {
          return { success: false, cancelled: true };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.directoryError', { error }));
        return { success: false, error: error.message };
      }
    });

    // Unified request handler for new architecture client
    console.log(i18n.t('ipc.handlers.registering', { handler: 'unified-request' }));
    ipcMain.handle('unified-request', async (event, request) => {
      try {
        console.log(i18n.t('ipc.handlers.unified.processing', { type: request.type }));

        switch (request.type) {
          case 'system:health':
            return {
              success: true,
              data: {
                status: 'healthy',
                chrome: this.app.chromeInstance ? 'running' : 'stopped',
                memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
                timestamp: Date.now(),
              },
            };

          case 'system:metrics':
            const metrics = (await this.app.ipcHandlers)
              ? await this.getSystemMetrics()
              : { cpu: 0, memory: 0, chromeTabs: 0 };
            return {
              success: true,
              data: metrics,
            };

          case 'chrome:status':
            const chromeStatus = await this.getChromeStatus();
            return {
              success: true,
              data: chromeStatus,
            };

          default:
            console.warn(i18n.t('ipc.handlers.unified.unknown', { type: request.type }));
            return {
              success: false,
              error: `Unknown request type: ${request.type}`,
            };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.unified.error', { error }));
        return {
          success: false,
          error: error.message,
        };
      }
    });

    console.log(i18n.t('ipc.complete'));
  }

  async getSystemMetrics() {
    try {
      let cpuUsage = 0;
      try {
        if (typeof app.getCPUUsage === 'function') {
          cpuUsage = app.getCPUUsage().percentCPUUsage;
        } else {
          cpuUsage = process.cpuUsage().user / 1000000;
        }
      } catch (error) {
        cpuUsage = 0;
      }

      const memoryUsage = process.memoryUsage();
      let chromeTabs = 0;

      if (this.app.cdpClient) {
        try {
          const targets = await this.app.cdpClient.Target.getTargets();
          chromeTabs = targets.targetInfos.filter(
            target => target.type === 'page' && !target.url.startsWith('devtools://')
          ).length;
        } catch (error) {
          chromeTabs = 0;
        }
      }

      return {
        cpu: parseFloat(cpuUsage.toFixed(1)),
        memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
        chromeTabs: chromeTabs,
        timestamp: Date.now(),
      };
    } catch (error) {
      return {
        cpu: 0,
        memory: 0,
        chromeTabs: 0,
        error: error.message,
      };
    }
  }

  async getChromeStatus() {
    try {
      const isRunning = this.app.chromeInstances && this.app.chromeInstances.length > 0;
      return {
        running: isRunning,
        pid: this.app.chromeInstance?.pid || null,
        port: this.app.chromeInstance?.port || null,
        instanceCount: this.app.chromeInstances?.length || 0,
        memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
      };
    } catch (error) {
      return {
        running: false,
        error: error.message,
        instanceCount: 0,
      };
    }
  }

  getFileType(extension) {
    const typeMap = {
      '.js': 'javascript',
      '.jsx': 'javascript',
      '.ts': 'typescript',
      '.tsx': 'typescript',
      '.py': 'python',
      '.html': 'html',
      '.css': 'css',
      '.scss': 'scss',
      '.less': 'less',
      '.json': 'json',
      '.md': 'markdown',
    };
    return typeMap[extension] || 'text';
  }

  async waitForChromeReady(port, timeout = 10000) {
    const startTime = Date.now();
    while (Date.now() - startTime < timeout) {
      try {
        const response = await fetch(`http://localhost:${port}/json/version`);
        if (response.ok) {
          console.log(`‚úÖ Chrome on port ${port} is ready.`);
          return true;
        }
      } catch (error) {
        // Ignore connection errors and retry
      }
      await new Promise(resolve => setTimeout(resolve, 200));
    }
    throw new Error(`Chrome on port ${port} did not become ready in time.`);
  }

  async connectToChrome(port) {
    if (this.app.cdpClient) {
      try {
        await this.app.cdpClient.close();
      } catch (e) {
        // Ignore errors closing old client
      }
      this.app.cdpClient = null;
    }

    try {
      console.log(`üîó Connecting to Chrome on port ${port} using CDP...`);

      const targets = await CDP.List({ port });
      console.log(`üìã Found ${targets.length} Chrome targets`);

      const pageTarget = targets.find(target => target.type === 'page') || targets[0];

      if (!pageTarget) {
        throw new Error('No suitable Chrome target found');
      }

      console.log(`üéØ Connecting to target: ${pageTarget.title || pageTarget.url}`);
      this.app.cdpClient = await CDP({ port, target: pageTarget });

      console.log('üîß Enabling Chrome DevTools Protocol domains...');
      await Promise.all([
        this.app.cdpClient.Page.enable(),
        this.app.cdpClient.Runtime.enable(),
        this.app.cdpClient.DOM.enable(),
        this.app.cdpClient.CSS.enable(),
        this.app.cdpClient.Network.enable(),
        this.app.cdpClient.Debugger.enable(),
        this.app.cdpClient.Target.setDiscoverTargets({ discover: true }),
      ]).catch(error => {
        console.warn('‚ö†Ô∏è Some CDP domains failed to enable:', error.message);
      });

      this.app.cdpClient.on('disconnect', () => {
        console.log('üî¥ CDP client disconnected.');
        this.app.cdpClient = null;
        this.app.updateRendererStatus();
      });

      this.app.cdpClient.on('error', err => {
        console.error('üî¥ CDP Client Error:', err);
        const errorObject = {
          source: 'CDP Backend',
          message: err.message,
          timestamp: new Date().toISOString(),
          type: 'error',
        };
        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('new-error-log', errorObject);
        }
      });

      console.log('‚úÖ CDP connection successful.');
      return true;
    } catch (error) {
      console.error('‚ùå CDP connection failed:', error);
      this.app.cdpClient = null;
      return false;
    }
  }

  /**
   * Request canvas state from renderer process
   * Used by Strike/AI systems to get live canvas data
   */
  async requestCanvasStateFromRenderer(event) {
    try {
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        // Send request to renderer and wait for response
        return new Promise((resolve, reject) => {
          const timeout = setTimeout(() => {
            reject(new Error('Canvas state request timeout'));
          }, 5000);

          // Setup one-time listener for response
          const responseHandler = (responseEvent, canvasState) => {
            clearTimeout(timeout);
            resolve(canvasState);
          };

          this.app.mainWindow.webContents.once('canvas-state-response', responseHandler);

          // Request canvas state from renderer
          this.app.mainWindow.webContents.send('request-canvas-state', {
            requestId: Date.now(),
            timestamp: Date.now()
          });
        });
      } else {
        throw new Error('Main window not available for canvas state request');
      }
    } catch (error) {
      console.error('‚ùå Failed to request canvas state from renderer:', error);
      // Return fallback state
      return {
        shapes: [],
        selection: [],
        currentTool: 'select',
        camera: { x: 0, y: 0, z: 1 },
        fallback: true
      };
    }
  }
}

module.exports = IPCHandlers;

