// Terminal module initializing with connection handlers

// Wait for DOM, then create ONE terminal with proper connections
document.addEventListener('DOMContentLoaded', () => {
  // DOM ready - verify terminal dependencies and container availability

  const container = document.getElementById('terminal-container');
  // Initialize terminal container with proper dimensions

  if (container && window.Terminal) {
    const term = new window.Terminal({
      theme: { background: '#1e1e1e', foreground: '#d4d4d4' },
      cursorBlink: true,
      cursorStyle: 'bar',
      fontSize: 14,
      fontFamily: 'Monaco, Consolas, monospace',
    });

    // Ensure container is visible and sized properly
    container.innerHTML = '';
    container.style.cssText =
      'height:400px;background:#1e1e1e;display:block;width:100%;min-height:400px;';

    // Add FitAddon if available (handle both old and new versions)
    let fitAddon = null;
    if (window.FitAddon && window.FitAddon.FitAddon) {
      // New @xterm/addon-fit format
      fitAddon = new window.FitAddon.FitAddon();
      term.loadAddon(fitAddon);
    } else if (window.FitAddon) {
      // Old xterm-addon-fit format
      fitAddon = new window.FitAddon();
      term.loadAddon(fitAddon);
    }

    term.open(container);

    // Fit terminal to container
    if (fitAddon) {
      setTimeout(() => fitAddon.fit(), 100);
    }

    // Write initial prompt
    term.write('\r\n$ ');

    // ESSENTIAL: Connect terminal input to backend
    term.onData(data => {
      if (window.electronAPI) {
        window.electronAPI.writeToTerminal(data, 1);
      }
    });

    // ESSENTIAL: Listen for backend data
    if (window.electronAPI) {
      window.electronAPI.onTerminalData(data => {
        if (data.terminalId === 1) {
          term.write(data.data);
        }
      });
    }

    // ESSENTIAL: Chrome Button connections with UI feedback
    const initBtn = document.getElementById('initialize-chrome');
    if (initBtn) {
      initBtn.addEventListener('click', async () => {
        // Chrome initialization process started
        initBtn.disabled = true;
        initBtn.textContent = 'üöÄ Starting...';
        if (window.electronAPI) {
          try {
            const result = await window.electronAPI.initializeChrome();
            // Chrome initialization completed successfully
            if (result.success) {
              initBtn.textContent = '‚úÖ Chrome Running';
              initBtn.style.backgroundColor = '#28a745';
            } else {
              initBtn.textContent = '‚ùå Failed';
              initBtn.style.backgroundColor = '#dc3545';
            }
          } catch (error) {
            console.error('Chrome init error:', error);
            initBtn.textContent = '‚ùå Error';
            initBtn.style.backgroundColor = '#dc3545';
          }
          setTimeout(() => {
            initBtn.disabled = false;
            initBtn.textContent = 'üöÄ Initialize Chrome';
            initBtn.style.backgroundColor = '';
          }, 3000);
        }
      });
    }

    const restartBtn = document.getElementById('restart-chrome');
    if (restartBtn) {
      restartBtn.addEventListener('click', async () => {
        // Chrome restart process initiated
        restartBtn.disabled = true;
        restartBtn.textContent = 'üîÑ Restarting...';
        if (window.electronAPI) {
          try {
            const result = await window.electronAPI.restartChrome();
            // Chrome restart completed successfully
            if (result.success) {
              restartBtn.textContent = '‚úÖ Restarted';
              restartBtn.style.backgroundColor = '#28a745';
            } else {
              restartBtn.textContent = '‚ùå Failed';
              restartBtn.style.backgroundColor = '#dc3545';
            }
          } catch (error) {
            console.error('Chrome restart error:', error);
            restartBtn.textContent = '‚ùå Error';
            restartBtn.style.backgroundColor = '#dc3545';
          }
          setTimeout(() => {
            restartBtn.disabled = false;
            restartBtn.textContent = 'üîÑ Restart Chrome';
            restartBtn.style.backgroundColor = '';
          }, 3000);
        }
      });
    }

    const navigateBtn = document.getElementById('navigate-canvas');
    if (navigateBtn) {
      navigateBtn.addEventListener('click', async () => {
        // Canvas navigation initiated
        navigateBtn.disabled = true;
        navigateBtn.textContent = 'üé® Navigating...';
        if (window.electronAPI) {
          try {
            const result = await window.electronAPI.navigateCanvas();
            // Canvas navigation completed successfully
            if (result.success) {
              navigateBtn.textContent = '‚úÖ Navigated';
              navigateBtn.style.backgroundColor = '#28a745';
            } else {
              navigateBtn.textContent = '‚ùå Failed';
              navigateBtn.style.backgroundColor = '#dc3545';
            }
          } catch (error) {
            console.error('Navigation error:', error);
            navigateBtn.textContent = '‚ùå Error';
            navigateBtn.style.backgroundColor = '#dc3545';
          }
          setTimeout(() => {
            navigateBtn.disabled = false;
            navigateBtn.textContent = 'üé® Go to System';
            navigateBtn.style.backgroundColor = '';
          }, 3000);
        }
      });
    }

    // ESSENTIAL: Strike Errors Button connections
    const launchStrikeBtn = document.getElementById('launch-strike-errors');
    if (launchStrikeBtn) {
      launchStrikeBtn.addEventListener('click', async () => {
        // Strike Errors AI analysis launch initiated
        if (window.electronAPI) {
          try {
            // Initialize Strike Errors AI first
            await window.electronAPI.initializeStrikeErrorsAI();
            // Then launch analysis
            const result = await window.electronAPI.strikeErrorsAILaunch();
            // Strike Errors analysis launched successfully
          } catch (error) {
            console.error('Strike Errors launch failed:', error);
          }
        }
      });
    }

    const uploadBtn = document.getElementById('upload-files');
    if (uploadBtn) {
      uploadBtn.addEventListener('click', async () => {
        // Directory selection for Strike Errors analysis
        if (window.electronAPI) {
          try {
            const result = await window.electronAPI.strikeErrorsAIOpenDirectory();
            // Directory selection completed successfully
          } catch (error) {
            console.error('Directory upload failed:', error);
          }
        }
      });
    }

    // Screenshot buttons - handle both UI system IDs
    const screenshotBtn =
      document.getElementById('take-screenshot') ||
      document.getElementById('take-screenshot-global');
    const headerScreenshotBtn = document.getElementById('header-screenshot-btn');

    const handleScreenshot = async () => {
      // Screenshot capture initiated
      if (window.electronAPI && window.electronAPI.takeScreenshot) {
        try {
          const result = await window.electronAPI.takeScreenshot();
          // Screenshot captured successfully
        } catch (error) {
          console.error('Screenshot failed:', error);
        }
      }
    };

    if (screenshotBtn) {
      screenshotBtn.addEventListener('click', handleScreenshot);
    }

    if (headerScreenshotBtn) {
      headerScreenshotBtn.addEventListener('click', handleScreenshot);
    }

    // Tab navigation
    const tabBtns = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');

    tabBtns.forEach(btn => {
      btn.addEventListener('click', () => {
        const targetTab = btn.getAttribute('data-tab');

        // Remove active class from all tabs and contents
        tabBtns.forEach(b => b.classList.remove('active'));
        tabContents.forEach(c => c.classList.remove('active'));

        // Add active class to clicked tab and corresponding content
        btn.classList.add('active');
        const targetContent = document.getElementById(targetTab + '-tab');
        if (targetContent) {
          targetContent.classList.add('active');
        }
      });
    });

    // Toolbar toggles
    const leftToolbar = document.getElementById('leftToolbar');
    const rightToolbar = document.getElementById('rightToolbar');
    const leftToggle = document.getElementById('leftToolbarToggle');
    const rightToggle = document.getElementById('rightToolbarToggle');

    if (leftToggle) {
      leftToggle.addEventListener('click', () => {
        leftToolbar.classList.toggle('collapsed');
      });
    }

    if (rightToggle) {
      rightToggle.addEventListener('click', () => {
        rightToolbar.classList.toggle('collapsed');
      });
    }

    // Status updates and live data polling
    if (window.electronAPI) {
      // Chrome status updates
      window.electronAPI.onChromeStatusUpdate(status => {
        // Chrome status update received - updating UI
        updateUIStatus(status);
      });

      // CRITICAL: Error log listener - WAS MISSING!
      window.electronAPI.onNewErrorLog(error => {
        // Error log received - displaying in UI
        displayErrorLog(error);
      });

      // CRITICAL: Screenshot capture listener - WAS MISSING!
      window.electronAPI.onScreenshotCaptured(screenshot => {
        // Screenshot captured - handling display
        handlePlanningScreenshot(screenshot);
      });

      // CRITICAL: Planning tab listener - WAS MISSING!
      window.electronAPI.onOpenPlanningTab(() => {
        // Planning tab activation requested
        openPlanningTab();
      });

      // Start live data polling immediately
      startLiveDataPolling();

      // Poll every 5 seconds
      setInterval(startLiveDataPolling, 5000);
    }

    window.TERM = term;
    // Terminal successfully created with all connection handlers active
  } else {
    console.error('‚ùå Terminal setup failed - container or Terminal not available');
  }
});

// REPLACED WITH ENHANCED VERSION BELOW

// UI Status updates
function updateUIStatus(status) {
  // Update Chrome status
  const chromeStatusText = document.getElementById('chrome-status')?.querySelector('.status-text');
  if (chromeStatusText) {
    chromeStatusText.textContent = status.running
      ? `Chrome: Running (PID: ${status.pid})`
      : 'Chrome: Stopped';
  }

  // Update connection count
  const connectionsText = document
    .getElementById('connection-status')
    ?.querySelector('.status-text');
  if (connectionsText) {
    connectionsText.textContent = `Connections: ${status.connections || 0}`;
  }
}

function updateChromeStatus(status) {
  const chromeStatusText = document.getElementById('chrome-status')?.querySelector('.status-text');
  if (chromeStatusText && status) {
    chromeStatusText.textContent = status.running
      ? `Chrome: Running (PID: ${status.pid})`
      : 'Chrome: Initializing...';
  }
}

function updateSystemMetrics(metrics) {
  console.log('üéØ updateSystemMetrics called with:', metrics);

  if (!metrics) {
    console.warn('‚ö†Ô∏è No metrics provided to updateSystemMetrics');
    return;
  }

  // Update CPU
  const cpuElement = document.getElementById('footer-cpu-usage');
  if (cpuElement) {
    if (metrics.cpu !== undefined) {
      cpuElement.textContent = metrics.cpu + '%';
      console.log('‚úÖ Updated CPU:', metrics.cpu + '%');
    } else {
      console.warn('‚ö†Ô∏è CPU metric undefined');
    }
  } else {
    console.error('‚ùå CPU element not found: footer-cpu-usage');
  }

  // Update Memory
  const memoryElement = document.getElementById('footer-memory-usage');
  if (memoryElement) {
    if (metrics.memory !== undefined) {
      memoryElement.textContent = metrics.memory + 'MB';
      console.log('‚úÖ Updated Memory:', metrics.memory + 'MB');
    } else {
      console.warn('‚ö†Ô∏è Memory metric undefined');
    }
  } else {
    console.error('‚ùå Memory element not found: footer-memory-usage');
  }

  // Update Chrome tabs
  const tabsElement = document.getElementById('footer-chrome-tabs');
  if (tabsElement) {
    if (metrics.chromeTabs !== undefined) {
      tabsElement.textContent = metrics.chromeTabs.toString();
      console.log('‚úÖ Updated Chrome tabs:', metrics.chromeTabs.toString());
    } else {
      console.warn('‚ö†Ô∏è Chrome tabs metric undefined');
    }
  } else {
    console.error('‚ùå Chrome tabs element not found: footer-chrome-tabs');
  }

  // Handle canvas metrics if available
  if (metrics.canvas) {
    console.log('üé® Canvas metrics available:', metrics.canvas);

    // Update canvas shape count if element exists
    const canvasElement = document.getElementById('footer-canvas-shapes');
    if (canvasElement && metrics.canvas.shapeCount !== undefined) {
      canvasElement.textContent = metrics.canvas.shapeCount.toString();
      console.log('‚úÖ Updated Canvas shapes:', metrics.canvas.shapeCount);
    }
  }
}

function updateConnectionStatus(status) {
  if (!status) return;

  // Update connection count
  const connectionsElement = document.getElementById('footer-connections');
  if (connectionsElement && status.connections !== undefined) {
    connectionsElement.textContent = status.connections.toString();
  }

  // Update WebSocket status
  const wsElement = document.getElementById('footer-websocket-status');
  if (wsElement) {
    wsElement.textContent = `WS: ${status.websocketPort || 9223}`;
  }

  // Update CDP status
  const cdpElement = document.getElementById('footer-cdp-status');
  if (cdpElement) {
    cdpElement.textContent = `CDP: ${status.cdpPort || 9222}`;
  }
}

// CRITICAL: Error log display function - WAS MISSING!
function displayErrorLog(error) {
  // Display error in notification overlay

  // Find or create error log container
  let errorContainer = document.getElementById('error-log-container');
  if (!errorContainer) {
    errorContainer = document.createElement('div');
    errorContainer.id = 'error-log-container';
    errorContainer.style.cssText = `
      position: fixed;
      top: 10px;
      right: 10px;
      max-width: 400px;
      background: #ff4444;
      color: white;
      padding: 10px;
      border-radius: 5px;
      z-index: 1000;
      font-family: monospace;
      font-size: 12px;
    `;
    document.body.appendChild(errorContainer);
  }

  // Add error to container
  const errorDiv = document.createElement('div');
  errorDiv.style.cssText = 'border-bottom: 1px solid rgba(255,255,255,0.3); padding: 5px 0;';
  errorDiv.innerHTML = `
    <strong>${error.source || 'System'}:</strong><br/>
    ${error.message}<br/>
    <small>${new Date(error.timestamp).toLocaleTimeString()}</small>
  `;

  errorContainer.insertBefore(errorDiv, errorContainer.firstChild);

  // Keep only last 5 errors
  while (errorContainer.children.length > 5) {
    errorContainer.removeChild(errorContainer.lastChild);
  }

  // Auto-hide after 10 seconds
  setTimeout(() => {
    if (errorDiv.parentNode) {
      errorDiv.parentNode.removeChild(errorDiv);
    }
  }, 10000);
}

// CRITICAL: Planning screenshot handler - WAS MISSING!
function handlePlanningScreenshot(screenshot) {
  // Process planning screenshot and show notification

  // Show screenshot notification
  const notification = document.createElement('div');
  notification.style.cssText = `
    position: fixed;
    top: 50px;
    right: 10px;
    background: #28a745;
    color: white;
    padding: 10px;
    border-radius: 5px;
    z-index: 1001;
    font-family: monospace;
  `;
  notification.innerHTML = `
    üì∏ Screenshot captured!<br/>
    <small>Path: ${screenshot.path}</small><br/>
    <small>Size: ${Math.round(screenshot.size / 1024)}KB</small>
  `;
  document.body.appendChild(notification);

  // Auto-hide notification
  setTimeout(() => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  }, 5000);

  // Switch to planning tab if it exists
  openPlanningTab();
}

// CRITICAL: Planning tab opener - WAS MISSING!
function openPlanningTab() {
  // Activate planning tab or show planning mode notification

  // Try to switch to planning tab
  const planningTab = document.querySelector('[data-tab="planning"]');
  if (planningTab) {
    planningTab.click();
    // Planning tab activated successfully
  } else {
    // Create planning notification if no tab exists
    const notification = document.createElement('div');
    notification.style.cssText = `
      position: fixed;
      top: 90px;
      right: 10px;
      background: #007bff;
      color: white;
      padding: 10px;
      border-radius: 5px;
      z-index: 1001;
      font-family: monospace;
    `;
    notification.innerHTML = 'üìã Planning mode activated';
    document.body.appendChild(notification);

    setTimeout(() => {
      if (notification.parentNode) {
        notification.parentNode.removeChild(notification);
      }
    }, 3000);
  }
}

// CRITICAL: Enhanced live data polling with error handling
async function startLiveDataPolling() {
  if (!window.electronAPI) {
    console.error('‚ùå electronAPI not available for live data polling');
    return;
  }

  try {
