/**
 * New Architecture Client
 * REPLACES old renderer IPC patterns with unified request system
 * Implements batch processing and event-driven communication
 */

class NewArchitectureClient {
  constructor() {
    this.requestQueue = [];
    this.batchInterval = 100; // 100ms
    this.batchTimeoutId = null;
    this.pendingRequests = new Map();
    this.eventListeners = new Map();
    this.isInitialized = false;

    console.log('üèóÔ∏è New Architecture Client initializing...');
    this.initialize();
  }

  /**
   * Initialize the client
   */
  async initialize() {
    try {
      // Test connectivity with new unified IPC system
      const testResult = await this.sendUnifiedRequest('system:health', {});

      if (testResult.success) {
        console.log('‚úÖ Connected to new architecture backend');
        this.isInitialized = true;

        // Setup event listeners
        this.setupEventListeners();

        // Canvas state bridge removed - Chrome Container should not handle canvas state
        // this.setupCanvasStateBridge();

        // Emit ready event
        this.emit('client:ready', { timestamp: Date.now() });
      } else {
        throw new Error('Health check failed');
      }
    } catch (error) {
      console.error('‚ùå Failed to initialize new architecture client:', error);
      // Fall back to legacy mode if needed
      this.initializeLegacyMode();
    }
  }

  /**
   * Send unified request to backend
   * @param {string} type - Request type
   * @param {Object} payload - Request payload
   * @param {Object} options - Request options
   * @returns {Promise<*>} Response
   */
  async sendUnifiedRequest(type, payload, options = {}) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const requestData = {
      type,
      payload,
      id: this.generateRequestId(),
      timestamp: Date.now(),
      ...options,
    };

    try {
      console.log(`üì§ Sending unified request: ${type}`);

      const response = await window.electronAPI.invoke('unified-request', requestData);

      if (response.success) {
        console.log(`‚úÖ Unified request completed: ${type}`);
        return response.result;
      } else {
        throw new Error(response.error);
      }
    } catch (error) {
      console.error(`‚ùå Unified request failed: ${type}`, error);
      throw error;
    }
  }

  /**
   * Send batch request for multiple operations
   * @param {Array} requests - Array of requests
   * @returns {Promise<Array>} Array of responses
   */
  async sendBatchRequest(requests) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const batchData = {
      requests: requests.map(req => ({
        ...req,
        id: this.generateRequestId(),
      })),
      batchId: this.generateRequestId(),
      timestamp: Date.now(),
    };

    try {
      console.log(`üì¶ Sending batch request with ${requests.length} items`);

      const response = await window.electronAPI.invoke('batch-request', batchData);

      if (response.success) {
        console.log(`‚úÖ Batch request completed`);
        return response.results;
      } else {
        throw new Error(response.error);
      }
    } catch (error) {
      console.error(`‚ùå Batch request failed:`, error);
      throw error;
    }
  }

  /**
   * Chrome operations with new architecture
   */
  async chrome(operation, payload = {}) {
    const requestType = `chrome:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * Terminal operations with new architecture
   */
  async terminal(operation, payload = {}) {
    const requestType = `terminal:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * File operations with new architecture
   */
  async file(operation, payload = {}) {
    const requestType = `file:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * System operations with new architecture
   */
  async system(operation, payload = {}) {
    const requestType = `system:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * Queue request for batch processing
   * @param {string} type - Request type
   * @param {Object} payload - Request payload
   * @returns {Promise<*>} Response promise
   */
  queueRequest(type, payload) {
    return new Promise((resolve, reject) => {
      const request = {
        type,
        payload,
        id: this.generateRequestId(),
        resolve,
        reject,
        timestamp: Date.now(),
      };

      this.requestQueue.push(request);
      this.scheduleBatchSend();
    });
  }

  /**
   * Schedule batch send if not already scheduled
   */
  scheduleBatchSend() {
    if (this.batchTimeoutId || this.requestQueue.length === 0) {
      return;
    }

    this.batchTimeoutId = setTimeout(() => {
      this.processBatchQueue();
    }, this.batchInterval);
  }

  /**
   * Process queued requests as a batch
   */
  async processBatchQueue() {
    this.batchTimeoutId = null;

    if (this.requestQueue.length === 0) {
      return;
    }

    const requests = [...this.requestQueue];
    this.requestQueue = [];

    try {
      const results = await this.sendBatchRequest(
        requests.map(req => ({
          type: req.type,
          payload: req.payload,
          id: req.id,
        }))
      );

      // Resolve individual promises
      results.forEach((result, index) => {
        const request = requests[index];
        if (result.success) {
          request.resolve(result.result);
        } else {
          request.reject(new Error(result.error));
        }
      });
    } catch (error) {
      // Reject all requests on batch failure
      requests.forEach(request => {
        request.reject(error);
      });
    }
  }

  /**
   * Setup event listeners for real-time updates
   */
  setupEventListeners() {
    if (!window.electronAPI || !window.electronAPI.on) {
      console.warn('‚ö†Ô∏è Event listening not available');
      return;
    }

    // Listen for system events
    window.electronAPI.on('system:event', eventData => {
      this.handleSystemEvent(eventData);
    });

    // Listen for service events
    window.electronAPI.on('service:event', eventData => {
      this.handleServiceEvent(eventData);
    });

    console.log('‚úÖ Event listeners setup');
  }

  /**
   * Handle system events
   */
  handleSystemEvent(eventData) {
    console.log('üì° System event received:', eventData);
    this.emit('system:event', eventData);
  }

  /**
   * Handle service events
   */
  handleServiceEvent(eventData) {
    console.log('üì° Service event received:', eventData);
    this.emit('service:event', eventData);
  }

  /**
   * Add event listener
   */
  on(eventType, handler) {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType).push(handler);
  }

  /**
   * Remove event listener
   */
  off(eventType, handler) {
    const handlers = this.eventListeners.get(eventType);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    }
  }

  /**
   * Emit event
   */
  emit(eventType, data) {
    const handlers = this.eventListeners.get(eventType);
    if (handlers) {
      handlers.forEach(handler => {
        try {
          handler(data);
        } catch (error) {
          console.error(`Event handler error for ${eventType}:`, error);
        }
      });
    }
  }

  /**
   * Generate unique request ID
   */
  generateRequestId() {
    return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Initialize legacy mode fallback
   */
  initializeLegacyMode() {
    console.warn('‚ö†Ô∏è Falling back to legacy mode');
    this.isLegacyMode = true;
    // Keep existing functionality as fallback
  }

  /**
   * Get client health status
   */
  async getHealth() {
    try {
      if (this.isInitialized) {
        const systemHealth = await this.system('health');
        return {
          client: 'healthy',
          backend: systemHealth?.status || 'unknown',
          queueSize: this.requestQueue.length,
          eventListeners: this.eventListeners.size,
        };
      } else {
        return {
          client: 'initializing',
          backend: 'unknown',
          queueSize: this.requestQueue.length,
          eventListeners: this.eventListeners.size,
        };
      }
    } catch (error) {
      return {
        client: 'error',
        error: error.message,
        queueSize: this.requestQueue.length,
        eventListeners: this.eventListeners.size,
      };
    }
  }

  /**
   * Get client metrics
   */
  getMetrics() {
    return {
      isInitialized: this.isInitialized,
      requestQueue: this.requestQueue.length,
      eventListeners: this.eventListeners.size,
      batchInterval: this.batchInterval,
      pendingRequests: this.pendingRequests.size,
    };
  }

  /**
