/**
 * New Architecture Client
 * REPLACES old renderer IPC patterns with unified request system
 * Implements batch processing and event-driven communication
 */

class NewArchitectureClient {
  constructor() {
    this.requestQueue = [];
    this.batchInterval = 100; // 100ms
    this.batchTimeoutId = null;
    this.pendingRequests = new Map();
    this.eventListeners = new Map();
    this.isInitialized = false;

    console.log('üèóÔ∏è New Architecture Client initializing...');
    this.initialize();
  }

  /**
   * Initialize the client
   */
  async initialize() {
    try {
      // Test connectivity with new unified IPC system
      const testResult = await this.sendUnifiedRequest('system:health', {});

      if (testResult.success) {
        console.log('‚úÖ Connected to new architecture backend');
        this.isInitialized = true;

        // Setup event listeners
        this.setupEventListeners();

        // Setup resilient canvas state bridge (no failure points)
        this.setupResilientCanvasBridge();

        // Emit ready event
        this.emit('client:ready', { timestamp: Date.now() });
      } else {
        throw new Error('Health check failed');
      }
    } catch (error) {
      console.error('‚ùå Failed to initialize new architecture client:', error);
      // Fall back to legacy mode if needed
      this.initializeLegacyMode();
    }
  }

  /**
   * Send unified request to backend
   * @param {string} type - Request type
   * @param {Object} payload - Request payload
   * @param {Object} options - Request options
   * @returns {Promise<*>} Response
   */
  async sendUnifiedRequest(type, payload, options = {}) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const requestData = {
      type,
      payload,
      id: this.generateRequestId(),
      timestamp: Date.now(),
      ...options,
    };

    try {
      console.log(`üì§ Sending unified request: ${type}`);

      const response = await window.electronAPI.invoke('unified-request', requestData);

      if (response.success) {
        console.log(`‚úÖ Unified request completed: ${type}`);
        return response.result;
      } else {
        throw new Error(response.error);
      }
    } catch (error) {
      console.error(`‚ùå Unified request failed: ${type}`, error);
      throw error;
    }
  }

  /**
   * Send batch request for multiple operations
   * @param {Array} requests - Array of requests
   * @returns {Promise<Array>} Array of responses
   */
  async sendBatchRequest(requests) {
    if (!window.electronAPI) {
      throw new Error('Electron API not available');
    }

    const batchData = {
      requests: requests.map(req => ({
        ...req,
        id: this.generateRequestId(),
      })),
      batchId: this.generateRequestId(),
      timestamp: Date.now(),
    };

    try {
      console.log(`üì¶ Sending batch request with ${requests.length} items`);

      const response = await window.electronAPI.invoke('batch-request', batchData);

      if (response.success) {
        console.log(`‚úÖ Batch request completed`);
        return response.results;
      } else {
        throw new Error(response.error);
      }
    } catch (error) {
      console.error(`‚ùå Batch request failed:`, error);
      throw error;
    }
  }

  /**
   * Chrome operations with new architecture
   */
  async chrome(operation, payload = {}) {
    const requestType = `chrome:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * Terminal operations with new architecture
   */
  async terminal(operation, payload = {}) {
    const requestType = `terminal:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * File operations with new architecture
   */
  async file(operation, payload = {}) {
    const requestType = `file:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * System operations with new architecture
   */
  async system(operation, payload = {}) {
    const requestType = `system:${operation}`;
    return this.sendUnifiedRequest(requestType, payload);
  }

  /**
   * Queue request for batch processing
   * @param {string} type - Request type
   * @param {Object} payload - Request payload
   * @returns {Promise<*>} Response promise
   */
  queueRequest(type, payload) {
    return new Promise((resolve, reject) => {
      const request = {
        type,
        payload,
        id: this.generateRequestId(),
        resolve,
        reject,
        timestamp: Date.now(),
      };

      this.requestQueue.push(request);
      this.scheduleBatchSend();
    });
  }

  /**
   * Schedule batch send if not already scheduled
   */
  scheduleBatchSend() {
    if (this.batchTimeoutId || this.requestQueue.length === 0) {
      return;
    }

    this.batchTimeoutId = setTimeout(() => {
      this.processBatchQueue();
    }, this.batchInterval);
  }

  /**
   * Process queued requests as a batch
   */
  async processBatchQueue() {
    this.batchTimeoutId = null;

    if (this.requestQueue.length === 0) {
      return;
    }

    const requests = [...this.requestQueue];
    this.requestQueue = [];

    try {
      const results = await this.sendBatchRequest(
        requests.map(req => ({
          type: req.type,
          payload: req.payload,
          id: req.id,
        }))
      );

      // Resolve individual promises
      results.forEach((result, index) => {
        const request = requests[index];
        if (result.success) {
          request.resolve(result.result);
        } else {
          request.reject(new Error(result.error));
        }
      });
    } catch (error) {
      // Reject all requests on batch failure
      requests.forEach(request => {
        request.reject(error);
      });
    }
  }

  /**
   * Setup event listeners for real-time updates
   */
  setupEventListeners() {
    if (!window.electronAPI || !window.electronAPI.on) {
      console.warn('‚ö†Ô∏è Event listening not available');
      return;
    }

    // Listen for system events
    window.electronAPI.on('system:event', eventData => {
      this.handleSystemEvent(eventData);
    });

    // Listen for service events
    window.electronAPI.on('service:event', eventData => {
      this.handleServiceEvent(eventData);
    });

    console.log('‚úÖ Event listeners setup');
  }

  /**
   * Handle system events
   */
  handleSystemEvent(eventData) {
    console.log('üì° System event received:', eventData);
    this.emit('system:event', eventData);
  }

  /**
   * Handle service events
   */
  handleServiceEvent(eventData) {
    console.log('üì° Service event received:', eventData);
    this.emit('service:event', eventData);
  }

  /**
   * Add event listener
   */
  on(eventType, handler) {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType).push(handler);
  }

  /**
   * Remove event listener
   */
  off(eventType, handler) {
    const handlers = this.eventListeners.get(eventType);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    }
  }

  /**
   * Emit event
   */
  emit(eventType, data) {
    const handlers = this.eventListeners.get(eventType);
    if (handlers) {
      handlers.forEach(handler => {
        try {
          handler(data);
        } catch (error) {
          console.error(`Event handler error for ${eventType}:`, error);
        }
      });
    }
  }

  /**
   * Generate unique request ID
   */
  generateRequestId() {
    return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Initialize legacy mode fallback
   */
  initializeLegacyMode() {
    console.warn('‚ö†Ô∏è Falling back to legacy mode');
    this.isLegacyMode = true;
    // Keep existing functionality as fallback
  }

  /**
   * Get client health status
   */
  async getHealth() {
    try {
      if (this.isInitialized) {
        const systemHealth = await this.system('health');
        return {
          client: 'healthy',
          backend: systemHealth?.status || 'unknown',
          queueSize: this.requestQueue.length,
          eventListeners: this.eventListeners.size,
        };
      } else {
        return {
          client: 'initializing',
          backend: 'unknown',
          queueSize: this.requestQueue.length,
          eventListeners: this.eventListeners.size,
        };
      }
    } catch (error) {
      return {
        client: 'error',
        error: error.message,
        queueSize: this.requestQueue.length,
        eventListeners: this.eventListeners.size,
      };
    }
  }

  /**
   * Get client metrics
   */
  getMetrics() {
    return {
      isInitialized: this.isInitialized,
      requestQueue: this.requestQueue.length,
      eventListeners: this.eventListeners.size,
      batchInterval: this.batchInterval,
      pendingRequests: this.pendingRequests.size,
    };
  }

  /**
   * Setup Canvas State Bridge for Strike/AI data connection
   */
  setupCanvasStateBridge() {
    console.log('üåâ Setting up canvas state bridge for Strike/AI integration...');

    // Listen for canvas state requests from main process
    window.electronAPI?.on?.('request-canvas-state', (data) => {
      console.log('üì® Canvas state requested by backend:', data);

      try {
        // Get current canvas state from TLDraw store
        const canvasState = this.getCurrentCanvasState();

        // Send response back to main process
        window.electronAPI?.send?.('canvas-state-response', canvasState);

        console.log('üì§ Canvas state sent to backend:', {
          shapeCount: canvasState.shapes?.length || 0,
          currentTool: canvasState.currentTool,
          hasSelection: (canvasState.selection?.length || 0) > 0
        });
      } catch (error) {
        console.error('‚ùå Failed to get canvas state:', error);

        // Send fallback state
        window.electronAPI?.send?.('canvas-state-response', {
          shapes: [],
          selection: [],
          currentTool: 'select',
          camera: { x: 0, y: 0, z: 1 },
          error: error.message,
          fallback: true
        });
      }
    });

    // Setup canvas event forwarding to backend
    this.setupCanvasEventForwarding();

    console.log('‚úÖ Canvas state bridge initialized');
  }

  /**
   * Get current canvas state from TLDraw store
   */
  getCurrentCanvasState() {
    try {
      // Try to get state from global TLDraw store
      if (window.tlEditor?.store) {
        const store = window.tlEditor.store;

        return {
          shapes: store.getCurrentPageShapes?.() || store.getAllShapes?.() || [],
          selection: store.getSelectedShapes?.() || [],
          currentTool: store.getCurrentTool?.() || store.getCurrentInstance?.()?.currentTool || 'select',
          camera: store.getCurrentCamera?.() || { x: 0, y: 0, z: 1 },
          page: store.getCurrentPage?.() || null,
          timestamp: Date.now(),
          source: 'tldraw-store'
        };
      }

      // Try alternative state sources
      if (window.editor?.store) {
        const store = window.editor.store;

        return {
          shapes: store.getCurrentPageShapes?.() || [],
          selection: store.getSelectedShapes?.() || [],
          currentTool: store.getCurrentTool?.() || 'select',
          camera: store.getCurrentCamera?.() || { x: 0, y: 0, z: 1 },
          timestamp: Date.now(),
          source: 'editor-store'
        };
      }

      // Try global store instance
      if (window.store) {
        return {
          shapes: window.store.getCurrentPageShapes?.() || window.store.getAllShapes?.() || [],
          selection: window.store.getSelectedShapes?.() || [],
          currentTool: window.store.getCurrentTool?.() || 'select',
          camera: window.store.getCurrentCamera?.() || { x: 0, y: 0, z: 1 },
          timestamp: Date.now(),
          source: 'global-store'
        };
      }

      // Fallback: empty state
      console.warn('‚ö†Ô∏è No canvas store found, returning empty state');
      return {
        shapes: [],
        selection: [],
        currentTool: 'select',
        camera: { x: 0, y: 0, z: 1 },
        timestamp: Date.now(),
        source: 'fallback'
      };

    } catch (error) {
      console.error('‚ùå Error getting canvas state:', error);
      throw error;
    }
  }

  /**
   * Setup canvas event forwarding to backend for Strike/AI analysis
   */
  setupCanvasEventForwarding() {
    console.log('üîÑ Setting up canvas event forwarding...');

    // Forward significant canvas events to backend
    const eventsToForward = [
      'shape:created',
      'shape:updated', 
      'shape:deleted',
      'selection:changed',
      'tool:changed',
      'camera:moved'
    ];

    // Try to hook into various event systems
    try {
      // Hook into global event bus if available
      if (window.globalEventBus?.on) {
        eventsToForward.forEach(eventType => {
          window.globalEventBus.on(eventType, (data) => {
            this.forwardCanvasEvent(eventType, data);
          });
        });
        console.log('‚úÖ Hooked into globalEventBus for canvas events');
      }

      // Hook into TLDraw store events if available
      if (window.tlEditor?.store?.on) {
        window.tlEditor.store.on('change', (change) => {
          this.forwardCanvasEvent('store:change', {
            added: change.added,
            updated: change.updated,
            removed: change.removed
          });
        });
        console.log('‚úÖ Hooked into TLDraw store events');
      }

      // Periodic canvas state sync for Strike/AI
      this.startCanvasStateSync();

    } catch (error) {
      console.error('‚ùå Failed to setup canvas event forwarding:', error);
    }
  }

  /**
   * Forward canvas event to backend for Strike/AI processing
   */
  async forwardCanvasEvent(eventType, data) {
    try {
      const result = await window.electronAPI?.invoke?.('forward-canvas-event', {
        type: eventType,
        data: data,
        timestamp: Date.now(),
        source: 'frontend-bridge'
      });

      if (result?.success) {
        console.log(`üåâ Event forwarded: ${eventType}`);
      } else {
        console.warn(`‚ö†Ô∏è Event forward failed: ${eventType}`, result);
      }
    } catch (error) {
      console.error(`‚ùå Failed to forward event ${eventType}:`, error);
    }
  }

  /**
   * Start periodic canvas state sync for Strike/AI systems
   */
  startCanvasStateSync() {
    // Sync canvas state every 5 seconds for Strike/AI analysis
    setInterval(() => {
      try {
        const canvasState = this.getCurrentCanvasState();

        // Only sync if there are shapes or active interactions
        if (canvasState.shapes.length > 0 || canvasState.selection.length > 0 || canvasState.currentTool !== 'select') {
          this.forwardCanvasEvent('canvas:sync', {
            summary: {
              shapeCount: canvasState.shapes.length,
              selectionCount: canvasState.selection.length,
              currentTool: canvasState.currentTool,
              cameraZoom: canvasState.camera.z
            },
            lastSync: Date.now()
          });
        }
      } catch (error) {
        // Silent fail - don't spam console with sync errors
      }
    }, 5000);

    console.log('‚è∞ Canvas state sync started (5s interval)');
  }
}

/**
 * Enhanced Terminal Controller using new architecture
 */
class EnhancedTerminalController {
  constructor(architectureClient) {
    this.client = architectureClient;
    this.terminals = new Map();
    this.activeTerminalId = null;
  }

  /**
   * Create terminal with new architecture
   */
  async createTerminal(options = {}) {
    try {
      const result = await this.client.terminal('create', { options });

      const terminalId = result.terminalId || Date.now().toString();
      this.terminals.set(terminalId, {
        id: terminalId,
        options,
        created: Date.now(),
      });

      if (!this.activeTerminalId) {
        this.activeTerminalId = terminalId;
      }

      console.log(`‚úÖ Terminal created: ${terminalId}`);
      return terminalId;
    } catch (error) {
      console.error('‚ùå Failed to create terminal:', error);
      throw error;
    }
  }

  /**
   * Write to terminal with batching
   */
  async writeToTerminal(terminalId, data) {
    // Use queuing for frequent terminal writes
    return this.client.queueRequest('terminal:write', {
      terminalId: terminalId || this.activeTerminalId,
      data,
    });
  }

  /**
   * Resize terminal
   */
  async resizeTerminal(terminalId, cols, rows) {
    return this.client.terminal('resize', {
      terminalId: terminalId || this.activeTerminalId,
      cols,
      rows,
    });
  }

  /**
   * Get terminal list
   */
  async getTerminals() {
    const result = await this.client.terminal('list');
    return result.terminals || [];
  }
}

/**
 * Enhanced Chrome Controller using new architecture
 */
class EnhancedChromeController {
  constructor(architectureClient) {
    this.client = architectureClient;
    this.isInitialized = false;
  }

  /**
   * Initialize Chrome with new architecture
   */
  async initialize(options = {}) {
    try {
      const result = await this.client.chrome('initialize', { options });
      this.isInitialized = true;
      console.log('‚úÖ Chrome initialized via new architecture');
      return result;
    } catch (error) {
      console.error('‚ùå Chrome initialization failed:', error);
      throw error;
    }
  }

  /**
   * Navigate Chrome
   */
  async navigate(url) {
    return this.client.chrome('navigate', { url });
  }

  /**
   * Get Chrome status
   */
  async getStatus() {
    return this.client.chrome('status');
  }

  /**
   * Restart Chrome
   */
  async restart() {
    return this.client.chrome('restart');
  }
}

// Initialize global client
const newArchitectureClient = new NewArchitectureClient();
const enhancedTerminal = new EnhancedTerminalController(newArchitectureClient);
const enhancedChrome = new EnhancedChromeController(newArchitectureClient);

// Export to global scope for backward compatibility
window.newArchitectureClient = newArchitectureClient;
window.enhancedTerminal = enhancedTerminal;
window.enhancedChrome = enhancedChrome;

// Log initialization
console.log('üéØ New Architecture Client loaded and available globally');

// Export for module use
if (typeof module !== 'undefined' && module.exports) {
  module.exports = {
    NewArchitectureClient,
    EnhancedTerminalController,
    EnhancedChromeController,
    newArchitectureClient,
    enhancedTerminal,
    enhancedChrome,
  };
}

