const { ipcMain, dialog } = require('electron');
const CDP = require('chrome-remote-interface');
const fs = require('fs');
const path = require('path');
const i18n = require('../i18n');

class IPCHandlers {
  constructor(app) {
    this.app = app;
  }

  setupIPC() {
    console.log(i18n.t('ipc.setup'));

    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-chrome-status' }));
    ipcMain.handle('get-chrome-status', async event => {
      try {
        const isRunning = this.app.chromeInstances && this.app.chromeInstances.length > 0;
        const status = {
          running: isRunning,
          pid: this.app.chromeInstance?.pid || null,
          port: this.app.chromeInstance?.port || null,
          instanceCount: this.app.chromeInstances?.length || 0,
          memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
        };

        console.log(
          i18n.t('ipc.handlers.chromeStatus.sending', { status: JSON.stringify(status) })
        );
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.chromeStatus.error', { error }));
        return {
          running: false,
          error: error.message,
          instanceCount: 0,
        };
      }
    });

    // Canvas state handler for webview integration (resilient)
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-canvas-state' }));
    ipcMain.handle('get-canvas-state', async event => {
      try {
        // Try to get canvas state from webview if available
        const canvasState = await this.requestCanvasStateFromWebview();
        return {
          success: true,
          data: canvasState,
          timestamp: Date.now(),
          source: 'webview-bridge',
        };
      } catch (error) {
        // Fail gracefully - canvas may not be loaded in webview
        return {
          success: false,
          error: error.message,
          fallback: {
            shapes: [],
            selection: [],
            currentTool: 'select',
            camera: { x: 0, y: 0, z: 1 },
          },
          timestamp: Date.now(),
        };
      }
    });

    // Canvas event forwarding handler for webview integration (resilient)
    console.log(i18n.t('ipc.handlers.registering', { handler: 'forward-canvas-event' }));
    ipcMain.handle('forward-canvas-event', async (event, { type, data }) => {
      try {
        // Forward canvas events to backend systems if eventBus available
        if (this.app.eventBus) {
          this.app.eventBus.emit(`canvas:${type}`, {
            ...data,
            timestamp: Date.now(),
            source: 'webview-bridge',
          });
          return { success: true, forwarded: true };
        } else {
          // EventBus not available - fail gracefully
          return { success: false, error: 'EventBus unavailable', graceful: true };
        }
      } catch (error) {
        // Always fail gracefully for canvas events
        return { success: false, error: error.message, graceful: true };
      }
    });

    // System metrics handler for live data
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-system-metrics' }));
    ipcMain.handle('get-system-metrics', async event => {
      try {
        let cpuUsage = 0;
        try {
          if (typeof app.getCPUUsage === 'function') {
            cpuUsage = app.getCPUUsage().percentCPUUsage;
          } else {
            cpuUsage = process.cpuUsage().user / 1000000;
          }
        } catch (error) {
          cpuUsage = 0;
        }

        const memoryUsage = process.memoryUsage();
        let chromeTabs = 0;

        if (this.app.cdpClient) {
          try {
            const targets = await this.app.cdpClient.Target.getTargets();
            chromeTabs = targets.targetInfos.filter(
              target => target.type === 'page' && !target.url.startsWith('devtools://')
            ).length;
          } catch (error) {
            chromeTabs = 0;
          }
        }

        // Try to get canvas metrics if webview has canvas loaded (no failure points)
        const baseMetrics = {
          cpu: parseFloat(cpuUsage.toFixed(1)),
          memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
          chromeTabs: chromeTabs,
          timestamp: Date.now(),
        };

        // Attempt to add canvas metrics if available (graceful failure)
        try {
          const canvasState = await Promise.race([
            this.requestCanvasStateFromWebview(),
            new Promise((_, reject) => setTimeout(() => reject(new Error('Quick timeout')), 500)),
          ]);

          // Canvas is available - add metrics
          baseMetrics.canvas = {
            shapeCount: canvasState.shapes?.length || 0,
            currentTool: canvasState.currentTool || 'select',
            hasSelection: (canvasState.selection?.length || 0) > 0,
            cameraZoom: canvasState.camera?.z || 1,
            available: true,
          };
        } catch (error) {
          // Canvas not available - add minimal metrics
          baseMetrics.canvas = {
            available: false,
            reason: error.message.includes('Canvas not loaded') ? 'not_loaded' : 'unavailable',
          };
        }

        const metrics = baseMetrics;

        console.log('üìä System metrics generated:', JSON.stringify(metrics, null, 2));
        console.log(
          i18n.t('ipc.handlers.systemMetrics.sending', { metrics: JSON.stringify(metrics) })
        );
        return metrics;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.systemMetrics.error', { error }));
        return {
          cpu: 0,
          memory: 0,
          chromeTabs: 0,
          error: error.message,
        };
      }
    });

    // Connection status handler for live data
    console.log(i18n.t('ipc.handlers.registering', { handler: 'get-connection-status' }));
    ipcMain.handle('get-connection-status', async event => {
      try {
        const status = {
          connections: this.app.connectionPool?.getActiveConnectionCount() || 0,
          websocketRunning: this.app.connectionPool?.isServerRunning() || false,
          websocketPort: 9223,
          cdpPort: this.app.chromeInstance?.port || 9222,
          cdpConnected: this.app.cdpClient !== null,
          timestamp: Date.now(),
        };

        console.log(
          i18n.t('ipc.handlers.connectionStatus.sending', { status: JSON.stringify(status) })
        );
        return status;
      } catch (error) {
        console.error(i18n.t('ipc.handlers.connectionStatus.error', { error }));
        return {
          connections: 0,
          websocketRunning: false,
          websocketPort: 9223,
          cdpPort: 9222,
          cdpConnected: false,
          error: error.message,
        };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'reconnect-devtools' }));
    ipcMain.handle('reconnect-devtools', async event => {
      try {
        console.log(i18n.t('ipc.handlers.devtools.reconnecting'));

        if (!this.app.chromeInstance || !this.app.chromeInstance.port) {
          throw new Error(i18n.t('ipc.handlers.devtools.noChromeInstance'));
        }

        if (this.app.cdpClient) {
          try {
            await this.app.cdpClient.close();
            console.log(i18n.t('ipc.handlers.devtools.closedConnection'));
          } catch (error) {
            console.log(i18n.t('ipc.handlers.devtools.errorClosing', { message: error.message }));
          }
          this.app.cdpClient = null;
        }

        await this.waitForChromeReady(this.app.chromeInstance.port, 5000);
        const connected = await this.connectToChrome(this.app.chromeInstance.port);

        if (connected) {
          console.log(i18n.t('ipc.handlers.devtools.successful'));
          this.app.updateRendererStatus();
          return { success: true, message: i18n.t('ipc.handlers.devtools.successMessage') };
        } else {
          throw new Error(i18n.t('ipc.handlers.devtools.failedConnection'));
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.devtools.failed', { error }));
        this.app.updateRendererStatus();
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('import-md-file', async () => {
      try {
        const result = await dialog.showOpenDialog(this.app.mainWindow, {
          properties: ['openFile', 'multiSelections'],
          filters: [
            { name: 'Markdown Files', extensions: ['md'] },
            { name: 'Text Files', extensions: ['txt'] },
            { name: 'All Files', extensions: ['*'] },
          ],
        });

        if (!result.canceled && result.filePaths.length > 0) {
          const files = [];
          for (const filePath of result.filePaths) {
            try {
              const content = fs.readFileSync(filePath, 'utf8');
              const fileName = path.basename(filePath);
              files.push({
                name: fileName,
                path: filePath,
                content: content,
                size: content.length,
              });
              console.log(
                i18n.t('ipc.handlers.mdImport.fileLoaded', { fileName, chars: content.length })
              );
            } catch (error) {
              console.error(i18n.t('ipc.handlers.mdImport.fileError', { filePath, error }));
            }
          }
          return { success: true, files: files, count: files.length };
        } else {
          return { success: false, error: 'No files selected', files: [] };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.mdImport.error', { error }));
        return { success: false, error: error.message, files: [] };
      }
    });

    ipcMain.handle('clear-sessions', async () => {
      console.log(i18n.t('ipc.handlers.sessions.clearing'));
      await this.app.connectionPool.clearAllSessions();
      return true;
    });

    ipcMain.handle('force-kill-websockets', async () => {
      console.log(i18n.t('ipc.handlers.websocket.forceKilling'));
      this.app.connectionPool.forceKillAllConnections();
      return { success: true, message: 'All WebSocket connections force killed' };
    });

    ipcMain.handle('restart-websocket-server', async () => {
      try {
        console.log(i18n.t('ipc.handlers.websocket.restarting'));
        await this.app.connectionPool.switchToNewServer();
        return { success: true, message: 'WebSocket server restarted successfully' };
      } catch (error) {
        console.error('‚ùå WebSocket server restart failed:', error);
        return { success: false, error: error.message };
      }
    });

    ipcMain.handle('queue-user-message', (event, message) => {
      console.log('üí¨ User message queued:', message);
      this.app.connectionPool.queueUserMessage(message);
      return true;
    });

    // CONSOLIDATED Terminal Write Handler - Routes to PTY Manager
    ipcMain.on('terminal-write', (event, data) => {
      console.log('üì§ Received terminal-write:', JSON.stringify(data));
      if (this.app.ptyManager) {
        const success = this.app.ptyManager.writeToTerminal(1, data); // Default to primary terminal
        if (!success) {
          console.error('‚ùå Failed to write to primary terminal (ID: 1)');
        }
      } else {
        console.error('‚ùå PTY Manager not available for terminal-write');
      }
    });

    ipcMain.on('write-to-terminal', (event, data, terminalId) => {
      console.log(
        'üì§ Received write-to-terminal:',
        JSON.stringify(data),
        'terminalId:',
        terminalId
      );
      if (this.app.ptyManager) {
        const targetId = terminalId || 1;
        const success = this.app.ptyManager.writeToTerminal(targetId, data);
        if (!success) {
          console.error(`‚ùå Failed to write to terminal ${targetId} - terminal may not exist`);
          // Try to create terminal if it doesn't exist
          if (!this.app.ptyManager.terminals.has(targetId)) {
            console.log(`üîÑ Attempting to create missing terminal ${targetId}`);
            this.app.ptyManager
              .setupTerminal(targetId)
              .then(() => {
                this.app.ptyManager.writeToTerminal(targetId, data);
              })
              .catch(err => {
                console.error(`‚ùå Failed to create terminal ${targetId}:`, err);
              });
          }
        }
      } else {
        console.error('‚ùå PTY Manager not available for write-to-terminal');
      }
    });

    // ENHANCED Terminal Write Handler for specific terminals
    ipcMain.on('write-to-specific-terminal', (event, { data, terminalId }) => {
      console.log(
        'üì§ Received write-to-specific-terminal:',
        JSON.stringify(data),
        'terminalId:',
        terminalId
      );
      if (this.app.ptyManager) {
        const success = this.app.ptyManager.writeToTerminal(terminalId, data);
        if (!success) {
          console.error(`‚ùå Failed to write to specific terminal ${terminalId}`);
        }
      } else {
        console.error('‚ùå PTY Manager not available for write-to-specific-terminal');
