        fs.writeFileSync(screenshotPath, buffer);

        // Send to renderer
        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('screenshot-captured', {
            path: screenshotPath,
            timestamp: timestamp,
            size: buffer.length,
