    // Open planning tab handler
    console.log(i18n.t('ipc.handlers.registering', { handler: 'open-planning-tab' }));
    ipcMain.handle('open-planning-tab', async event => {
      try {
        console.log(i18n.t('ipc.handlers.planningTab.opening'));

        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('open-planning-tab');
          return { success: true, message: 'Planning tab opened' };
        } else {
          return { success: false, error: 'Main window not available' };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.planningTab.error', { error }));
        return { success: false, error: error.message };
      }
    });

    // Chrome navigation handlers - MISSING FROM ORIGINAL
    console.log(i18n.t('ipc.handlers.registering', { handler: 'navigate-canvas' }));
    ipcMain.handle('navigate-canvas', async event => {
      try {
        console.log(i18n.t('ipc.handlers.canvas.navigating'));
        if (this.app.cdpClient) {
          const canvasUrl = 'http://localhost:8000/canvas.html';
          console.log(i18n.t('info.chrome.navigating', { url: canvasUrl }));
          await this.app.cdpClient.Page.navigate({ url: canvasUrl });
          console.log(i18n.t('info.chrome.navigated'));
          return { success: true, message: 'Navigated to canvas' };
        } else {
          throw new Error(i18n.t('errors.chrome.notConnected'));
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.canvas.error', { error }));
        return { success: false, error: error.message };
      }
    });

    // Strike Errors AI initialization handlers - MISSING FROM ORIGINAL
    console.log(i18n.t('ipc.handlers.registering', { handler: 'initialize-strike-errors-ai' }));
    ipcMain.handle('initialize-strike-errors-ai', async event => {
      try {
        console.log(i18n.t('ipc.handlers.strikeAI.initializing'));
        // Initialize Strike Errors AI system
        return { success: true, message: 'Strike Errors AI initialized' };
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.error', { error }));
        return { success: false, error: error.message };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'strike-errors-ai-launch' }));
    ipcMain.handle('strike-errors-ai-launch', async event => {
      try {
        console.log(i18n.t('ipc.handlers.strikeAI.launching'));
        // Launch Strike Errors AI analysis
        return { success: true, message: 'Strike Errors AI analysis launched' };
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.launchError', { error }));
        return { success: false, error: error.message };
      }
    });

    console.log(i18n.t('ipc.handlers.registering', { handler: 'strike-errors-ai-open-directory' }));
    ipcMain.handle('strike-errors-ai-open-directory', async event => {
      try {
        const result = await dialog.showOpenDialog(this.app.mainWindow, {
          properties: ['openDirectory'],
          title: 'Select Directory for Strike Errors Analysis',
        });

        if (!result.canceled && result.filePaths.length > 0) {
          const dirPath = result.filePaths[0];
          console.log(i18n.t('ipc.handlers.strikeAI.directorySelected', { dirPath }));
          return { success: true, path: dirPath };
        } else {
          return { success: false, cancelled: true };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.strikeAI.directoryError', { error }));
        return { success: false, error: error.message };
      }
    });

    // Unified request handler for new architecture client
    console.log(i18n.t('ipc.handlers.registering', { handler: 'unified-request' }));
    ipcMain.handle('unified-request', async (event, request) => {
      try {
        console.log(i18n.t('ipc.handlers.unified.processing', { type: request.type }));

        switch (request.type) {
          case 'system:health':
            return {
              success: true,
              data: {
                status: 'healthy',
                chrome: this.app.chromeInstance ? 'running' : 'stopped',
                memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
                timestamp: Date.now(),
              },
            };

          case 'system:metrics':
            const metrics = (await this.app.ipcHandlers)
              ? await this.getSystemMetrics()
              : { cpu: 0, memory: 0, chromeTabs: 0 };
            return {
              success: true,
              data: metrics,
            };

          case 'chrome:status':
            const chromeStatus = await this.getChromeStatus();
            return {
              success: true,
              data: chromeStatus,
            };

          default:
            console.warn(i18n.t('ipc.handlers.unified.unknown', { type: request.type }));
            return {
              success: false,
              error: `Unknown request type: ${request.type}`,
            };
        }
      } catch (error) {
        console.error(i18n.t('ipc.handlers.unified.error', { error }));
        return {
          success: false,
          error: error.message,
        };
      }
    });

    console.log(i18n.t('ipc.complete'));
  }

  async getSystemMetrics() {
    try {
      let cpuUsage = 0;
      try {
        if (typeof app.getCPUUsage === 'function') {
          cpuUsage = app.getCPUUsage().percentCPUUsage;
        } else {
          cpuUsage = process.cpuUsage().user / 1000000;
        }
      } catch (error) {
        cpuUsage = 0;
      }

      const memoryUsage = process.memoryUsage();
      let chromeTabs = 0;

      if (this.app.cdpClient) {
        try {
          const targets = await this.app.cdpClient.Target.getTargets();
          chromeTabs = targets.targetInfos.filter(
            target => target.type === 'page' && !target.url.startsWith('devtools://')
          ).length;
        } catch (error) {
          chromeTabs = 0;
        }
      }

      return {
        cpu: parseFloat(cpuUsage.toFixed(1)),
        memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
        chromeTabs: chromeTabs,
        timestamp: Date.now(),
      };
    } catch (error) {
      return {
        cpu: 0,
        memory: 0,
        chromeTabs: 0,
        error: error.message,
      };
    }
  }

  async getChromeStatus() {
    try {
      const isRunning = this.app.chromeInstances && this.app.chromeInstances.length > 0;
      return {
        running: isRunning,
        pid: this.app.chromeInstance?.pid || null,
        port: this.app.chromeInstance?.port || null,
        instanceCount: this.app.chromeInstances?.length || 0,
        memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
      };
    } catch (error) {
      return {
        running: false,
        error: error.message,
        instanceCount: 0,
      };
    }
  }

  getFileType(extension) {
    const typeMap = {
      '.js': 'javascript',
      '.jsx': 'javascript',
      '.ts': 'typescript',
      '.tsx': 'typescript',
      '.py': 'python',
      '.html': 'html',
      '.css': 'css',
      '.scss': 'scss',
      '.less': 'less',
      '.json': 'json',
      '.md': 'markdown',
    };
    return typeMap[extension] || 'text';
  }

  async waitForChromeReady(port, timeout = 10000) {
    const startTime = Date.now();
    while (Date.now() - startTime < timeout) {
      try {
        const response = await fetch(`http://localhost:${port}/json/version`);
        if (response.ok) {
          console.log(`‚úÖ Chrome on port ${port} is ready.`);
          return true;
        }
      } catch (error) {
        // Ignore connection errors and retry
      }
      await new Promise(resolve => setTimeout(resolve, 200));
    }
    throw new Error(`Chrome on port ${port} did not become ready in time.`);
  }

  async connectToChrome(port) {
    if (this.app.cdpClient) {
      try {
        await this.app.cdpClient.close();
      } catch (e) {
        // Ignore errors closing old client
      }
      this.app.cdpClient = null;
    }

    try {
      console.log(`üîó Connecting to Chrome on port ${port} using CDP...`);

      const targets = await CDP.List({ port });
      console.log(`üìã Found ${targets.length} Chrome targets`);

      const pageTarget = targets.find(target => target.type === 'page') || targets[0];

      if (!pageTarget) {
        throw new Error('No suitable Chrome target found');
      }

      console.log(`üéØ Connecting to target: ${pageTarget.title || pageTarget.url}`);
      this.app.cdpClient = await CDP({ port, target: pageTarget });

      console.log('üîß Enabling Chrome DevTools Protocol domains...');
      await Promise.all([
        this.app.cdpClient.Page.enable(),
        this.app.cdpClient.Runtime.enable(),
        this.app.cdpClient.DOM.enable(),
        this.app.cdpClient.CSS.enable(),
        this.app.cdpClient.Network.enable(),
        this.app.cdpClient.Debugger.enable(),
        this.app.cdpClient.Target.setDiscoverTargets({ discover: true }),
      ]).catch(error => {
        console.warn('‚ö†Ô∏è Some CDP domains failed to enable:', error.message);
      });

      this.app.cdpClient.on('disconnect', () => {
        console.log('üî¥ CDP client disconnected.');
        this.app.cdpClient = null;
        this.app.updateRendererStatus();
      });

      this.app.cdpClient.on('error', err => {
        console.error('üî¥ CDP Client Error:', err);
        const errorObject = {
          source: 'CDP Backend',
          message: err.message,
          timestamp: new Date().toISOString(),
          type: 'error',
        };
        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('new-error-log', errorObject);
        }
      });

      console.log('‚úÖ CDP connection successful.');
      return true;
    } catch (error) {
      console.error('‚ùå CDP connection failed:', error);
      this.app.cdpClient = null;
      return false;
    }
  }

  /**
   * Request canvas state from webview (resilient - fails gracefully)
   * Only attempts to get state if Chrome is running and navigated to canvas
   */
  async requestCanvasStateFromWebview() {
    try {
      // Check if Chrome is running and has canvas page loaded
      if (!this.app.cdpClient) {
        throw new Error('Chrome not connected');
      }

      // Check if current page is canvas
      const { targetInfos } = await this.app.cdpClient.Target.getTargets();
      const canvasTab = targetInfos.find(
        target => target.type === 'page' && target.url.includes('canvas.html')
      );

      if (!canvasTab) {
        throw new Error('Canvas not loaded in Chrome');
      }

      // Try to get canvas state via CDP Runtime evaluation (with timeout)
      const statePromise = this.app.cdpClient.Runtime.evaluate({
        expression: `
          (function() {
            try {
              if (window.tlEditor && window.tlEditor.store) {
                const shapes = Object.values(window.tlEditor.store.allRecords())
                  .filter(record => record.typeName === 'shape');
                const selection = window.tlEditor.getSelectedShapeIds();
                const currentTool = window.tlEditor.getCurrentTool().id;
                const camera = window.tlEditor.getCamera();
                return {
                  shapes: shapes,
                  selection: selection,
                  currentTool: currentTool,
                  camera: camera,
                  timestamp: Date.now()
                };
              }
              return { error: 'TLDraw not available' };
            } catch (e) {
              return { error: e.message };
            }
          })()
        `,
        returnByValue: true,
      });

      const timeoutPromise = new Promise((_, reject) =>
        setTimeout(() => reject(new Error('Canvas state timeout')), 2000)
      );

      const result = await Promise.race([statePromise, timeoutPromise]);

      if (result.result && result.result.value && !result.result.value.error) {
        return result.result.value;
      } else {
        throw new Error(result.result?.value?.error || 'Failed to get canvas state');
      }
    } catch (error) {
      // Always throw to let handler decide on fallback
      throw new Error(`Webview canvas state unavailable: ${error.message}`);
    }
  }
}

module.exports = IPCHandlers;

