function handlePlanningScreenshot(screenshot) {
  // Process planning screenshot and show notification

  // Show screenshot notification
  const notification = document.createElement('div');
  notification.style.cssText = `
    position: fixed;
    top: 50px;
    right: 10px;
    background: #28a745;
    color: white;
    padding: 10px;
    border-radius: 5px;
    z-index: 1001;
    font-family: monospace;
  `;
  notification.innerHTML = `
    üì∏ Screenshot captured!<br/>
    <small>Path: ${screenshot.path}</small><br/>
    <small>Size: ${Math.round(screenshot.size / 1024)}KB</small>
  `;
  document.body.appendChild(notification);

  // Auto-hide notification
  setTimeout(() => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  }, 5000);

  // Switch to planning tab if it exists
  openPlanningTab();
}

// CRITICAL: Planning tab opener - WAS MISSING!
function openPlanningTab() {
  // Activate planning tab or show planning mode notification

  // Try to switch to planning tab
  const planningTab = document.querySelector('[data-tab="planning"]');
  if (planningTab) {
    planningTab.click();
    // Planning tab activated successfully
  } else {
