          window.unifiedDiagnostics.addExternalData('chrome-container', {
            timestamp: ${Date.now()},
            source: 'chrome-container-app',
            metrics: ${JSON.stringify(metrics)},
            system: 'Chrome Container IPC'
          });
        } catch (e) {
          // Diagnostic connection failed - continue without it
        }
