  async getSystemMetrics() {
    try {
      let cpuUsage = 0;
      try {
        if (typeof app.getCPUUsage === 'function') {
          cpuUsage = app.getCPUUsage().percentCPUUsage;
        } else {
          cpuUsage = process.cpuUsage().user / 1000000;
        }
      } catch (error) {
        cpuUsage = 0;
      }

      const memoryUsage = process.memoryUsage();
      let chromeTabs = 0;

      if (this.app.cdpClient) {
        try {
          const targets = await this.app.cdpClient.Target.getTargets();
          chromeTabs = targets.targetInfos.filter(
            target => target.type === 'page' && !target.url.startsWith('devtools://')
          ).length;
        } catch (error) {
          chromeTabs = 0;
        }
      }

      return {
        cpu: parseFloat(cpuUsage.toFixed(1)),
        memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
        chromeTabs: chromeTabs,
        timestamp: Date.now(),
      };
    } catch (error) {
      return {
        cpu: 0,
        memory: 0,
        chromeTabs: 0,
        error: error.message,
      };
    }
  }

  async getChromeStatus() {
    try {
      const isRunning = this.app.chromeInstances && this.app.chromeInstances.length > 0;
      return {
        running: isRunning,
        pid: this.app.chromeInstance?.pid || null,
        port: this.app.chromeInstance?.port || null,
        instanceCount: this.app.chromeInstances?.length || 0,
        memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
      };
    } catch (error) {
      return {
        running: false,
        error: error.message,
        instanceCount: 0,
      };
    }
  }

  getFileType(extension) {
    const typeMap = {
      '.js': 'javascript',
      '.jsx': 'javascript',
      '.ts': 'typescript',
      '.tsx': 'typescript',
      '.py': 'python',
      '.html': 'html',
      '.css': 'css',
      '.scss': 'scss',
      '.less': 'less',
      '.json': 'json',
      '.md': 'markdown',
    };
    return typeMap[extension] || 'text';
  }

  async waitForChromeReady(port, timeout = 10000) {
    const startTime = Date.now();
    while (Date.now() - startTime < timeout) {
      try {
        const response = await fetch(`http://localhost:${port}/json/version`);
        if (response.ok) {
          console.log(`‚úÖ Chrome on port ${port} is ready.`);
          return true;
        }
      } catch (error) {
        // Ignore connection errors and retry
      }
      await new Promise(resolve => setTimeout(resolve, 200));
    }
    throw new Error(`Chrome on port ${port} did not become ready in time.`);
  }

  async connectToChrome(port) {
    if (this.app.cdpClient) {
      try {
        await this.app.cdpClient.close();
      } catch (e) {
        // Ignore errors closing old client
      }
      this.app.cdpClient = null;
    }

    try {
      console.log(`üîó Connecting to Chrome on port ${port} using CDP...`);

      const targets = await CDP.List({ port });
      console.log(`üìã Found ${targets.length} Chrome targets`);

      const pageTarget = targets.find(target => target.type === 'page') || targets[0];

      if (!pageTarget) {
        throw new Error('No suitable Chrome target found');
      }

      console.log(`üéØ Connecting to target: ${pageTarget.title || pageTarget.url}`);
      this.app.cdpClient = await CDP({ port, target: pageTarget });

      console.log('üîß Enabling Chrome DevTools Protocol domains...');
      await Promise.all([
        this.app.cdpClient.Page.enable(),
        this.app.cdpClient.Runtime.enable(),
        this.app.cdpClient.DOM.enable(),
        this.app.cdpClient.CSS.enable(),
        this.app.cdpClient.Network.enable(),
        this.app.cdpClient.Debugger.enable(),
        this.app.cdpClient.Target.setDiscoverTargets({ discover: true }),
      ]).catch(error => {
        console.warn('‚ö†Ô∏è Some CDP domains failed to enable:', error.message);
      });

      this.app.cdpClient.on('disconnect', () => {
        console.log('üî¥ CDP client disconnected.');
        this.app.cdpClient = null;
        this.app.updateRendererStatus();
      });

      this.app.cdpClient.on('error', err => {
        console.error('üî¥ CDP Client Error:', err);
        const errorObject = {
          source: 'CDP Backend',
          message: err.message,
          timestamp: new Date().toISOString(),
          type: 'error',
        };
        if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
          this.app.mainWindow.webContents.send('new-error-log', errorObject);
        }
      });

      console.log('‚úÖ CDP connection successful.');
      return true;
    } catch (error) {
      console.error('‚ùå CDP connection failed:', error);
      this.app.cdpClient = null;
      return false;
    }
  }

  /**
   * Request canvas state from webview (resilient - fails gracefully)
   * Only attempts to get state if Chrome is running and navigated to canvas
   */
  async requestCanvasStateFromWebview() {
    try {
      // Check if Chrome is running and has canvas page loaded
      if (!this.app.cdpClient) {
        throw new Error('Chrome not connected');
      }

      // Check if current page is canvas
      const { targetInfos } = await this.app.cdpClient.Target.getTargets();
      const canvasTab = targetInfos.find(
        target => target.type === 'page' && target.url.includes('canvas.html')
      );

      if (!canvasTab) {
        throw new Error('Canvas not loaded in Chrome');
      }

      // Try to get canvas state via CDP Runtime evaluation (with timeout)
      const statePromise = this.app.cdpClient.Runtime.evaluate({
        expression: `
          (function() {
            try {
              if (window.tlEditor && window.tlEditor.store) {
                const shapes = Object.values(window.tlEditor.store.allRecords())
                  .filter(record => record.typeName === 'shape');
                const selection = window.tlEditor.getSelectedShapeIds();
                const currentTool = window.tlEditor.getCurrentTool().id;
                const camera = window.tlEditor.getCamera();
                return {
                  shapes: shapes,
                  selection: selection,
                  currentTool: currentTool,
                  camera: camera,
                  timestamp: Date.now()
                };
              }
              return { error: 'TLDraw not available' };
            } catch (e) {
              return { error: e.message };
            }
          })()
        `,
        returnByValue: true,
      });

      const timeoutPromise = new Promise((_, reject) =>
        setTimeout(() => reject(new Error('Canvas state timeout')), 2000)
      );

      const result = await Promise.race([statePromise, timeoutPromise]);

      if (result.result && result.result.value && !result.result.value.error) {
        return result.result.value;
      } else {
        throw new Error(result.result?.value?.error || 'Failed to get canvas state');
      }
    } catch (error) {
      // Always throw to let handler decide on fallback
      throw new Error(`Webview canvas state unavailable: ${error.message}`);
    }
  }
}

module.exports = IPCHandlers;

