export class CodeMapAnalyzer {
  constructor(config) {
    this.config = config;
  }

  /**
   * Analyze the given directory with dependency-cruiser and return a parsed object
   * that contains a `modules` array compatible with downstream consumers.
   */
  async analyze(directory, options = {}) {
    try {
      const depCruiserModule = await import("dependency-cruiser");
      // dependency-cruiser may export as CJS default or ESM named
      const cruise =
        depCruiserModule.cruise || depCruiserModule.default?.cruise;
      if (!cruise) {
        throw new Error(
          "dependency-cruiser did not expose a cruise() function",
        );
      }

      const mergedOptions = {
        // Prefer explicit options passed in, then config, then sane defaults
        ...this.config.dependencyCruiserOptions,
        ...options.dependencyCruiserOptions,
      };

      // Ensure we use the json reporter so we can parse modules
      if (!mergedOptions.reporterOptions) mergedOptions.reporterOptions = {};
      // dependency-cruiser programmatic API returns { output } string when a reporter is set
      // Use the built-in json reporter
      mergedOptions.reporter = "json";

      // Map includes/excludes if provided at top-level
      if (
        Array.isArray(options.excludePatterns) &&
        options.excludePatterns.length
      ) {
        mergedOptions.exclude = mergedOptions.exclude || {};
        const safePatterns = options.excludePatterns
          .map((p) => this.globToSafeRegex(p))
          .filter(Boolean);
        mergedOptions.exclude.path = [
          ...(mergedOptions.exclude.path || []),
          ...safePatterns,
        ];
      }
      if (typeof options.depth === "number") {
        mergedOptions.maxDepth = options.depth;
      }

      const cruiseResult = await cruise([directory], mergedOptions);
      const rawOutput = cruiseResult?.output;
      if (!rawOutput) {
        throw new Error("dependency-cruiser returned no output");
      }

      // output is a JSON string with shape { modules: [...], summary: {...}, ... }
      const parsed =
        typeof rawOutput === "string" ? JSON.parse(rawOutput) : rawOutput;
      if (!parsed.modules) {
        throw new Error("dependency-cruiser JSON did not include modules[]");
      }
      return parsed;
    } catch (error) {
      console.error("Error during dependency analysis:", error);
      throw error;
    }
  }

  globToSafeRegex(pattern) {
    if (!pattern || typeof pattern !== "string") return null;
    // Convert common globs to relatively safe regex fragments dependency-cruiser accepts
    // Keep it conservative to avoid perf issues
    const escaped = pattern
      .replace(/[.+^${}()|[\]\\]/g, "\\$&")
      .replace(/\*\*\//g, "(.*/)?")
      .replace(/\*\*/g, ".*")
      .replace(/\*/g, "[^/]*");
    try {
      // dependency-cruiser wants strings, it compiles internally; we just ensure it's reasonable
      return escaped;
    } catch {
      return null;
    }
  }
}

