generateActionableReport(nodes, edges, options = {}) {
  const ts = this.formatChicagoTimestamp();
  const unresolvedEdges = edges.filter((e) => e.broken);
  const totalEdges = edges.length;
  const brokenCount = unresolvedEdges.length;
  const brokenPercent =
    totalEdges > 0 ? ((brokenCount / totalEdges) * 100).toFixed(1) : 0;

  // Critical analysis - identify all problematic patterns
  const criticalIssues = {
    unresolved: unresolvedEdges,
    emptyFiles: [],
    missingHandlers: [],
    orphanedFunctions: [],
    circularDeps: [],
  };

  const byFile = new Map();
  const indexNode = (id) => nodes.get(id);
  const parseNodeId = (id) => {
    // pattern: file::name@line
    const atIdx = id.lastIndexOf("@");
    const base = atIdx >= 0 ? id.slice(0, atIdx) : id;
    const line = atIdx >= 0 ? id.slice(atIdx + 1) : "?";
    const parts = base.split("::");
    const file = parts[0];
    const name = parts[1] || "";
    return { file, name, line };
  };

  // Build reverse edges for upstream callers
  const targetsToSources = new Map();
  for (const e of edges) {
    if (!targetsToSources.has(e.to)) targetsToSources.set(e.to, new Set());
    targetsToSources.get(e.to).add(e.from);
  }

  // Map unresolved by caller file
  for (const e of unresolvedEdges) {
    const fromInfo = parseNodeId(e.from);
    if (!byFile.has(fromInfo.file)) byFile.set(fromInfo.file, []);
    byFile.get(fromInfo.file).push({ from: fromInfo, to: parseNodeId(e.to) });
  }

  let report = "";
  report += `# üö® CRITICAL: Function-Level Dependency Analysis Report\n\n`;
  report += `## ‚ö†Ô∏è BROKEN CALL ANALYSIS - IMMEDIATE ACTION REQUIRED\n\n`;
  report += `**Timestamp (Chicago):** ${ts}\n`;
  report += `**Project:** Chrome Container App\n`;
  report += `**Reference SOP:** chrome-container-app/END-TO-END.md\n\n`;
  report += `### üìä CRITICAL METRICS\n\n`;
  report += `| Metric | Value | Status |\n`;
  report += `|--------|-------|--------|\n`;
  report += `| **Total Function Calls** | ${totalEdges} | - |\n`;
  report += `| **‚úÖ Resolved Calls** | ${totalEdges - brokenCount} | ${totalEdges - brokenCount > 0 ? "‚úì" : "‚ùå"} |\n`;
  report += `| **‚ùå BROKEN/UNRESOLVED** | **${brokenCount}** | **${brokenCount > 0 ? "üî¥ CRITICAL" : "‚úÖ CLEAN"}** |\n`;
  report += `| **Broken Percentage** | **${brokenPercent}%** | ${brokenPercent > 10 ? "üî¥ UNACCEPTABLE" : brokenPercent > 0 ? "‚ö†Ô∏è NEEDS WORK" : "‚úÖ PERFECT"} |\n`;
  report += `\n`;

  if (brokenCount > 0) {
    report += `### üî¥ BROKEN CALLS REQUIRING IMMEDIATE FIX\n\n`;
    report += `**${brokenCount} broken function calls detected. These MUST be fixed:**\n\n`;
  } else {
    report += `### ‚úÖ NO BROKEN CALLS DETECTED\n\n`;
    report += `All function calls are properly resolved. Excellent!\n\n`;
  }

  for (const [file, items] of byFile.entries()) {
    report += `## File: ${file}\n\n`;
    const callers = new Map();
    items.forEach((it) => {
      const key = `${it.from.name}@${it.from.line}`;
      if (!callers.has(key)) callers.set(key, []);
      callers.get(key).push(it);
    });

    for (const [callerKey, list] of callers.entries()) {
      const [callerName, callerLine] = callerKey.split("@");
      report += `### Caller: ${callerName} (line ${callerLine})\n\n`;
      report += `- Unresolved callees (${list.length}):\n`;
      list.forEach((it) => {
        report += `  - ${it.to.name} (expected in ${it.to.file})\n`;
      });

      // Upstream callers of this caller
      const thisNodeIdPrefix = `${file}::${callerName}@`;
      const nodeIds = Array.from(nodes.keys()).filter((id) =>
        id.startsWith(thisNodeIdPrefix),
      );
      const upstream = new Set();
      for (const nid of nodeIds) {
        for (const [target, sources] of targetsToSources.entries()) {
          if (target === nid) {
            for (const s of sources) upstream.add(s);
          }
        }
      }
      if (upstream.size) {
        report += `- Upstream callers:\n`;
        for (const up of upstream) {

